--
-- Cấu trúc bảng cho bảng `tb_products`
--

CREATE TABLE `tb_products` (
  `product_id` int(11) NOT NULL,
  `product_code` varchar(1000) NOT NULL,
  `product_name` varchar(1000) NOT NULL,
  `price` int(255) NOT NULL,
  `product_desc` varchar(1000) NOT NULL,
  `product_thumb` varchar(1000) NOT NULL,
  `product_content` longtext DEFAULT NULL,
  `quantity` int(11) NOT NULL,
  `status` enum('Đã đăng','Chờ xét duyệt') DEFAULT NULL,
  `cat_id` int(255) NOT NULL,
  `created_date` timestamp NOT NULL DEFAULT current_timestamp(),
  `creator` varchar(1000) DEFAULT NULL,
  `sales` int(255) DEFAULT 0
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_vietnamese_ci;

--
-- Đang đổ dữ liệu cho bảng `tb_products`
--

INSERT INTO `tb_products` (`product_id`, `product_code`, `product_name`, `price`, `product_desc`, `product_thumb`, `product_content`, `quantity`, `status`, `cat_id`, `created_date`, `creator`, `sales`) VALUES
(10, 'VHL#7655', 'Táo Rockit New Zealand Nhập Khẩu', 130000, '<p><span style=\"font-size:14px;\">Táo Rockit New Zealand có vị ngọt, giòn, mùi thơm dịu đặc trưng.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Được mệnh danh là một trong những loại táo ngon nhất thế giới.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Táo rockit cũng nằm trong danh sách những loại táo cao cấp nhất hiện nay.</span></p>\r\n', '1707-1.jpg', '<p>T&aacute;o Rockit New Zealand c&oacute; vị ngọt, gi&ograve;n, m&ugrave;i thơm dịu đặc trưng. Được mệnh danh l&agrave; một trong những loại t&aacute;o ngon nhất thế giới, t&aacute;o rockit cũng nằm trong danh s&aacute;ch những loại t&aacute;o cao cấp nhất hiện nay. Với mong muốn mang đến cho kh&aacute;ch h&agrave;ng trong nước cơ hội thưởng thức tr&aacute;i c&acirc;y nhập khẩu an to&agrave;n, chất lượng, lu&ocirc;n mua được sản phẩm với gi&aacute; tốt nhất tr&ecirc;n thị trường.</p>\r\n\r\n<p><img data-src=\"https://hoaquaxanh.vn/wp-content/uploads/2023/02/1707.jpg\" decoding=\"async\" src=\"https://hoaquaxanh.vn/wp-content/uploads/2023/02/1707.jpg\" /></p>\r\n\r\n<h2>Th&ocirc;ng tin sản phẩm</h2>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td>T&ecirc;n sản phẩm</td>\r\n			<td>T&aacute;o Rockit New Zealand</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Nguồn gốc</td>\r\n			<td>T&acirc;n T&acirc;y Lan</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Đơn vị</td>\r\n			<td>Hộp</td>\r\n		</tr>\r\n		<tr>\r\n			<td>đ&oacute;ng g&oacute;i</td>\r\n			<td>280g &ndash; Hộp 4 c&aacute;i</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Bảo tồn</td>\r\n			<td>nhiệt độ tủ lạnh</td>\r\n		</tr>\r\n		<tr>\r\n			<td>hết hạn</td>\r\n			<td>cảm x&uacute;c</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Sử dụng</td>\r\n			<td>C&oacute; thể ăn trực tiếp, l&agrave;m nước &eacute;p, b&aacute;nh nướng, tr&aacute;ng miệng</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<h3>Đặc điểm t&aacute;o Rockit New Zealand do hoaquaxanh.vn cung cấp</h3>\r\n\r\n<p>T&aacute;o Rockit New Zealand quả nhỏ, cầm chắc tay. Vỏ t&aacute;o b&oacute;ng, m&agrave;u đỏ sẫm pha ch&uacute;t v&agrave;ng. Thịt t&aacute;o trắng, gi&ograve;n v&agrave; mọng nước, vị ngọt đậm.</p>\r\n\r\n<p>T&aacute;o Rockit th&iacute;ch hợp bảo quản ở nhiệt độ 4-8 độ C, c&oacute; thể giữ độ tươi từ 1-4 tuần.</p>\r\n\r\n<p>Lưu &yacute;: Kh&ocirc;ng để t&aacute;o gần c&aacute;c thực phẩm nặng m&ugrave;i như h&agrave;nh, tỏi&hellip; để tr&aacute;nh bị b&aacute;m bẩn, giảm ngon miệng.</p>\r\n\r\n<p><img data-src=\"https://hoaquaxanh.vn/wp-content/uploads/2023/02/1707-1.jpg\" decoding=\"async\" src=\"https://hoaquaxanh.vn/wp-content/uploads/2023/02/1707-1.jpg\" /></p>\r\n\r\n<h3>L&yacute; do bạn n&ecirc;n chọn mua t&aacute;o Rockit tại hoaquaxanh.vn</h3>\r\n\r\n<ul>\r\n	<li>Đảm bảo chất lượng: T&aacute;o Rockit được hoaquasinh.vn nhập khẩu ch&iacute;nh ngạch, đảm bảo nguồn gốc xuất xứ r&otilde; r&agrave;ng, an to&agrave;n vệ sinh thực phẩm, n&oacute;i kh&ocirc;ng với chất bảo quản.</li>\r\n	<li>Đảm bảo độ tươi ngon: T&aacute;o Rockit được bảo quản trong kho lạnh bằng c&aacute;ch hạ thấp nhiệt độ v&agrave; nồng độ oxy để giữ được độ tươi, gi&ograve;n của quả khi đến tay kh&aacute;ch h&agrave;ng.</li>\r\n	<li>Gi&aacute; tốt nhất thị trường: Kh&ocirc;ng chỉ cung cấp c&aacute;c loại tr&aacute;i c&acirc;y bổ dưỡng, tươi sạch, hoaquaxanh.vn c&ograve;n đưa ra mức gi&aacute; tốt nhất, đảm bảo sẽ l&agrave;m h&agrave;i l&ograve;ng mọi kh&aacute;ch h&agrave;ng.</li>\r\n</ul>\r\n\r\n<h3>Th&ocirc;ng tin dinh dưỡng trong t&aacute;o Rockit New Zealand của hoaquaxanh.vn</h3>\r\n\r\n<p>Mặc d&ugrave; nhỏ nhưng t&aacute;o rockit nổi tiếng về gi&aacute; trị dinh dưỡng.</p>\r\n\r\n<ul>\r\n	<li>Giảm cholesterol : T&aacute;o Rockit New Zealand c&oacute; nhiều chất xơ v&agrave; pectin. Pectin c&oacute; t&aacute;c dụng ngăn ngừa cholesterol h&igrave;nh th&agrave;nh tr&ecirc;n th&agrave;nh mạch m&aacute;u, từ đ&oacute; giảm nguy cơ xơ vữa động mạch v&agrave; bệnh tim.</li>\r\n	<li>Ngăn ngừa huyết &aacute;p cao : Do t&aacute;o rockite chứa nhiều kali, một loại kho&aacute;ng chất gi&uacute;p kiểm so&aacute;t huyết &aacute;p n&ecirc;n ăn t&aacute;o gi&uacute;p giảm nguy cơ đột quỵ, ngăn ngừa cao huyết &aacute;p v&agrave; bệnh tim.</li>\r\n	<li>Giảm c&acirc;n: Axit ursolic c&oacute; trong vỏ t&aacute;o hỗ trợ đốt ch&aacute;y calo v&agrave; mỡ thừa hiệu quả, c&oacute; t&aacute;c dụng lớn trong việc giảm c&acirc;n.</li>\r\n</ul>\r\n\r\n<h4>Ch&iacute;nh s&aacute;ch mua h&agrave;ng tiện lợi tại hoaquaxanh.vn</h4>\r\n\r\n<p>hoaquaxanh.vn &ndash; chuỗi cửa h&agrave;ng b&aacute;n lẻ thực phẩm nhập khẩu cao cấp cam kết mang đến cho kh&aacute;ch h&agrave;ng những sản phẩm chất lượng ho&agrave;n hảo với gi&aacute; tốt nhất tr&ecirc;n thị trường. Bạn c&oacute; thể t&igrave;m thấy c&aacute;c loại thực phẩm nhập khẩu tươi ngon như c&aacute; hồi tươi, thịt b&ograve; nhập khẩu, hải sản cao cấp tại tất cả hệ thống cửa h&agrave;ng của hoaquaxanh.vn tr&ecirc;n to&agrave;n quốc.</p>\r\n\r\n<p>hoaquaxanh.vn cung cấp dịch vụ Freeship khi vận chuyển trong nội th&agrave;nh (b&aacute;n k&iacute;nh dưới 5km) cho tất cả c&aacute;c đơn h&agrave;ng c&oacute; gi&aacute; trị tr&ecirc;n 500k. Bạn c&oacute; thể t&igrave;m hiểu th&ecirc;m về ch&iacute;nh s&aacute;ch vận chuyển của ch&uacute;ng t&ocirc;i tại đ&acirc;y.</p>\r\n\r\n<p>&nbsp;</p>\r\n', 15, 'Đã đăng', 1, '2023-11-28 11:04:06', 'vuhonglinh123', 33),
(11, 'VHL#8', 'Kiwi Xanh New Zealand – Kiwi Green Zespri', 110000, '<p><strong>Quả</strong>&nbsp;<b>Kiwi xanh</b>&nbsp;c&oacute; h&igrave;nh bầu dục, k&iacute;ch thước tương đương 1 quả trứng g&agrave; lớn , từ 5 &ndash; 8 cm chiều d&agrave;i v&agrave; 4,5 &ndash; 5,5 cm đường k&iacute;nh.</p>\r\n\r\n<p>Trọng lượng b&igrave;nh qu&acirc;n của một quả&nbsp;<b>Kiwi xanh</b>&nbsp;New Zealand l&agrave; từ 100g đến 130g.</p>\r\n\r\n<p>Kiwi Green c&oacute; vỏ ngo&agrave;i xanh hơn khi đặt cạch kiwi v&agrave;ng, ruột c&oacute; m&agrave;u xanh ngọc rất đẹp mắt, hạt nhỏ m&agrave;u đen v&agrave; c&oacute; l&ocirc;ng ở ngo&agrave;i vỏ d&agrave;i hơn so với l&ocirc;ng của kiwi sungold.</p>\r\n', '1700-768x768.jpg', '<h3><b>1. Giới thiệu chung</b></h3>\r\n\r\n<p>Năm 1904 quả Kiwi xanh bắt đầu được trồng đại tr&agrave; tr&ecirc;n Đảo Bắc Nelson của New Zealand. Giờ đ&acirc;y, kiwi xanh kh&ocirc;ng c&ograve;n chỉ được trồng ở New Zealand m&agrave; c&ograve;n được du nhập v&agrave; trồng ở Mỹ, Nhật, Ph&aacute;p,&nbsp;<b>&Uacute;c</b>&nbsp;, &Yacute; v&agrave; ph&aacute;t triển theo m&ocirc; h&igrave;nh thương mại xuất khẩu.</p>\r\n\r\n<p>Sở dĩ kiwi v&agrave; c&aacute;c loại tr&aacute;i c&acirc;y kh&aacute;c ở New Zealand ph&aacute;t triển mạnh v&agrave; được thị trường thế giới ưa chuộng l&agrave; do New Zealand c&oacute; kh&iacute; hậu cận nhiệt đới, nhiệt đới v&agrave; &ocirc;n đới. H&agrave;ng năm, kiwi bắt đầu được thu hoạch từ th&aacute;ng 5 đến th&aacute;ng 9 trong năm.</p>\r\n\r\n<p><img data-src=\"https://hoaquaxanh.vn/wp-content/uploads/2023/02/1700.jpg\" decoding=\"async\" src=\"https://hoaquaxanh.vn/wp-content/uploads/2023/02/1700.jpg\" /></p>\r\n\r\n<h3><b>2. Đặc điểm</b></h3>\r\n\r\n<p>&ndash; H&igrave;nh dạng: bầu dục, c&oacute; l&ocirc;ng mượn, to bằng quả trứng g&agrave;, vỏ m&agrave;u lục v&agrave; c&oacute; l&ocirc;ng mượn.</p>\r\n\r\n<p>&ndash; Thịt: m&agrave;u xanh, ở giữa c&oacute; hạt nhỏ m&agrave;u đen, nhiều nước, thịt mềm.</p>\r\n\r\n<p>&ndash; Vị: thơm, chua ngọt, dịu</p>\r\n\r\n<h3><b>3. Dinh dưỡng &amp; sức khỏe</b></h3>\r\n\r\n<p>&ndash; Kiwi xanh chứa nhiều hợp chất rất tốt cho sức khỏe, một quả kiwi chứa 270% Vitamin C, E v&agrave; c&aacute;c loại protein, canxi, muối, clo&hellip; gi&uacute;p cải thiện chức năng của hệ miễn dịch, ngăn ngừa vi&ecirc;m gan v&agrave; n&acirc;ng cao hệ miễn dịch. Tấn c&ocirc;ng virus v&agrave; vi khuẩn, cải thiện khả năng miễn dịch.</p>\r\n\r\n<p>&ndash; Kiwi xanh gi&uacute;p ngăn ngừa sự h&igrave;nh th&agrave;nh gelatin hoặc v&oacute;n cục khi ti&ecirc;u thụ thực phẩm độc hại.</p>\r\n\r\n<p>&ndash; Kiwi l&agrave;m tăng hiệu quả giảm c&acirc;n, giảm nguy cơ ung thư v&agrave; bệnh tim, l&agrave;m trắng răng, trị t&agrave;n nhang v&agrave; ức chế qu&aacute; tr&igrave;nh oxy h&oacute;a da, đ&agrave;o thải v&agrave; ngăn ngừa c&aacute;c sắc tố đen gi&uacute;p da trắng hồng. h&agrave;o v&agrave; mịn m&agrave;ng nhất l&agrave; đối với c&aacute;c b&agrave; mẹ sau khi sinh.</p>\r\n\r\n<p>&ndash; Ngo&agrave;i Kiwi, bạn c&oacute; thể sử dụng một số loại tr&aacute;i c&acirc;y như T&aacute;o hay&nbsp;<b>Nho</b>&nbsp;cũng c&oacute; nhiều dưỡng chất tự nhi&ecirc;n, rất tốt cho cơ thể.</p>\r\n\r\n<p><img data-src=\"https://hoaquaxanh.vn/wp-content/uploads/2023/02/1700-1.jpg\" decoding=\"async\" height=\"1024\" src=\"https://hoaquaxanh.vn/wp-content/uploads/2023/02/1700-1.jpg\" width=\"768\" /></p>\r\n\r\n<h3><b>&nbsp;4. Bảo quản</b></h3>\r\n\r\n<p>&ndash; N&ecirc;n chọn những quả Kiwi xanh c&oacute; vỏ căng, quả to v&agrave; c&oacute; m&agrave;u s&aacute;ng.</p>\r\n\r\n<p>Kiwi xanh chỉ ngon v&agrave; ngọt khi quả ch&iacute;n. Khi ăn sờ thấy mềm l&agrave; l&uacute;c kiwi đ&atilde; ch&iacute;n, c&oacute; thể ăn được ngay.</p>\r\n\r\n<p>&ndash; Nếu quả Kiwi xanh c&ograve;n cứng th&igrave; n&ecirc;n để ở nhiệt độ ph&ograve;ng từ 3-5 ng&agrave;y cho ch&iacute;n. Hoặc bọc quả trong t&uacute;i giấy, t&uacute;i ni l&ocirc;ng để t&aacute;o (hoặc chuối) ch&iacute;n nhanh hơn. Kiwi xanh bảo quản ở nhiệt độ b&igrave;nh thường cần tr&aacute;nh &aacute;nh nắng mặt trời hoặc những nơi c&oacute; nhiệt độ cao.</p>\r\n\r\n<p>&ndash; Khi ch&iacute;n, bảo quản quả kiwi xanh trong tủ lạnh ở nhiệt độ 0-4oC sẽ giữ được quả kiwi trong khoảng 2 tuần. Loại quả n&agrave;y sẽ ngon hơn khi để trong tủ lạnh.</p>\r\n\r\n<p>&ndash; Kiwi xanh c&agrave;ng để l&acirc;u c&agrave;ng ngọt. Nhưng nếu để qu&aacute; l&acirc;u, ch&uacute;ng sẽ &acirc;m ỉ v&agrave; l&ecirc;n men. Do đ&oacute;, tốt nhất l&agrave; ăn ch&uacute;ng trước khi ch&uacute;ng qu&aacute; ch&iacute;n.</p>\r\n\r\n<p>&ndash; Khi sử dụng&nbsp;<b>Kiwi xanh</b>&nbsp;người ta thường cắt đ&ocirc;i rồi nạo, d&ugrave;ng th&igrave;a gọt vỏ v&agrave; th&aacute;i mỏng, trộn trong c&aacute;c m&oacute;n salad, thường d&ugrave;ng để tr&aacute;ng miệng hoặc ăn k&egrave;m với c&aacute;c m&oacute;n nướng.</p>\r\n\r\n<p>&ndash; Tr&aacute;i kiwi xanh c&ograve;n c&oacute; thể d&ugrave;ng để chế biến c&aacute;c loại thức uống như sinh tố, nước &eacute;p, sữa uống; Hoặc d&ugrave;ng với sữa để l&agrave;m sữa chua, kem, m&oacute;n tr&aacute;ng miệng, kem tr&aacute;i c&acirc;y đ&ocirc;ng lạnh.</p>\r\n\r\n<p>Ngo&agrave;i ra, loại quả n&agrave;y c&ograve;n c&oacute; thể d&ugrave;ng để l&agrave;m salad, nước xốt, thịt xi&ecirc;n nướng, b&aacute;nh, mứt, kem, bơ trộn&hellip;</p>\r\n', 20, 'Đã đăng', 1, '2023-11-25 04:25:58', 'vuhonglinh123', 0),
(12, 'VHL#9', 'Kiwi ruột đỏ nhập khẩu', 150000, '<p>Kiwi ruột đỏ Đài Loan là loại quả có hình bầu dục, vỏ màu nâu sẫm, thịt quả màu vàng xen lẫn hạt và các tia đỏ rất nổi bật và đẹp mắt. Kiwi sở hữu vị chua ngọt hoàn hảo, mùi thơm đặc trưng, thịt quả cung cấp cho cơ thể lượng vitamin vô cùng dồi dào, khi chín có vị ngọt dịu hơn kiwi vàng.</p>\r\n', '1694-533x400.jpg', '<h3>&nbsp;</h3>\r\n\r\n<p>Kiwi ruột đỏ Đ&agrave;i Loan l&agrave; loại quả c&oacute; h&igrave;nh bầu dục, vỏ m&agrave;u n&acirc;u sẫm, thịt quả m&agrave;u v&agrave;ng xen lẫn hạt v&agrave; c&aacute;c tia đỏ rất nổi bật v&agrave; đẹp mắt. Kiwi sở hữu vị chua ngọt ho&agrave;n hảo, m&ugrave;i thơm đặc trưng, thịt quả b&ugrave;i b&ugrave;i cung cấp cho cơ thể lượng vitamin v&ocirc; c&ugrave;ng phong ph&uacute;.</p>\r\n\r\n<h3>1. Nguồn gốc Kiwi ruột đỏ Đ&agrave;i Loan</h3>\r\n\r\n<p>Kiwi lu&ocirc;n nằm trong top những loại tr&aacute;i c&acirc;y gi&agrave;u vitamin C v&agrave; được ưa chuộng nhất tr&ecirc;n thị trường hiện nay.</p>\r\n\r\n<p>Kiwi ruột đỏ l&agrave; sản phẩm lai tạo từ kiwi ruột v&agrave;ng. Tuy nhi&ecirc;n, sản phẩm n&agrave;y c&oacute; hương vị đặc biệt hơn rất nhiều, pha trộn giữa vị quả m&acirc;m x&ocirc;i v&agrave; quả kiwi v&agrave;ng m&agrave; ch&uacute;ng ta vẫn thường ăn.</p>\r\n\r\n<p><img data-src=\"https://hoaquaxanh.vn/wp-content/uploads/2023/02/1694.jpg\" decoding=\"async\" src=\"https://hoaquaxanh.vn/wp-content/uploads/2023/02/1694.jpg\" /></p>\r\n\r\n<p>Đặc biệt, kiwi ruột đỏ khi ăn chưa ch&iacute;n kh&ocirc;ng bị h&oacute;c lưỡi như kiwi v&agrave;ng. Khi ch&iacute;n, kiwi đỏ ngọt hơn kiwi v&agrave;ng.</p>\r\n\r\n<p>Hiện Kiwi đỏ vẫn đang được ph&aacute;t triển n&ecirc;n số lượng xuất khẩu cực kỳ hạn chế, rất khan hiếm tr&ecirc;n thị trường.</p>\r\n\r\n<p>Đ&agrave;i Loan l&agrave; một trong những quốc gia trồng thử nghiệm th&agrave;nh c&ocirc;ng giống kiwi n&agrave;y. hoaquaquang.vn vinh dự l&agrave; một trong những đơn vị ti&ecirc;n phong nhập khẩu giống kiwi rực rỡ n&agrave;y.</p>\r\n\r\n<p>Sản phẩm được đ&oacute;ng g&oacute;i v&agrave; xuất khẩu dưới dạng th&ugrave;ng 4 hộp 450 gram, rất sang trọng, th&iacute;ch hợp l&agrave;m qu&agrave; tặng trong c&aacute;c dịp đặc biệt.</p>\r\n\r\n<p>Sản phẩm được đ&oacute;ng hộp 1.8kg, gồm 4 hộp</p>\r\n\r\n<h3>2. Gi&aacute; trị dinh dưỡng của Kiwi ruột đỏ Đ&agrave;i Loan</h3>\r\n\r\n<p>Tương tự như c&aacute;c loại kiwi kh&aacute;c, kiwi ruột đỏ cũng sở hữu h&agrave;m lượng Vitamin C v&ocirc; c&ugrave;ng dồi d&agrave;o. V&igrave; vậy, ăn kiwi gi&uacute;p cơ thể chống lại qu&aacute; tr&igrave;nh oxy h&oacute;a, ngăn ngừa xơ vữa động mạch, hạ mỡ m&aacute;u, giảm thiểu nguy cơ mắc bệnh tim mạch.</p>\r\n\r\n<p>Đồng thời, vitamin C c&ograve;n gi&uacute;p tăng cường hệ thống miễn dịch, hạ huyết &aacute;p, giảm cholesterol v&agrave; triglyceride.</p>\r\n\r\n<p>C&aacute;c kho&aacute;ng chất như Mg, Cu, K, Zn trong quả kiwi c&oacute; vai tr&ograve; to lớn trong việc giữ cho tr&aacute;i tim khỏe mạnh.</p>\r\n\r\n<p>Ca, Cr, Cu gi&uacute;p xương chắc khỏe, khớp dẻo dai.</p>\r\n\r\n<p>Chất xơ dồi d&agrave;o trong kiwi hỗ trợ ti&ecirc;u h&oacute;a rất tốt, ngăn ngừa t&aacute;o b&oacute;n, trĩ, ruột v&agrave; ung thư ruột kết, hỗ trợ giảm c&acirc;n cho chị em phụ nữ.</p>\r\n\r\n<p>B&agrave; bầu ăn một quả kiwi mỗi ng&agrave;y sẽ bổ sung nhiều dưỡng chất cho thai nhi khỏe mạnh, tăng cường hệ miễn dịch, hỗ trợ sự ph&aacute;t triển hệ xương, hệ h&ocirc; hấp của thai nhi, em b&eacute; sinh ra &iacute;t c&oacute; nguy cơ mắc c&aacute;c bệnh nhiễm tr&ugrave;ng. hen suyễn hoặc bệnh ch&agrave;m.</p>\r\n\r\n<h3>3. C&aacute;ch thưởng thức Kiwi ruột đỏ</h3>\r\n\r\n<p>C&oacute; nhiều c&aacute;ch ăn kiwi: cắt l&aacute;t mỏng ăn tươi, hoặc trộn với sữa chua, trang tr&iacute; b&aacute;nh, trộn salad.</p>\r\n\r\n<p>Kiwi c&oacute; thịt v&agrave;ng xen lẫn những tia đỏ rất đẹp mắt</p>\r\n\r\n<p>Ch&uacute;ng ta c&oacute; thể tham khảo th&ecirc;m nhiều m&oacute;n ngon từ kiwi qua b&agrave;i viết n&agrave;y của hoaquaxanh.vn.</p>\r\n\r\n<p>Tuy nhi&ecirc;n, hoaquaxanh.vn lưu &yacute; mọi người kh&ocirc;ng n&ecirc;n để kiwi đ&atilde; gọt vỏ ngo&agrave;i kh&ocirc;ng kh&iacute; qu&aacute; l&acirc;u, điều n&agrave;y sẽ khiến Vitamin C trong quả bị oxy h&oacute;a, giảm gi&aacute; trị dinh dưỡng.</p>\r\n\r\n<p>V&igrave; vậy, khi chế biến c&aacute;c m&oacute;n ăn, n&ecirc;n cắt kiwi sau c&ugrave;ng để bảo to&agrave;n dinh dưỡng.</p>\r\n\r\n<p>Mỗi ng&agrave;y, một người chỉ cần ăn từ 1 đến 2 quả kiwi cỡ trung b&igrave;nh 120-150g l&agrave; đủ, kh&ocirc;ng n&ecirc;n lạm dụng ăn qu&aacute; nhiều, cơ thể kh&ocirc;ng hấp thụ được hết chất dinh dưỡng dẫn đến l&atilde;ng ph&iacute;.</p>\r\n\r\n<h3>4. Bảo quản kiwi đ&uacute;ng c&aacute;ch</h3>\r\n\r\n<p>Kiwi c&oacute; quanh năm nhưng quả chưa ch&iacute;n sẽ c&oacute; vị chua. Nếu l&agrave; người th&iacute;ch ăn tr&aacute;i c&acirc;y ngọt, bạn cần để th&ecirc;m v&agrave;i ng&agrave;y nữa cho kiwi đạt độ ch&iacute;n v&agrave; ngọt mới c&oacute; thể thưởng thức.</p>\r\n\r\n<p>Nếu muốn tr&aacute;i c&acirc;y ch&iacute;n nhanh hơn, hoaquaquang.vn m&aacute;ch nhỏ bạn, h&atilde;y kết hợp kiwi với một v&agrave;i loại tr&aacute;i c&acirc;y kh&aacute;c như t&aacute;o, chuối, l&ecirc; rồi d&ugrave;ng giấy g&oacute;i để ủ, kiwi sẽ ch&iacute;n nhanh đến bất ngờ.</p>\r\n\r\n<p>Theo nghi&ecirc;n cứu của nh&agrave; địa chất học Innsbruck, &Aacute;o, quả Kiwi ch&iacute;n c&oacute; thể bảo quản trong tủ lạnh tới 10 ng&agrave;y m&agrave; kh&ocirc;ng bị mất hương vị v&agrave; dinh dưỡng.</p>\r\n\r\n<h3>5. Mua Kiwi ruột đỏ Đ&agrave;i Loan ở đ&acirc;u?</h3>\r\n\r\n<p>Nếu bạn đang t&igrave;m mua những tr&aacute;i kiwi đỏ ngon, chất lượng, an to&agrave;n th&igrave; h&atilde;y đến với cửa h&agrave;ng Hoaquaxanh.vn Hoa Quả Nhập Khẩu.</p>\r\n\r\n<p>Kiwi ruột đỏ tại hoaquaxanh.vn</p>\r\n\r\n<p>Ch&uacute;ng t&ocirc;i b&aacute;n Kiwi đỏ nhập khẩu từ Đ&agrave;i Loan, c&oacute; đầy đủ giấy tờ xuất nhập cảnh. Cam kết gi&aacute; lẻ tốt như gi&aacute; sỉ, ph&ugrave; hợp với điều kiện kinh tế của từng gia đ&igrave;nh.</p>\r\n\r\n<p>Ngo&agrave;i kiwi ruột đỏ, hoaquaxanh.vn c&ograve;n c&oacute; c&aacute;c loại kiwi kh&aacute;c như kiwi v&agrave;ng Newzealand, kiwi xanh để phục vụ nhu cầu đa dạng của kh&aacute;ch h&agrave;ng.</p>\r\n', 20, 'Đã đăng', 1, '2023-11-19 13:48:11', 'vuhonglinh123', 0),
(13, 'VHL#10', 'Táo Envy Nhập Khẩu New Zealand', 150000, '<p>T&aacute;o Envy c&oacute; quả to tr&ograve;n, vỏ mỏng, m&agrave;u đỏ sọc v&agrave;ng, thịt quả gi&ograve;n, ngọt, thơm. Đặc biệt, sau khi gọt v&agrave; cắt, thịt t&aacute;o Envy vẫn giữ được m&agrave;u trắng tinh trong nhiều giờ</p>\r\n', '1750.jpg', '<p>Hiện nay, tr&ecirc;n thị trường tr&aacute;i c&acirc;y nhập khẩu c&oacute; rất nhiều loại t&aacute;o, tuy nhi&ecirc;n t&aacute;o Envy New Zealand vẫn l&agrave; giống t&aacute;o được ưa chuộng nhất bởi vị ngọt dịu, hương thơm nồng n&agrave;n rất ri&ecirc;ng, thịt t&aacute;o gi&ograve;n v&agrave; chắc. Đặc biệt, ruột t&aacute;o c&oacute; thể giữ được m&agrave;u trắng tinh khi để ngo&agrave;i kh&ocirc;ng kh&iacute; nhiều giờ m&agrave; kh&ocirc;ng bị ngả v&agrave;ng như c&aacute;c loại t&aacute;o kh&aacute;c.</p>\r\n\r\n<p><img data-src=\"https://hoaquaxanh.vn/wp-content/uploads/2023/02/1750.jpg\" decoding=\"async\" src=\"https://hoaquaxanh.vn/wp-content/uploads/2023/02/1750.jpg\" /></p>\r\n\r\n<h3><b>1. Nguồn gốc, m&ugrave;a vụ t&aacute;o Envy</b></h3>\r\n\r\n<p>Envy l&agrave; giống t&aacute;o mới, ra đời năm 2008, lai giữa Royal Gala v&agrave; Braeburn ở New Zealand, được trồng độc quyền ở New Zealand v&agrave; Mỹ. Giống t&aacute;o với hương vị thơm ngon, gi&ograve;n ngọt &ldquo;rất đ&aacute;ng ghen tị&rdquo;, đ&atilde; mang đến một l&agrave;n gi&oacute; mới cho thị trường tr&aacute;i c&acirc;y tr&ecirc;n thế giới, thuyết phục những thực kh&aacute;ch c&oacute; khẩu vị kh&oacute; t&iacute;nh nhất.</p>\r\n\r\n<p>T&aacute;o Envy thu hoạch từ th&aacute;ng 4, được bảo quản ngay trong kho lạnh bằng c&aacute;ch hạ nhiệt độ v&agrave; nồng độ oxy trong quy tr&igrave;nh kh&eacute;p k&iacute;n. V&igrave; vậy t&aacute;o c&oacute; thể bảo quản được từ 6 th&aacute;ng trở l&ecirc;n m&agrave; vẫn giữ được độ tươi, gi&ograve;n b&ecirc;n trong.</p>\r\n\r\n<p>T&aacute;o Envy New Zealand c&oacute; sọc đỏ v&agrave;ng rất đẹp</p>\r\n\r\n<h3><b>2. K&iacute;ch thước v&agrave; c&aacute;ch đ&oacute;ng g&oacute;i t&aacute;o Envy như thế n&agrave;o?</b></h3>\r\n\r\n<p>T&aacute;o Envy New Zealand tương tự như Envy Mỹ, được xuất khẩu với nhiều k&iacute;ch cỡ kh&aacute;c nhau, đ&oacute;ng g&oacute;i theo số lượng t&aacute;o/th&ugrave;ng:</p>\r\n\r\n<ul>\r\n	<li aria-level=\"1\">Size 24 đựng trong th&ugrave;ng 9Kg;</li>\r\n	<li aria-level=\"1\">Size 36, 40, 45, 50, 55, 60, 70 đ&oacute;ng th&ugrave;ng 12.5Kg;</li>\r\n	<li aria-level=\"1\">Size 100 đ&oacute;ng trong th&ugrave;ng 17.5Kg.</li>\r\n</ul>\r\n\r\n<h3><b>3. Gi&aacute; trị dinh dưỡng của t&aacute;o Envy</b></h3>\r\n\r\n<p><i>&ldquo;Mỗi ng&agrave;y một quả t&aacute;o, b&aacute;c sĩ kh&ocirc;ng tới nh&agrave;&hellip;.!&rdquo;</i></p>\r\n\r\n<p>Trung b&igrave;nh một quả t&aacute;o Envy cung cấp cho cơ thể khoảng 100 kcal. Trong đ&oacute; chất xơ chiếm 17% hỗ trợ tốt cho hệ ti&ecirc;u h&oacute;a, tạo cảm gi&aacute;c no l&acirc;u gi&uacute;p giảm c&acirc;n v&agrave; 14% vitamin C tăng cường sức đề kh&aacute;ng cho cơ thể.</p>\r\n\r\n<p>Nhờ h&agrave;m lượng dưỡng chất thực vật dồi d&agrave;o bao gồm kaempferol v&agrave; quercetin, bổ sung một quả t&aacute;o v&agrave;o thực đơn mỗi ng&agrave;y c&oacute; thể g&oacute;p phần l&agrave;m chậm qu&aacute; tr&igrave;nh ph&aacute;t triển của c&aacute;c tế b&agrave;o g&acirc;y hại, từ đ&oacute; ngăn ngừa nguy cơ mắc c&aacute;c bệnh ung thư phổ biến.</p>\r\n\r\n<p>Ngo&agrave;i ra, bạn c&oacute; thể tham khảo th&ecirc;m 12 gi&aacute; trị tuyệt vời của t&aacute;o Envy qua b&agrave;i viết n&agrave;y của Sea Flower.</p>\r\n\r\n<h3><b>4. Bảo quản đ&uacute;ng c&aacute;ch</b></h3>\r\n\r\n<p>Mặc d&ugrave; c&ugrave;i t&aacute;o Envy c&oacute; khả năng giữ được m&agrave;u trắng tinh trong nhiều giờ nhưng nếu muốn c&ugrave;i t&aacute;o được trắng l&acirc;u hơn, Sea Flower m&aacute;ch bạn một mẹo đơn giản: sau khi gọt vỏ, h&atilde;y ng&acirc;m c&ugrave;i t&aacute;o v&agrave;o nước. Pha v&agrave;i giọt chanh hoặc muối lo&atilde;ng!</p>\r\n\r\n<p>Nếu bảo quản trong tủ lạnh từ 4 &ndash; 8 độ C, t&aacute;o sẽ giữ được độ tươi, gi&ograve;n từ 1 &ndash; 4 tuần. Sau thời gian n&agrave;y t&aacute;o sẽ ngọt hơn, chỉ số PSI thấp hơn dẫn đến t&aacute;o xốp hơn.</p>\r\n\r\n<p>Cần tr&aacute;nh để t&aacute;o chung với c&aacute;c thực phẩm c&oacute; m&ugrave;i kh&aacute;c như h&agrave;nh, tỏi, t&aacute;o sẽ dễ bị nhiễm khuẩn.</p>\r\n\r\n<h4><b><i>C&aacute;c trường hợp cần lưu &yacute;:</i></b></h4>\r\n\r\n<p><b>T&aacute;o bị th&acirc;m b&ecirc;n trong:</b>&nbsp;Vỏ t&aacute;o rất chắc, hơn nữa trước khi đ&oacute;ng g&oacute;i được phủ một lớp s&aacute;p ong b&ecirc;n ngo&agrave;i để bảo vệ n&ecirc;n t&aacute;o Envy rất &iacute;t khi bị hư, trừ khi bị dập vỏ trong qu&aacute; tr&igrave;nh vận chuyển. chuyển khoản. Cuống t&aacute;o l&agrave; nơi nhạy cảm, d&ugrave; chỉ một vết xước nhỏ tr&ecirc;n cuống, hay nước đọng cũng tạo điều kiện cho vi khuẩn x&acirc;m nhập v&agrave; l&agrave;m hỏng quả t&aacute;o từ b&ecirc;n trong.</p>\r\n\r\n<p><b>T&aacute;o xốp:</b>&nbsp;T&aacute;o xốp kh&ocirc;ng phải l&agrave; bị hư m&agrave; thực chất l&agrave; do giảm độ gi&ograve;n (PSI). Người gi&agrave; v&agrave; trẻ em thường th&iacute;ch ăn t&aacute;o xốp v&igrave; kh&ocirc;ng qu&aacute; cứng m&agrave; lại ngọt. T&aacute;o xốp vẫn đảm bảo chất lượng v&agrave; h&agrave;m lượng vitamin b&ecirc;n trong. Nếu kh&aacute;ch mua nhiều ăn kh&ocirc;ng kịp vẫn c&oacute; thể d&ugrave;ng tiếp m&agrave; kh&ocirc;ng cần phải vứt đi nh&eacute;!</p>\r\n\r\n<p>Ruột t&aacute;o c&oacute; thể trắng trong nhiều giờ</p>\r\n\r\n<p><b><i>T&aacute;o bị xốp do nhiều nguy&ecirc;n nh&acirc;n:</i></b></p>\r\n\r\n<ul>\r\n	<li aria-level=\"1\">Bản th&acirc;n quả t&aacute;o khi được h&aacute;i đ&atilde; ch&iacute;n n&ecirc;n kể cả khi được bảo quản lạnh đ&uacute;ng c&aacute;ch th&igrave; n&oacute; vẫn ch&iacute;n theo tự nhi&ecirc;n v&agrave; xốp hơn theo thời gian.</li>\r\n	<li aria-level=\"1\">Do nhiệt độ trong qu&aacute; tr&igrave;nh bảo quản kh&ocirc;ng ph&ugrave; hợp, t&aacute;o bị sốc nhiệt cũng l&agrave; nguy&ecirc;n nh&acirc;n khiến t&aacute;o nhanh ch&iacute;n hơn.</li>\r\n</ul>\r\n\r\n<p><i>Nếu bạn đang t&igrave;m kiếm những tr&aacute;i t&aacute;o Envy thơm ngon, gi&ograve;n ngọt th&igrave; h&atilde;y đến với cửa h&agrave;ng Hoa Quả Nhập Khẩu hoaquaxanh.vn</i>&nbsp;<i>. Ch&uacute;ng t&ocirc;i b&aacute;n t&aacute;o Envy từ New Zealand v&agrave; Mỹ, c&oacute; đầy đủ giấy tờ xuất nhập cảnh. Cam kết gi&aacute; lẻ tốt như gi&aacute; sỉ, ph&ugrave; hợp với điều kiện kinh tế của từng gia đ&igrave;nh.</i></p>\r\n', 20, 'Đã đăng', 1, '2023-11-13 05:22:06', 'vuhonglinh123', 0),
(14, 'VHL#11', 'Kim Quất Đài Loan – Quả Tắc Đường Đài Loan', 75000, '<ul>\r\n	<li>Kim Quất Đ&agrave;i Loan gi&ograve;n, thơm, ăn rất ngon chứ kh&ocirc;ng chua v&agrave; ch&aacute;t như quất Việt Nam, ăn l&agrave; ghiền, quả c&agrave;ng v&agrave;ng c&agrave;ng ngọt.</li>\r\n	<li>Quả Kim Quất Đ&agrave;i Loan ăn được cả vỏ, vỏ nhạt v&agrave; dai, c&ugrave;i mềm ngọt, kh&ocirc;ng c&oacute; hạt ăn rất ngon.</li>\r\n	<li>Kim Quất Đ&agrave;i Loan c&oacute; thể d&ugrave;ng tươi v&agrave; ăn ngay như một loại quả, v&agrave;o m&ugrave;a lạnh ph&ograve;ng chống cảm c&uacute;m, đường ph&egrave;n d&ugrave;ng l&agrave;m thuốc ho v&agrave; tăng sức đề kh&aacute;ng cho cơ thể.</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n', 'kim-quat-dai-loan-qua-tac-duong-dai-loan-1682-4.jpg', '<h2>Kim Kim quất Đ&agrave;i Loan</h2>\r\n\r\n<p>Kim Kim quất Đ&agrave;i Loan l&agrave; loại quả c&oacute; h&igrave;nh bầu dục, khi ch&iacute;n vỏ c&oacute; m&agrave;u xanh v&agrave; khi ch&iacute;n chuyển sang m&agrave;u v&agrave;ng cam tươi.</p>\r\n\r\n<p>Kim quất kh&ocirc;ng c&oacute; m&uacute;i, cơm rất d&agrave;y, kh&ocirc;ng c&oacute; hạt, ăn cực ngọt v&agrave; gi&ograve;n, quyện với vị v&agrave; m&ugrave;i thơm đặc trưng của quất, rất đặc biệt, nhất định phải thử &iacute;t nhất một lần.</p>\r\n\r\n<p><img alt=\"Kim quất Đài Loan 500gram - Trái cây Hoa Biển\" data-src=\"https://hoaquaxanh.vn/wp-content/uploads/2023/02/kim-quat-dai-loan-qua-tac-duong-dai-loan-1682.jpg\" decoding=\"async\" src=\"https://hoaquaxanh.vn/wp-content/uploads/2023/02/kim-quat-dai-loan-qua-tac-duong-dai-loan-1682.jpg\" title=\"Quất Đài Loan 500gram 4\" /></p>\r\n\r\n<h3>1. Đặc điểm của Kim quất Đ&agrave;i Loan</h3>\r\n\r\n<p>Quất l&agrave; loại c&acirc;y c&oacute; m&uacute;i, c&oacute; nguồn gốc từ Nam &Aacute;, đặc biệt được trồng nhiều ở Đ&agrave;i Loan.</p>\r\n\r\n<p>Loại quả n&agrave;y c&oacute; h&igrave;nh bầu dục, khi ch&iacute;n vỏ chuyển từ m&agrave;u xanh sang m&agrave;u v&agrave;ng cam tươi, đường k&iacute;nh mỗi quả khoảng 3-4cm.</p>\r\n\r\n<p><img alt=\"Kim quất Đài Loan 500gram - Trái cây Hoa Biển\" data-src=\"https://hoaquaxanh.vn/wp-content/uploads/2023/02/kim-quat-dai-loan-qua-tac-duong-dai-loan-1682-1.jpg\" decoding=\"async\" src=\"https://hoaquaxanh.vn/wp-content/uploads/2023/02/kim-quat-dai-loan-qua-tac-duong-dai-loan-1682-1.jpg\" title=\"Quất Đài Loan 500gram 5\" /></p>\r\n\r\n<p>Tuy gọi l&agrave; quất nhưng quất kim chi kh&ocirc;ng c&oacute; vị chua như quất Việt Nam</p>\r\n\r\n<p>Kim quất Đ&agrave;i Loan kh&ocirc;ng m&uacute;i cũng kh&ocirc;ng hạt, vị kh&ocirc;ng chua, vỏ kh&ocirc;ng đắng như quất Việt Nam.</p>\r\n\r\n<p><img alt=\"Kim quất Đài Loan 500gram - Trái cây Hoa Biển\" data-src=\"https://hoaquaxanh.vn/wp-content/uploads/2023/02/kim-quat-dai-loan-qua-tac-duong-dai-loan-1682-2.jpg\" decoding=\"async\" src=\"https://hoaquaxanh.vn/wp-content/uploads/2023/02/kim-quat-dai-loan-qua-tac-duong-dai-loan-1682-2.jpg\" title=\"Quất Đài Loan 500gram 6\" /></p>\r\n\r\n<p>Kh&ocirc;ng m&uacute;i, kh&ocirc;ng hạt, ăn rất ngọt v&agrave; gi&ograve;n</p>\r\n\r\n<p>Ngược lại, loại quả n&agrave;y c&oacute; cơm rất d&agrave;y, kh&ocirc;ng c&oacute; hạt, ăn cực ngọt v&agrave; gi&ograve;n, h&ograve;a c&ugrave;ng vị v&agrave; m&ugrave;i thơm đặc trưng của quất, rất đặc biệt, nhất định phải thử &iacute;t nhất một lần.</p>\r\n\r\n<p>Sản phẩm được đ&oacute;ng g&oacute;i v&agrave; xuất khẩu dưới dạng th&ugrave;ng khoảng 5kg với 12 hộp, mỗi hộp gần 500g.</p>\r\n\r\n<h3>2. Gi&aacute; trị dinh dưỡng của Kim quất Đ&agrave;i Loan</h3>\r\n\r\n<p>Quả quất tuy c&oacute; k&iacute;ch thước nhỏ nhưng lại chứa h&agrave;m lượng vitamin A, C rất cao c&ugrave;ng với c&aacute;c chất như đồng, sắt, kali v&agrave; chất chống oxy h&oacute;a c&oacute; lợi cho sức khỏe.</p>\r\n\r\n<p>Vitamin C gi&uacute;p cơ thể hấp thụ sắt v&agrave; tăng cường hệ thống miễn dịch. B&ecirc;n cạnh đ&oacute;, n&oacute; c&ograve;n gi&uacute;p da s&aacute;ng v&agrave; đều m&agrave;u, mờ dần c&aacute;c đốm n&acirc;u.</p>\r\n\r\n<p><img alt=\"Kim quất Đài Loan 500gram - Trái cây Hoa Biển\" data-src=\"https://hoaquaxanh.vn/wp-content/uploads/2023/02/kim-quat-dai-loan-qua-tac-duong-dai-loan-1682-3.jpg\" decoding=\"async\" src=\"https://hoaquaxanh.vn/wp-content/uploads/2023/02/kim-quat-dai-loan-qua-tac-duong-dai-loan-1682-3.jpg\" title=\"Quất Đài Loan 500gram 7\" /></p>\r\n\r\n<p>Quất c&oacute; h&agrave;m lượng vitamin v&agrave; chất xơ dồi d&agrave;o</p>\r\n\r\n<p>C&aacute;c hợp chất chống oxy h&oacute;a gi&uacute;p ngăn ngừa qu&aacute; tr&igrave;nh l&atilde;o h&oacute;a, ngăn chặn c&aacute;c gốc tự do g&acirc;y hại, hạn chế c&aacute;c bệnh ung thư nguy hiểm.</p>\r\n\r\n<p>Ngo&agrave;i ra, bạch tuộc Đ&agrave;i Loan c&ograve;n chứa lượng chất xơ dồi d&agrave;o gi&uacute;p đường ruột hoạt động khỏe mạnh, chống t&aacute;o b&oacute;n.</p>\r\n\r\n<p>Chất xơ c&ograve;n tạo cảm gi&aacute;c no l&acirc;u, hỗ trợ giảm c&acirc;n rất hiệu quả. Đồng thời, lượng calo trong loại quả n&agrave;y cực thấp, kh&ocirc;ng g&acirc;y tăng c&acirc;n.</p>\r\n\r\n<h3>3. C&aacute;ch thưởng thức quất</h3>\r\n\r\n<p>Sở hữu vị ngọt, b&ugrave;i v&agrave; thơm, quất c&oacute; thể được ướp lạnh v&agrave; ăn tươi như một loại tr&aacute;i c&acirc;y để thưởng thức trọn vẹn hương vị.</p>\r\n\r\n<p><img alt=\"Kim quất Đài Loan 500gram - Trái cây Hoa Biển\" data-src=\"https://hoaquaxanh.vn/wp-content/uploads/2023/02/kim-quat-dai-loan-qua-tac-duong-dai-loan-1682-4.jpg\" decoding=\"async\" src=\"https://hoaquaxanh.vn/wp-content/uploads/2023/02/kim-quat-dai-loan-qua-tac-duong-dai-loan-1682-4.jpg\" title=\"Quất Đài Loan 500gram 8\" /></p>\r\n\r\n<p>C&oacute; thể ướp lạnh v&agrave; ăn tươi như tr&aacute;i c&acirc;y</p>\r\n\r\n<p>Tuy nhi&ecirc;n, bạn cũng c&oacute; thể d&ugrave;ng quất để pha tr&agrave; với mật ong, chưng với đường ph&egrave;n để trị ho, kh&aacute;ng vi&ecirc;m rất tốt cho sức khỏe.</p>\r\n\r\n<p><img alt=\"Kim quất Đài Loan 500gram - Trái cây Hoa Biển\" data-src=\"https://hoaquaxanh.vn/wp-content/uploads/2023/02/kim-quat-dai-loan-qua-tac-duong-dai-loan-1682-5.jpg\" decoding=\"async\" src=\"https://hoaquaxanh.vn/wp-content/uploads/2023/02/kim-quat-dai-loan-qua-tac-duong-dai-loan-1682-5.jpg\" title=\"Quất Đài Loan 500g 9\" /></p>\r\n\r\n<p>Ng&acirc;m với đường ph&egrave;n trị ho</p>\r\n\r\n<p><img alt=\"Kim quất Đài Loan 500gram - Trái cây Hoa Biển\" data-src=\"https://hoaquaxanh.vn/wp-content/uploads/2023/02/kim-quat-dai-loan-qua-tac-duong-dai-loan-1682-6.jpg\" decoding=\"async\" src=\"https://hoaquaxanh.vn/wp-content/uploads/2023/02/kim-quat-dai-loan-qua-tac-duong-dai-loan-1682-6.jpg\" title=\"Quất Đài Loan 500g 10\" /></p>\r\n\r\n<p>Tr&agrave; mật ong sả</p>\r\n\r\n<h3>4. Bảo quản Kim quất Đ&agrave;i Loan đ&uacute;ng c&aacute;ch</h3>\r\n\r\n<p>Quất tươi mua về n&ecirc;n bảo quản trong tủ lạnh.</p>\r\n\r\n<p>Ăn đến đ&acirc;u rửa đến đ&oacute;, kh&ocirc;ng n&ecirc;n rửa trước rồi mới bảo quản trong tủ lạnh sẽ l&agrave;m hoa quả bị &uacute;ng nước.</p>\r\n\r\n<p>Kh&ocirc;ng để quất chung với c&aacute;c thực phẩm nặng m&ugrave;i như h&agrave;nh, ng&ograve;, tỏi.</p>\r\n\r\n<h3>5. Mua Kim quất Đ&agrave;i Loan chất lượng ở đ&acirc;u?</h3>\r\n\r\n<p>Nếu bạn đang t&igrave;m mua những quả quất tươi ngon, chất lượng h&atilde;y đến với cửa h&agrave;ng hoaquaxanh.vn.</p>\r\n\r\n<p><img alt=\"Kim quất Đài Loan 500gram - Trái cây Hoa Biển\" data-src=\"https://hoaquaxanh.vn/wp-content/uploads/2023/02/kim-quat-dai-loan-qua-tac-duong-dai-loan-1682-7.jpg\" decoding=\"async\" src=\"https://hoaquaxanh.vn/wp-content/uploads/2023/02/kim-quat-dai-loan-qua-tac-duong-dai-loan-1682-7.jpg\" title=\"Quất Đài Loan 500g 11\" /></p>\r\n\r\n<p>Kim quất Đ&agrave;i Loan nhập khẩu tại hoaquasinh.vn</p>\r\n\r\n<p>Ch&uacute;ng t&ocirc;i b&aacute;n quất (tắc tắc) nhập khẩu từ Đ&agrave;i Loan, c&oacute; đầy đủ giấy tờ xuất nhập cảnh. Cam kết gi&aacute; lẻ tốt như gi&aacute; sỉ, ph&ugrave; hợp với điều kiện kinh tế của từng gia đ&igrave;nh.</p>\r\n\r\n<p>Ngo&agrave;i Kim quất Đ&agrave;i Loan, hoaquasinh.vn c&ograve;n c&oacute; c&aacute;c loại tr&aacute;i c&acirc;y nhập khẩu kh&aacute;c như lựu Th&aacute;i Lan, nho xanh kh&ocirc;ng hạt, t&aacute;o Ambrosia ,&hellip; phục vụ nhu cầu đa dạng của qu&yacute; kh&aacute;ch h&agrave;ng.</p>\r\n', 20, 'Đã đăng', 1, '2023-11-16 09:12:28', 'vuhonglinh123', 0),
(15, 'VHL#12', 'Táo Hữu Cơ Sugarbee Nhập Khẩu USA', 110000, '<p>T&aacute;o Sugarbee&nbsp;Mỹ&nbsp; Đ&oacute;ng g&oacute;i: 1 t&uacute;i / 4 tr&aacute;i&nbsp; Xuất xứ: Mỹ Hướng dẫn sử dụng: Rửa sạch trước khi&nbsp;ăn. ăn trực tiếp, ăn k&egrave;m sữa chua, l&agrave;m sinh tố, tr&agrave; tr&aacute;i c&acirc;y hoặc b&aacute;nh mứt &hellip;</p>\r\n', '1742.jpg', '<p>&nbsp;<b>T&aacute;o Sugarbee Mỹ&nbsp;</b></p>\r\n\r\n<p>Đ&oacute;ng g&oacute;i: 1 bịch/ 4 quả</p>\r\n\r\n<p>Xuất xứ: Mỹ</p>\r\n\r\n<p>Hướng dẫn sử dụng: Rửa sạch trước khi ăn.</p>\r\n\r\n<ul>\r\n	<li aria-level=\"1\">ăn trực tiếp, ăn k&egrave;m với sữa chua,</li>\r\n	<li aria-level=\"1\">l&agrave;m sinh tố, ch&egrave; tr&aacute;i c&acirc;y hay l&agrave;m mứt&hellip;</li>\r\n	<li aria-level=\"1\">Trang tr&iacute; b&aacute;nh, đồ uống, m&oacute;n ăn&hellip;</li>\r\n</ul>\r\n\r\n<p><img data-src=\"https://hoaquaxanh.vn/wp-content/uploads/2023/02/1742.jpg\" decoding=\"async\" height=\"546\" src=\"https://hoaquaxanh.vn/wp-content/uploads/2023/02/1742.jpg\" width=\"546\" /></p>\r\n\r\n<p><b>Lưu &yacute;: Kh&ocirc;ng sử dụng sản phẩm khi n&oacute; bị hư hỏng hoặc hư hỏng!</b></p>\r\n\r\n<p>&ndash; Nhiệt độ bảo quản:</p>\r\n\r\n<p>+ Bảo quản kho c&ocirc;ng nghiệp: từ 0 &ndash; 4 độ C. T&aacute;o sẽ giữ được độ tươi, gi&ograve;n trong v&ograve;ng 1 &ndash; 3 th&aacute;ng. Sau thời gian n&agrave;y, t&aacute;o sẽ ngọt hơn, độ gi&ograve;n (PSI) thấp hơn (xốp hơn).</p>\r\n\r\n<p>+ Bảo quản tại nh&agrave;: Tủ lạnh từ 4 &ndash; 8 độ C: T&aacute;o giữ được độ tươi, gi&ograve;n trong v&ograve;ng 1 &ndash; 4 tuần. Sau thời gian n&agrave;y, t&aacute;o sẽ ngọt hơn, PSI thấp hơn (t&aacute;o xốp hơn). Cần tr&aacute;nh để t&aacute;o chung với c&aacute;c thực phẩm c&oacute; m&ugrave;i kh&aacute;c như h&agrave;nh, tỏi, t&aacute;o sẽ dễ bị nhiễm khuẩn.</p>\r\n\r\n<p>&ndash; C&aacute;c trường hợp cần lưu &yacute;:</p>\r\n\r\n<p>+ T&aacute;o bị th&acirc;m b&ecirc;n trong: Vỏ t&aacute;o rất chắc v&agrave; hơn nữa thường được phủ một lớp s&aacute;p ong trước khi xuất khẩu (t&aacute;o Washington) n&ecirc;n t&aacute;o rất &iacute;t khi bị th&acirc;m b&ecirc;n ngo&agrave;i v&agrave; c&oacute; thể nh&igrave;n thấy bằng mắt thường trừ khi vỏ bị dập. do va chạm trong qu&aacute; tr&igrave;nh vận chuyển. Cuống t&aacute;o l&agrave; nơi nhạy cảm nhất, th&ocirc;ng thường chỉ cần một vết xước ở cuống, hay nước đọng tr&ecirc;n cuống cũng sẽ dễ d&agrave;ng gi&uacute;p vi khuẩn x&acirc;m nhập v&agrave;o t&aacute;o v&agrave; l&agrave;m t&aacute;o hư từ b&ecirc;n trong.</p>\r\n\r\n<p>+ T&aacute;o xốp: T&aacute;o xốp kh&ocirc;ng phải t&aacute;o hư m&agrave; do độ gi&ograve;n (PSI) giảm. Nhiều người th&iacute;ch ăn t&aacute;o xốp v&igrave; kh&ocirc;ng qu&aacute; cứng, nhất l&agrave; khi cho trẻ ăn. T&aacute;o xốp thường rất ngọt. T&aacute;o xốp vẫn đảm bảo chất lượng v&agrave; h&agrave;m lượng vitamin trong quả. T&aacute;o xốp v&igrave; nhiều l&yacute; do. Đầu ti&ecirc;n l&agrave; bản th&acirc;n quả t&aacute;o khi h&aacute;i đ&atilde; hơi ch&iacute;n n&ecirc;n kể cả khi được bảo quản lạnh đ&uacute;ng c&aacute;ch th&igrave; n&oacute; vẫn ch&iacute;n nhanh hơn v&agrave; xốp hơn. Thứ hai l&agrave; do qu&aacute; tr&igrave;nh bảo quản ở nhiệt độ kh&ocirc;ng đ&uacute;ng khiến t&aacute;o ch&iacute;n nhanh hơn.​</p>\r\n\r\n<p>M&ocirc; tả Sản phẩm</p>\r\n\r\n<p>Đối với h&agrave;ng tươi, trọng lượng thực tế c&oacute; thể ch&ecirc;nh lệch khoảng 10%.</p>\r\n\r\n<ul>\r\n	<li aria-level=\"1\">T&aacute;o SugarBee được thu hoạch tại c&aacute;c vườn c&acirc;y ăn quả của bang Washington v&agrave; một nửa được cung cấp từ Honey Crisp. t&aacute;o</li>\r\n	<li aria-level=\"1\">T&aacute;o SugarBee mọng nước, miếng thịt thơm khi cắn v&agrave; vị t&aacute;o ngọt</li>\r\n	<li aria-level=\"1\">Da đỏ ửng, c&oacute; 2 m&agrave;u, đỏ từ 30%-100%, thịt m&agrave;u trắng kem</li>\r\n	<li aria-level=\"1\">SugarBee sẽ thu h&uacute;t những người ăn t&aacute;o v&agrave; m&agrave;u sắc đẹp mắt của n&oacute; sẽ thu h&uacute;t kh&aacute;ch h&agrave;ng ở mọi lứa tuổi!</li>\r\n</ul>\r\n\r\n<p><b>Gi&aacute; trị dinh dưỡng:</b></p>\r\n\r\n<ul>\r\n	<li aria-level=\"1\">L&agrave; d&ograve;ng tr&aacute;i c&acirc;y nhập khẩu cao cấp nhất n&ecirc;n lượng chất dinh dưỡng c&oacute; trong T&aacute;o kh&ocirc;ng l&agrave;m bạn thất vọng. H&agrave;m lượng vitamin A, C, B, chất xơ, chất chống oxi h&oacute;a, kali, sắt, magie dồi d&agrave;o gi&uacute;p cải thiện v&agrave; duy tr&igrave; sức khỏe.</li>\r\n	<li aria-level=\"1\">Ăn &iacute;t nhất 5 quả t&aacute;o mỗi tuần, phổi sẽ hoạt động hiệu quả hơn nhờ chất chống oxy h&oacute;a Quercetin c&oacute; trong vỏ t&aacute;o. Phụ nữ khi mang thai n&ecirc;n ăn thường xuy&ecirc;n để ngăn ngừa nguy cơ mắc bệnh hen suyễn cho thai nhi.</li>\r\n	<li aria-level=\"1\">Gi&uacute;p giảm 27% nguy cơ tử vong do c&aacute;c bệnh tim mạch, 40% nguy cơ đột quỵ</li>\r\n	<li aria-level=\"1\">Ngăn ngừa bệnh tiểu đường loại 2, chống ung thư, Chống bệnh Parkinson v&agrave; Alzheimer, Chống ti&ecirc;u chảy v&agrave; t&aacute;o b&oacute;n.</li>\r\n	<li aria-level=\"1\">T&aacute;o chứa nhiều boron l&agrave;m tăng lượng estrogen trong m&aacute;u v&agrave; c&aacute;c hợp chất kh&aacute;c, gi&uacute;p ngăn ngừa mất canxi v&agrave; gi&uacute;p xương lu&ocirc;n chắc khỏe.</li>\r\n</ul>\r\n', 20, 'Đã đăng', 1, '2023-11-16 09:14:03', 'vuhonglinh123', 0),
(16, 'VHL#1316', 'Lê Hàn Quốc Nhập Khẩu', 110000, '<p><strong>L&ecirc; H&agrave;n Quốc</strong>&nbsp; nhập khẩu ch&iacute;nh ngạch trực tiếp tại H&agrave;n Quốc. C&oacute; đầy đủ giầy tờ chứng minh nguồn gốc, quả to, tươi ngon, đi biếu hay đi tặng đều rất sang. H&agrave;ng loại 1, l&ecirc; nhiều nước, ăn gi&ograve;n v&agrave; ngọt.</p>\r\n', '1744-768x768.jpg', '<h2><b>Đặc điểm/xuất xứ của l&ecirc; H&agrave;n Quốc</b></h2>\r\n\r\n<p>L&ecirc; H&agrave;n Quốc được trồng ở v&ugrave;ng Nachi. L&ecirc;&nbsp;<b>H&agrave;n Quốc</b>&nbsp;l&agrave; loại l&ecirc; cao cấp nhất được tuyển chọn tại những vườn danh tiếng nhất H&agrave;n Quốc. Với k&iacute;ch thước quả rất to khoảng 800gr-1000gr/quả, vỏ mỏng, nhẵn, kh&ocirc;ng t&igrave; vết, c&ugrave;i gi&ograve;n, ngọt v&agrave; mọng nước, chắc chắn bạn sẽ thấy sự kh&aacute;c biệt trong hương vị của những tr&aacute;i l&ecirc; chuy&ecirc;n xuất khẩu tr&ecirc;n thị trường. Nhật Bản, Mỹ, Đức&hellip;</p>\r\n\r\n<p><img data-src=\"https://hoaquaxanh.vn/wp-content/uploads/2023/02/1744.jpg\" decoding=\"async\" src=\"https://hoaquaxanh.vn/wp-content/uploads/2023/02/1744.jpg\" /></p>\r\n\r\n<p>L&ecirc; n&acirc;u H&agrave;n Quốc h&igrave;nh tr&ograve;n, b&ecirc;n trong m&agrave;u trắng, b&ecirc;n ngo&agrave;i m&agrave;u v&agrave;ng, ăn c&oacute; vị ngọt, hơi chua ở l&otilde;i v&agrave; rất ngon. Loại quả n&agrave;y l&agrave; c&acirc;y lai giữa t&aacute;o v&agrave; l&ecirc;.</p>\r\n\r\n<p>Kh&aacute;c với c&aacute;c loại l&ecirc; kh&aacute;c, khi ch&iacute;n l&ecirc; vẫn giữ được độ gi&ograve;n, đặc biệt l&ecirc; kh&ocirc;ng bị th&acirc;m khi cắt n&ecirc;n rất ngon khi d&ugrave;ng l&agrave;m salad.</p>\r\n\r\n<p><b>L&ecirc; H&agrave;n Quốc</b>&nbsp;từ l&acirc;u đ&atilde; được người ti&ecirc;u d&ugrave;ng Việt Nam ưa chuộng bởi chất lượng đ&atilde; được khẳng định tr&ecirc;n to&agrave;n thế giới. H&agrave;ng năm, v&agrave;o m&ugrave;a l&ecirc; H&agrave;n Quốc, h&agrave;ng ngh&igrave;n hộp l&ecirc; được nhập về phục vụ kh&aacute;ch h&agrave;ng tại SodaFoods.</p>\r\n\r\n<h2><b>Gi&aacute; trị dinh dưỡng của quả l&ecirc; H&agrave;n Quốc:</b></h2>\r\n\r\n<p>Theo y học cổ truyền, l&ecirc; c&oacute; t&iacute;nh m&aacute;t, hơi chua, c&oacute; t&aacute;c dụng thanh nhiệt, nhuận phế, ti&ecirc;u đờm, giảm ho, thanh t&acirc;m hỏa, dưỡng huyết, sinh t&acirc;n dịch, nhuận tr&agrave;ng, giải độc.</p>\r\n\r\n<p>L&ecirc; rất gi&agrave;u chất xơ h&ograve;a tan, l&ecirc; thực sự l&agrave; loại tr&aacute;i c&acirc;y chống tiểu đường, chống cholesterol v&agrave; cứu đ&oacute;i. Ngo&agrave;i ra, với sự g&oacute;p mặt của chất chống oxy h&oacute;a tanin trong th&agrave;nh phần, l&ecirc; c&ograve;n l&agrave; loại tr&aacute;i c&acirc;y c&oacute; khả năng giữ cho tr&aacute;i tim khỏe mạnh. L&ecirc; rất gi&agrave;u chất dinh dưỡng. Cứ 100g quả l&ecirc; c&oacute; 86,5g nước, 0,1g chất b&eacute;o, 0,2g protein, 11g chất bột đường, 1,6g chất xơ, 14mg canxi, 13mg photpho, 0,5mg sắt, 0,2mg vitamin PP. , vitamin nh&oacute;m P, C, betacaroten, axit folic 1mg. So với thực phẩm thực vật, l&ecirc; c&oacute; nhiều chất xơ, đ&oacute;ng vai tr&ograve; quan trọng trong qu&aacute; tr&igrave;nh ti&ecirc;u h&oacute;a v&agrave; trao đổi chất.</p>\r\n\r\n<p><img data-src=\"https://hoaquaxanh.vn/wp-content/uploads/2023/02/1744-1.jpg\" decoding=\"async\" src=\"https://hoaquaxanh.vn/wp-content/uploads/2023/02/1744-1.jpg\" /></p>\r\n\r\n<h2><b>Bảo quản v&agrave; sử dụng l&ecirc; H&agrave;n Quốc</b></h2>\r\n\r\n<p><b>&ndash; L&ecirc; H&agrave;n Quốc</b>&nbsp;bảo quản tốt nhất ở nhiệt độ từ 0 đến 4 độ C, c&oacute; thể đảm bảo độ tươi ngon trong hơn 1 th&aacute;ng. Ngo&agrave;i ra, l&ecirc; cũng kh&ocirc;ng n&ecirc;n để qu&aacute; l&acirc;u để tr&aacute;nh mất hương vị v&agrave; chất dinh dưỡng.</p>\r\n\r\n<p>&ndash; L&ecirc; H&agrave;n Quốc c&oacute; thể ăn tươi, &eacute;p lấy nước hoặc l&agrave;m salad rau củ</p>\r\n', 20, 'Đã đăng', 1, '2023-11-13 05:22:06', 'vuhonglinh123', 0),
(17, 'VHL#14', 'Lựu đỏ Peru', 180000, '<p>Lựu nhập khẩu trực tiếp từ Peru theo đường h&agrave;ng kh&ocirc;ng size lớn, tr&aacute;i to, vỏ m&agrave;u hồng đỏ, hạt đỏ thẫm, nhiều nước, ăn rất ngon, b&ecirc;n cạnh đ&oacute; c&ograve;n chứa nhiều vitamin v&agrave; c&aacute;c kho&aacute;ng chất c&oacute; lợi cho sức khỏe v&agrave; ph&ograve;ng ngừa bệnh tật.</p>\r\n', 'luu-do-peru-1668.jpg', '<p><strong>Lựu đỏ nhập&nbsp;l&agrave; một trong những loại tr&aacute;i c&acirc;y nhập khẩu được người ti&ecirc;u d&ugrave;ng Việt Nam rất y&ecirc;u th&iacute;ch v&agrave; c&oacute; b&aacute;n tại hoaquaxanh.vn.</strong></p>\r\n\r\n<p><img data-src=\"https://hoaquaxanh.vn/wp-content/uploads/2023/02/luu-do-peru-1668.jpg\" decoding=\"async\" src=\"https://hoaquaxanh.vn/wp-content/uploads/2023/02/luu-do-peru-1668.jpg\" /></p>\r\n\r\n<h2><b>Nguồn gốc xuất xứ v&agrave; đặc điểm của Lựu đỏ nhập khẩu</b></h2>\r\n\r\n<p>Lựu Peru l&agrave; một loại tr&aacute;i c&acirc;y được trồng tại nước Cộng h&ograve;a Peru v&agrave; đ&atilde; được xuất khẩu đi nhiều thị trường tr&ecirc;n tr&ecirc;n thế giới.</p>\r\n\r\n<p>Peru&nbsp;l&agrave; một quốc gia nằm tại khu vực T&acirc;y Nam Mỹ, c&oacute; vị tr&iacute; địa l&yacute; ph&iacute;a Bắc gi&aacute;p với Ecuador v&agrave; Colombia, về ph&iacute;a đ&ocirc;ng l&agrave; Brasil, về ph&iacute;a đ&ocirc;ng nam l&agrave; Bolivia, ở ph&iacute;a nam l&agrave; Chile, v&agrave; ph&iacute;a t&acirc;y Peru l&agrave; Th&aacute;i B&igrave;nh Dương. Đ&acirc;y l&agrave; một quốc gia nổi tiếng về giống lựu c&oacute; k&iacute;ch thước tr&aacute;i lớn nhờ c&oacute; kh&iacute; hậu ổn định v&agrave; ph&ugrave; hợp để trồng lựu.</p>\r\n\r\n<p>Giống lựu size lớn của Peru n&agrave;y được xuất khẩu sang nhiều thị trường kh&oacute; t&iacute;nh như Nga, Canada v&agrave; Mỹ.&nbsp;<b>Lựu Peru nhập khẩu</b>&nbsp;v&agrave;o Việt Nam theo đường h&agrave;ng kh&ocirc;ng size lớn được rất nhiều người ti&ecirc;u d&ugrave;ng y&ecirc;u th&iacute;ch v&agrave; săn t&igrave;m.</p>\r\n\r\n<p>M&ugrave;a vụ lựu Peru thường diễn ra v&agrave;o thời gian từ th&aacute;ng 3 đến th&aacute;ng 8.</p>\r\n\r\n<p><img data-src=\"https://hoaquaxanh.vn/wp-content/uploads/2023/02/luu-do-peru-1668-1.jpg\" decoding=\"async\" src=\"https://hoaquaxanh.vn/wp-content/uploads/2023/02/luu-do-peru-1668-1.jpg\" /></p>\r\n\r\n<h2><b>Gi&aacute; trị dinh dưỡng v&agrave; lợi &iacute;ch đối với sức khỏe của Lựu đỏ nhập khẩu</b></h2>\r\n\r\n<p>Lựu nhập khẩu Peru c&oacute; đặc điểm l&agrave; vỏ m&agrave;u đỏ hồng, k&iacute;ch thước tr&aacute;i lớn (khoảng 700g/tr&aacute;i), hạt đỏ thẫm, nhiều nước, ăn rất ngọt.</p>\r\n\r\n<p>Nước quả lựu c&oacute; chứa nhiều chất chống oxy h&oacute;a polyphenol, c&oacute; t&aacute;c dụng bảo vệ cơ thể khỏi c&aacute;c gốc tự do g&acirc;y l&atilde;o h&oacute;a, gi&uacute;p ph&ograve;ng ngừa c&aacute;c bệnh tim mạch, bệnh Alzheimer v&agrave; cả ung thư.</p>\r\n\r\n<p>Trong th&agrave;nh phần của nước quả Lựu c&oacute; chứa nhiều vitamin v&agrave; kho&aacute;ng chất như natri, vitamin B2, sinh tố B, niaxin, vitamin C, canxi v&agrave; photpho&hellip; c&oacute; t&aacute;c dụng l&agrave;m giảm qu&aacute; tr&igrave;nh h&igrave;nh th&agrave;nh c&aacute;c mảng b&aacute;m trong c&aacute;c động mạch.</p>\r\n\r\n<p>Dầu hạt quả lựu c&oacute; t&aacute;c dụng ngăn ngừa sự h&igrave;nh th&agrave;nh ung thư da nhờ khả năng l&agrave;m l&agrave;nh c&aacute;c tổn thương tr&ecirc;n da nhanh ch&oacute;ng. B&ecirc;n cạnh đ&oacute;, nước quả lựu c&ograve;n c&oacute; t&aacute;c dụng gi&uacute;p c&aacute;c trị em khắc phục c&aacute;c triệu chứng thời k&igrave; m&atilde;n kinh.</p>\r\n\r\n<p>Nghi&ecirc;n cứu của c&aacute;c nh&agrave; khoa học cũng cho thấy nước quả lựu c&oacute; t&aacute;c dụng trong việc điều trị c&aacute;c bệnh về tim, l&atilde;o h&oacute;a sớm v&agrave; ung thư. Ăn lựu gi&uacute;p bảo vệ sức khỏe tim mạch v&agrave; ngăn ngừa c&aacute;c bệnh về tim mạch.</p>\r\n\r\n<p>Với những lợi &iacute;ch cho sức khỏe như tr&ecirc;n, c&aacute;c chuy&ecirc;n gia khuyến kh&iacute;ch bạn n&ecirc;n ăn lựu để c&oacute; sức khỏe tốt v&agrave; ph&ograve;ng ngừa nguy cơ mắc c&aacute;c bệnh tật.</p>\r\n\r\n<h2><b>Hướng dẫn bảo quản v&agrave; sử dụng</b></h2>\r\n\r\n<p>Bảo quản lựu Peru ở nhiệt độ 0 &ndash; 4 độ C gi&uacute;p bạn c&oacute; thể giữ được lựu đến 10 ng&agrave;y. Trong qu&aacute; tr&igrave;nh vận chuyển lu&ocirc;n phải ổn định, tr&aacute;nh l&agrave;m dập hỏng phần hạt trong lớp vỏ.</p>\r\n\r\n<p>Nếu bạn kh&ocirc;ng biết c&aacute;ch t&aacute;ch lựu th&igrave; sẽ l&agrave;m dập đi phần hạt rất nhiều v&agrave; l&agrave;m quả mất ngon. Bạn c&oacute; thể t&aacute;ch lựu một c&aacute;ch đơn giản theo hướng dẫn như sau:</p>\r\n\r\n<ul>\r\n	<li aria-level=\"1\">Cắt đi phần đầu của Lựu Peru</li>\r\n	<li aria-level=\"1\">Sau đ&oacute; cắt v&agrave;o c&aacute;c khe mắt</li>\r\n	<li aria-level=\"1\">D&ugrave;ng tay nhẹ nh&agrave;ng t&aacute;ch lựu</li>\r\n</ul>\r\n\r\n<p>Qu&yacute; kh&aacute;ch c&oacute; thể li&ecirc;n hệ trực tiếp đến số hotline để được hướng dẫn mua h&agrave;ng nhanh ch&oacute;ng nhất.</p>\r\n', 20, 'Đã đăng', 1, '2023-11-16 09:33:36', 'vuhonglinh123', 0);
INSERT INTO `tb_products` (`product_id`, `product_code`, `product_name`, `price`, `product_desc`, `product_thumb`, `product_content`, `quantity`, `status`, `cat_id`, `created_date`, `creator`, `sales`) VALUES
(18, 'VHL#15', 'Me Thái Ngọt Nhập Khẩu', 150000, '<p>Me Th&aacute;i Nguy&ecirc;n Tr&aacute;i</p>\r\n\r\n<ul>\r\n	<li>Me Th&aacute;i được nhập khẩu trực tiếp từ Th&aacute;i Lan.</li>\r\n	<li>Quả me Th&aacute;i Lan đạt ti&ecirc;u chuẩn sạch, đảm bảo an to&agrave;n vệ sinh thực phẩm.</li>\r\n	<li>Me Th&aacute;i Nguy&ecirc;n Vỏ quả vẫn giữ được độ dẻo của me, mềm chứ kh&ocirc;ng qu&aacute; kh&ocirc; như me bỏ hạt đ&oacute;ng hộp.</li>\r\n	<li>Me Th&aacute;i rất &iacute;t vị chua, ngọt vừa ngon, rất thơm m&ugrave;i me, d&ugrave;ng với ch&uacute;t muối ớt l&agrave; t&ecirc; t&aacute;i.</li>\r\n</ul>\r\n', 'me-thai-ngot-nhap-khau-1671.jpg', '<p>Sản phẩm me Th&aacute;i Lan nhập khẩu hẳn l&agrave; m&oacute;n kho&aacute;i khẩu của rất nhiều bạn trẻ, đặc biệt l&agrave; c&aacute;c chị em phụ nữ. Sản phẩm n&agrave;y được nhập trực tiếp từ hoaquaxanh.vn n&ecirc;n đảm bảo chất lượng. Me Th&aacute;i lu&ocirc;n rất ngọt v&agrave; thơm.</p>\r\n\r\n<p>✓ Me được nhập khẩu 100% từ Th&aacute;i Lan (Giấy chứng nhận nhập khẩu)</p>\r\n\r\n<p>✓ Đạt ti&ecirc;u chuẩn xuất khẩu to&agrave;n cầu</p>\r\n\r\n<p>✓ Bảo quản cẩn thận đến tận tay kh&aacute;ch h&agrave;ng</p>\r\n\r\n<p>✓ Me Th&aacute;i c&oacute; m&agrave;u n&acirc;u, quả to, d&agrave;i v&agrave; hơi cong</p>\r\n\r\n<p>✓ Me c&oacute; vị ngọt đậm, kh&ocirc;ng chua, c&ugrave;i d&agrave;y, dẻo, hạt vừa, m&ugrave;i thơm đặc trưng</p>\r\n\r\n<p><img alt=\"Me Thái Ngọt Nhập Khẩu - Bao Bao, Bao Bì\" data-src=\"https://hoaquaxanh.vn/wp-content/uploads/2023/02/me-thai-ngot-nhap-khau-1671-1.jpg\" decoding=\"async\" src=\"https://hoaquaxanh.vn/wp-content/uploads/2023/02/me-thai-ngot-nhap-khau-1671-1.jpg\" /></p>\r\n\r\n<h2>Gi&aacute; trị dinh dưỡng trong 100g me Th&aacute;i</h2>\r\n\r\n<ul>\r\n	<li>Vitamin A: 30 IU</li>\r\n	<li>Vitamin B: 0,34 mg</li>\r\n	<li>Vitamin B2: 0,14 mg</li>\r\n	<li>Niacin: 1,2 mg</li>\r\n	<li>Vitamin C: 2 mg</li>\r\n	<li>Canxi: 74 mg</li>\r\n	<li>Sắt: 2,8 mg</li>\r\n	<li>Phốt pho: 113 mg</li>\r\n	<li>Chất b&eacute;o: 0,6 gam</li>\r\n	<li>Chất đạm: 2,8 gam</li>\r\n	<li>Năng lượng: 239</li>\r\n</ul>\r\n\r\n<p><img alt=\"Me Thái Ngọt Nhập Khẩu - Bao Bao, Bao Bì\" data-src=\"https://hoaquaxanh.vn/wp-content/uploads/2023/02/me-thai-ngot-nhap-khau-1671-2.jpg\" decoding=\"async\" src=\"https://hoaquaxanh.vn/wp-content/uploads/2023/02/me-thai-ngot-nhap-khau-1671-2.jpg\" /></p>\r\n\r\n<h2>C&ocirc;ng dụng của me ngọt Th&aacute;i Lan</h2>\r\n\r\n<p>Ngo&agrave;i việc sử dụng trong ng&agrave;nh chế biến thực phẩm v&agrave; b&aacute;nh kẹo, me ngọt Th&aacute;i Lan c&ograve;n được ứng dụng trong y học bởi những nguy&ecirc;n tố v&agrave; dược chất c&oacute; trong me mang lại cho sức khỏe con người.</p>\r\n\r\n<p>Chống vi&ecirc;m v&agrave; nhiễm tr&ugrave;ng</p>\r\n\r\n<p>Quả me c&oacute; chứa chất s&aacute;t tr&ugrave;ng v&agrave; l&agrave;m l&agrave;nh vết thương. Khi bị thương hoặc bị c&ocirc;n tr&ugrave;ng cắn, bạn c&oacute; thể lấy thịt quả me b&ocirc;i l&ecirc;n để l&agrave;m dịu vết thương v&agrave; tr&aacute;nh l&acirc;y lan sang c&aacute;c v&ugrave;ng kh&aacute;c. Ngo&agrave;i ra, nếu đường ruột bị vi&ecirc;m, bạn cũng c&oacute; thể lấy nước cốt quả me để uống, cũng c&oacute; t&aacute;c dụng s&aacute;t tr&ugrave;ng đường ruột rất hiệu quả.</p>\r\n\r\n<p>kiểm so&aacute;t cholesterol</p>\r\n\r\n<p>Niacin v&agrave; vitamin B1 trong quả me sẽ gi&uacute;p giảm lượng cholesterol xấu trong cơ thể v&agrave; tăng lượng cholesterol tốt gi&uacute;p hệ tim mạch lu&ocirc;n khỏe mạnh.</p>\r\n\r\n<p>Ngăn ngừa b&eacute;o ph&igrave; v&agrave; kiểm so&aacute;t huyết &aacute;p</p>\r\n\r\n<p>Lượng kali trong mỗi quả me cao gấp đ&ocirc;i lượng kali trong mỗi quả chuối. Khi sử dụng me v&agrave;o cơ thể, chất n&agrave;y sẽ kiểm so&aacute;t hoạt động của natri trong cơ thể, kh&ocirc;ng để lượng natri tăng l&ecirc;n tr&aacute;nh l&agrave;m tăng huyết &aacute;p.</p>\r\n\r\n<p>Trong mỗi quả me c&ograve;n chứa chất riboflavin gi&uacute;p chuyển h&oacute;a carbohydrate th&agrave;nh năng lượng hỗ trợ qu&aacute; tr&igrave;nh giảm c&acirc;n của cơ thể v&agrave; ngăn ngừa b&eacute;o ph&igrave;.</p>\r\n\r\n<p>Điều trị t&aacute;o b&oacute;n</p>\r\n\r\n<p>Hiện tượng t&aacute;o b&oacute;n, kh&oacute; ti&ecirc;u thường gặp ở nhiều người ở nhiều độ tuổi kh&aacute;c nhau, chất xơ trong me gi&uacute;p điều h&ograve;a nhu động ruột, nhuận tr&agrave;ng gi&uacute;p hệ ti&ecirc;u h&oacute;a của bạn khỏe mạnh hơn.</p>\r\n\r\n<p>Ngăn ngừa ung thư v&agrave; bổ m&aacute;u</p>\r\n\r\n<p>Quả me kh&ocirc;ng c&oacute; t&aacute;c dụng chữa ung thư nhưng lại l&agrave; một nh&acirc;n tố ph&ograve;ng ngừa ung thư rất hiệu quả. Chất chống oxy h&oacute;a trong quả me gi&uacute;p ngăn ngừa sự h&igrave;nh th&agrave;nh c&aacute;c gốc tự do của tế b&agrave;o ung thư.</p>\r\n\r\n<p>Khi nạp me v&agrave;o cơ thể, me c&ograve;n c&oacute; t&aacute;c dụng lọc m&aacute;u rất tốt. Ngo&agrave;i ra, me Th&aacute;i c&ograve;n chứa nhiều vitamin A rất tốt cho sự ph&aacute;t triển v&agrave; sức khỏe của đ&ocirc;i mắt.</p>\r\n\r\n<p><img alt=\"Me Thái Ngọt Nhập Khẩu - Bao Bao, Bao Bì\" data-src=\"https://hoaquaxanh.vn/wp-content/uploads/2023/02/me-thai-ngot-nhap-khau-1671-3.jpg\" decoding=\"async\" src=\"https://hoaquaxanh.vn/wp-content/uploads/2023/02/me-thai-ngot-nhap-khau-1671-3.jpg\" /></p>\r\n', 9, 'Đã đăng', 1, '2023-11-13 05:22:06', 'vuhonglinh123', 7),
(19, 'VHL#16', 'Hồng Sấy Dẻo Hàn Quốc', 150000, '<p>Hồng sấy dẻo H&agrave;n Quốc được sấy kh&ocirc; bằng phương ph&aacute;p 100% tự nhi&ecirc;n dưới &aacute;nh nắng mặt trời. Sản phẩm hồng kh&ocirc; nổi tiếng của v&ugrave;ng Jeondo, tỉnh Gyeongbuk được l&agrave;m từ những quả hồng kh&ocirc;ng hạt duy nhất của H&agrave;n Quốc</p>\r\n', 'hong-say-deo-han-quoc-1755.jpg', '<p>Hồng sấy kh&ocirc; với những quả dai dai l&agrave; một trong những niềm tự h&agrave;o của người H&agrave;n Quốc. Kh&ocirc;ng nơi n&agrave;o tạo n&ecirc;n hương vị đặc biệt như hồng kh&ocirc; H&agrave;n Quốc. Loại hồng đặc biệt, lạ miệng n&agrave;y m&agrave; c&oacute; mặt trong m&acirc;m kẹo ng&agrave;y Tết cổ truyền của d&acirc;n tộc để chi&ecirc;u đ&atilde;i người th&acirc;n, bạn b&egrave; sẽ v&ocirc; c&ugrave;ng hấp dẫn.</p>\r\n\r\n<p>Hồng sấy dẻo H&agrave;n Quốc được sấy kh&ocirc; bằng phương ph&aacute;p 100% tự nhi&ecirc;n dưới &aacute;nh nắng mặt trời. Sản phẩm hồng kh&ocirc; nổi tiếng của v&ugrave;ng Jeondo, tỉnh Gyeongbuk được l&agrave;m từ những quả hồng kh&ocirc;ng hạt duy nhất của H&agrave;n Quốc. Chất lượng hồng ở đ&acirc;y được đ&aacute;nh gi&aacute; l&agrave; ngon nhất H&agrave;n Quốc. Quả hồng kh&ocirc; c&oacute; m&agrave;u cam đậm tự nhi&ecirc;n, b&ecirc;n ngo&agrave;i c&oacute; một lớp phấn trắng tự nhi&ecirc;n, đ&oacute; ch&iacute;nh l&agrave; đường trong quả.</p>\r\n\r\n<p>Quả hồng được người H&agrave;n Quốc y&ecirc;u th&iacute;ch v&agrave; tặng nhau v&agrave;o những dịp quan trọng trong năm. Vừa thưởng thức miếng hồng sấy dẻo mềm ngọt vừa nh&acirc;m nhi với t&aacute;ch tr&agrave; n&oacute;ng th&igrave; thật l&agrave; tuyệt, v&igrave; thế hồng dẻo cũng l&agrave; m&oacute;n ăn vặt kho&aacute;i khẩu của nhiều người.</p>\r\n\r\n<p><img data-src=\"https://hoaquaxanh.vn/wp-content/uploads/2023/02/hong-say-deo-han-quoc-1755.jpg\" decoding=\"async\" src=\"https://hoaquaxanh.vn/wp-content/uploads/2023/02/hong-say-deo-han-quoc-1755.jpg\" /></p>\r\n\r\n<h2>Th&ocirc;ng tin sản phẩm:</h2>\r\n\r\n<p>T&ecirc;n sản phẩm: Hồng sấy kh&ocirc; 16 quả 1 nắng</p>\r\n\r\n<ul>\r\n	<li>Độ ẩm: &lt;40%</li>\r\n	<li>Sản xuất tại H&agrave;n Quốc</li>\r\n	<li>K&iacute;ch thước: nguy&ecirc;n dạng</li>\r\n	<li>K&iacute;ch thước hộp: Hộp 25 x 15 cm</li>\r\n	<li>Th&agrave;nh phần: 100% hồng xi&ecirc;m H&agrave;n Quốc</li>\r\n	<li>Khối lượng tịnh: 2 hộp x 500g (hộp 10 quả)</li>\r\n	<li>HSD: 08 th&aacute;ng</li>\r\n</ul>\r\n\r\n<p><img data-src=\"https://hoaquaxanh.vn/wp-content/uploads/2023/02/hong-say-deo-han-quoc-1755-1.jpg\" decoding=\"async\" src=\"https://hoaquaxanh.vn/wp-content/uploads/2023/02/hong-say-deo-han-quoc-1755-1.jpg\" /></p>\r\n\r\n<h2>Hướng dẫn sử dụng:</h2>\r\n\r\n<p>Lưu &yacute; điều kiện bảo quản trong ngăn đ&aacute; tủ lạnh, mở t&uacute;i sử dụng trực tiếp, ph&ugrave; hợp với mọi đối tượng kể cả người ăn chay.</p>\r\n\r\n<p>Hồng sấy kh&ocirc; NK H&agrave;n Quốc c&oacute; hương vị đặc trưng ri&ecirc;ng</p>\r\n\r\n<h2>M&ocirc; tả Sản phẩm :</h2>\r\n\r\n<p>Những quả hồng kh&ocirc; rất tinh tế v&agrave; đẹp mắt. Loại hồng d&ugrave;ng để sấy l&agrave; những quả hồng to tr&ograve;n, ăn rất ngọt v&agrave; kh&ocirc;ng hạt.</p>\r\n\r\n<p>Hồng kh&ocirc; chọn quả hồng to, tr&ograve;n đều, rất ngọt v&agrave; kh&ocirc;ng hạt.</p>\r\n\r\n<p>Hồng sấy kh&ocirc; được sấy kh&ocirc; tự nhi&ecirc;n, kh&ocirc;ng chứa chất bảo quản hay tẩm ướp bất kỳ chất phụ gia n&agrave;o n&ecirc;n rất an to&agrave;n cho sức khỏe.</p>\r\n\r\n<p>C&oacute; vị ngọt đậm đ&agrave;, hương thơm tự nhi&ecirc;n, đặc biệt mềm v&agrave; dẻo</p>\r\n\r\n<p>Hồng kh&ocirc; H&agrave;n Quốc cũng c&oacute; t&aacute;c dụng chữa bệnh nhất định. Ch&uacute;ng c&oacute; t&aacute;c dụng rất tốt trong việc hỗ trợ điều trị bệnh cao huyết &aacute;p, c&aacute;c bệnh về đường huyết..</p>\r\n\r\n<p>Gi&aacute; trị dinh dưỡng ho&agrave;n to&agrave;n tự nhi&ecirc;n, l&agrave; m&oacute;n qu&agrave; v&ocirc; c&ugrave;ng &yacute; nghĩa</p>\r\n', 16, 'Đã đăng', 1, '2023-11-27 06:11:51', 'vuhonglinh123', 0),
(20, 'VHL#17', 'Táo Xanh New Zealand', 250000, '<p>&ndash; Xuất xứ: H&agrave;n Quốc<br />\r\n&ndash; M&ugrave;a vụ: từ th&aacute;ng 12&nbsp;đến th&aacute;ng 5 năm sau<br />\r\n&ndash; Chủng loại:&nbsp;Maehyang</p>\r\n', '1683538239tao-xanh-new.jpg', '<p>D&acirc;u H&agrave;n Quốc Golden Farm được mệnh danh l&agrave; &ldquo;nữ ho&agrave;ng tr&aacute;i c&acirc;y&rdquo; của xứ sở Kim Chi. Chinh phục người ti&ecirc;u d&ugrave;ng bởi hương thơm tự nhi&ecirc;n. Vị ngọt tinh tế cũng như h&igrave;nh thức v&agrave; chất lượng vượt trội.</p>\r\n\r\n<p>D&acirc;u t&acirc;y H&agrave;n Quốc c&oacute; ti&ecirc;u chuẩn chất lượng vượt trội nhờ hệ thống quản l&yacute; chất lượng nghi&ecirc;m ngặt bằng c&ocirc;ng nghệ n&ocirc;ng trại th&ocirc;ng minh. Đặc biệt, người trồng d&acirc;u hạn chế tối đa việc sử dụng thuốc bảo vệ thực vật theo ti&ecirc;u chuẩn GAP.</p>\r\n\r\n<p><img data-src=\"https://hoaquaxanh.vn/wp-content/uploads/2023/02/dau-tay-han-quoc-vip-1713.jpg\" decoding=\"async\" height=\"667\" src=\"https://hoaquaxanh.vn/wp-content/uploads/2023/02/dau-tay-han-quoc-vip-1713.jpg\" width=\"667\" /></p>\r\n\r\n<p>Hiện d&acirc;u t&acirc;y H&agrave;n Quốc trở th&agrave;nh mặt h&agrave;ng n&ocirc;ng sản xuất khẩu chủ lực của H&agrave;n Quốc. D&acirc;u t&acirc;y H&agrave;n Quốc đ&atilde; được cấp ph&eacute;p nhập khẩu ch&iacute;nh ngạch tại Việt Nam.</p>\r\n\r\n<p>&ndash; Sản xuất tại H&agrave;n Quốc</p>\r\n\r\n<p>&ndash; M&ugrave;a vụ: từ th&aacute;ng 12 đến th&aacute;ng 5 năm sau</p>\r\n\r\n<p>+ D&acirc;u t&acirc;y H&agrave;n Quốc c&oacute; m&agrave;u đỏ au, tươi ngon, vị ngọt tự nhi&ecirc;n v&agrave; đặc biệt l&agrave; hương thơm nồng n&agrave;n, quyến rũ. + Điểm kh&aacute;c biệt lớn nhất của giống D&acirc;u t&acirc;y HQ l&agrave; D&acirc;u t&acirc;y n&agrave;y c&oacute; vị ngọt v&agrave; thơm, kh&aacute;c hẳn với c&aacute;c d&ograve;ng d&acirc;u t&acirc;y Ch&acirc;u &Acirc;u thường c&oacute; vị chua truyền thống.</p>\r\n\r\n<p>&ndash; Th&ocirc;ng tin dinh dưỡng:</p>\r\n\r\n<p>+ D&acirc;u t&acirc;y H&agrave;n Quốc gi&uacute;p th&uacute;c đẩy chuyển h&oacute;a c&aacute;c chất trong cơ thể, tăng cường lưu th&ocirc;ng m&aacute;u, c&oacute; t&aacute;c dụng thanh nhiệt, an thần, d&ugrave;ng được khi mắc bệnh tiểu đường&hellip;</p>\r\n\r\n<p>+ D&acirc;u t&acirc;y H&agrave;n Quốc gi&uacute;p tăng sức đề kh&aacute;ng, chống nhiễm tr&ugrave;ng, ngộ độc, cảm c&uacute;m v&agrave; stress, chống l&atilde;o h&oacute;a, giảm t&aacute;c hại của thuốc l&aacute;, l&agrave; thức uống giải kh&aacute;t rất được ưa chuộng&hellip;</p>\r\n\r\n<p>+ D&acirc;u t&acirc;y c&oacute; vị ngọt, chua, t&iacute;nh m&aacute;t, c&oacute; t&aacute;c dụng bổ phổi, điều h&ograve;a chức năng ti&ecirc;u h&oacute;a, bồi bổ cơ thể, m&aacute;t huyết, giải độc. D&ugrave;ng chữa c&aacute;c bệnh như ho do phổi n&oacute;ng, vi&ecirc;m họng, ch&aacute;n ăn, tiểu gắt, thiếu m&aacute;u, suy nhược, mụn nhọt, say rượu&hellip;</p>\r\n', 20, 'Đã đăng', 1, '2023-11-28 02:59:31', 'vuhonglinh123', 0),
(21, 'VHL#18', 'Cam xanh Úc', 169000, '<p>Cam Dolci Navel được ph&aacute;t hiện v&agrave;o năm 2010 bởi một người h&aacute;i tr&aacute;i c&acirc;y Washington Navels tr&ecirc;n trang trại của c&aacute;c ch&agrave;ng trai Nardi (Frank, Mick v&agrave; Joe) ở Leeton, New South Wales.&nbsp;&Ocirc;ng nhận thấy một nh&aacute;nh của c&acirc;y cam c&oacute; ba đến bốn quả cam c&oacute; m&agrave;u sắc kh&aacute;c thường với lớp vỏ m&agrave;u xanh n&acirc;u. Sau khi nếm thử họ thấy những quả cam n&agrave;y c&oacute; hương vị v&ocirc; c&ugrave;ng ngọt ng&agrave;o, ngọt ng&agrave;o hơn nhiều so với những d&ograve;ng cam Navel th&ocirc;ng thường kh&aacute;c.</p>\r\n', 'e40c2b703e50146557e288379b2c4c3ee5fad555_s2_n2_y2.png', '<h2><strong>CAM XANH &Uacute;C&nbsp;</strong></h2>\r\n\r\n<h3>1. Giống v&agrave; chủng loại</h3>\r\n\r\n<ul>\r\n	<li>Cam Xanh &Uacute;c được nhập khẩu trực tiếp từ &Uacute;c. Đ&acirc;y l&agrave; một quốc gia c&oacute; tất cả c&aacute;c m&ugrave;a, điều kiện tự nhi&ecirc;n thuận lợi n&ecirc;n &nbsp;tạo điều kiện cho cam xanh ph&aacute;t triển thuận lợi v&agrave; đạt chất lượng hảo &nbsp;hạng nhất</li>\r\n	<li>&nbsp;M&ugrave;a vụ: từ th&aacute;ng 7 đến th&aacute;ng 12 h&agrave;ng năm&nbsp;</li>\r\n</ul>\r\n\r\n<h3>2. Đặc điểm sản phẩm</h3>\r\n\r\n<ul>\r\n	<li>Cam Dolci Navel được ph&aacute;t hiện v&agrave;o năm 2010 bởi một người h&aacute;i tr&aacute;i c&acirc;y Washington Navels tr&ecirc;n trang trại của c&aacute;c ch&agrave;ng trai Nardi (Frank, Mick v&agrave; Joe) ở Leeton, New South Wales.&nbsp;&Ocirc;ng nhận thấy một nh&aacute;nh của c&acirc;y cam c&oacute; ba đến bốn quả cam c&oacute; m&agrave;u sắc kh&aacute;c thường với lớp vỏ m&agrave;u xanh n&acirc;u. Sau khi nếm thử họ thấy những quả cam n&agrave;y c&oacute; hương vị v&ocirc; c&ugrave;ng ngọt ng&agrave;o, ngọt ng&agrave;o hơn nhiều so với những d&ograve;ng cam Navel th&ocirc;ng thường kh&aacute;c.</li>\r\n	<li>Thời gian thu hoạch: Từ th&aacute;ng 7 tới đầu th&aacute;ng 8</li>\r\n	<li>Cam Dolci c&oacute; 1 m&agrave;u sắc rất kh&aacute;c biệt: m&agrave;u xanh n&acirc;u. Lớp vỏ sần s&ugrave;i b&ecirc;n ngo&agrave;i tuy nhi&ecirc;n khi bổ ra bạn sẽ thấy sắc cam v&agrave;ng &oacute;ng ả, t&eacute;p cam mọng nước thơm ngọt v&agrave; đặc biệt kh&ocirc;ng c&oacute; hạt.</li>\r\n	<li>M&uacute;i cam to, t&eacute;p mọng nước m&agrave;u v&agrave;ng tươi, &iacute;t xơ, vị ngọt thanh, thơm &nbsp;m&aacute;t v&agrave; đặc trưng m&agrave; ai ai cũng th&iacute;ch l&agrave; hầu hết kh&ocirc;ng c&oacute; hạt. Vị tươi &nbsp;ngọt sảng kho&aacute;i tan v&agrave;o trong miệng khiến bạn cảm thấy cực k&igrave; sung sướng &nbsp;khi được thưởng thức cam v&agrave;ng &Uacute;c</li>\r\n</ul>\r\n\r\n<h3>3. Gi&aacute; trị dinh dưỡng</h3>\r\n\r\n<ul>\r\n	<li>Cam v&agrave;ng &Uacute;c c&oacute; chứa nhiều Vitamin C, tốt cho da, chống l&atilde;o h&oacute;a, &nbsp;giảm Cholesterol, c&oacute; t&aacute;c dụng hồi phục sức khỏe nhanh, tăng cường chức &nbsp;năng tạo hồng huyết cầu, giảm căng thẳng thần kinh.</li>\r\n	<li>&nbsp;Việc ti&ecirc;u thụ vitamin C ở liều cao (khoảng 500mg mỗi ng&agrave;y) rất tốt cho người ốm.&nbsp;Quả cam mọng nước chứa một h&agrave;m lượng Vitamin C cao đến mức chỉ &nbsp;cần ăn một quả cũng đ&aacute;p ứng được 130% nhu cầu vitaminC h&agrave;ng ng&agrave;y của &nbsp;ch&uacute;ng ta.</li>\r\n	<li>Ngo&agrave;i ra, cam c&ograve;n được biết tới như một loại đồ ăn ki&ecirc;ng gi&agrave;u &nbsp;chất xơ, Vitamin A, B, Canxi, Magnesium, Sắt, Đồng, Iốt&hellip;</li>\r\n	<li>Ăn cam thường xuy&ecirc;n sẽ gi&uacute;p ph&ograve;ng tr&aacute;nh c&aacute;c bệnh truyền nhiễm do &nbsp;virus; giảm đ&aacute;ng kể nguy cơ mắc bệnh sỏi thận. Để tr&aacute;nh lượng calo dư &nbsp;thừa, h&atilde;y bổ sung Cam v&agrave;o chế độ dinh dưỡng h&agrave;ng ng&agrave;y của m&igrave;nh.</li>\r\n</ul>\r\n\r\n<h3>4. Lưu &yacute; bảo quản</h3>\r\n\r\n<ul>\r\n	<li>Cam c&oacute; thể được bảo quản ở nhiệt độ thường hoặc trong tủ lạnh. Nếu &nbsp;bảo quản trong tủ lạnh, nhiệt độ th&iacute;ch hợp nhất từ 7oC &ndash; 10oC. Đối với &nbsp;cả hai phương ph&aacute;p bảo quản, cam thường sẽ tươi được trong khoảng 2 tuần &nbsp;v&agrave; sẽ giữ được độ tươi c&ugrave;ng h&agrave;m lượng vitamin.</li>\r\n	<li>C&aacute;ch tốt nhất để bảo quản cam l&agrave; để tự nhi&ecirc;n thay v&igrave; cho v&agrave;o t&uacute;i &nbsp;nhựa v&igrave; nếu tiếp x&uacute;c với độ ẩm, cam c&oacute; thể dễ d&agrave;ng xuất hiện nấm mốc.</li>\r\n	<li>Cam xanh &Uacute;c vị ngọt thanh, &iacute;t xơ thường d&ugrave;ng để ăn tươi chứ kh&ocirc;ng n&ecirc;n &eacute;p nước.</li>\r\n</ul>\r\n', 20, 'Đã đăng', 1, '2023-11-27 11:49:55', 'vuhonglinh123', 0),
(22, 'VHL#19', 'Táo Juliet Pháp', 129000, '<ul>\r\n	<li>Giảm cholesterol v&agrave; chất chống oxy h&oacute;a, ngăn ngừa bệnh tim.</li>\r\n	<li>Điều chỉnh &aacute;p suất m&aacute;u v&agrave; giữ cho nhịp đập tim ở mức ổn định, chất chống &ocirc;xy ho&aacute; tự nhi&ecirc;n gi&uacute;p bảo vệ th&agrave;nh mạch m&aacute;u.</li>\r\n	<li>Vỏ t&aacute;o gi&agrave;u chất xơ v&agrave; c&oacute; lợi cho hệ ti&ecirc;u h&oacute;a</li>\r\n	<li>Lượng đường tự nhi&ecirc;n trong t&aacute;o kh&ocirc;ng ảnh hưởng tới bệnh nh&acirc;n tiểu đường.</li>\r\n	<li>Tăng khả năng ghi nhớ của n&atilde;o người, n&acirc;ng cao khả năng đề kh&aacute;ng của cơ thể.</li>\r\n</ul>\r\n', 'tao-jiliet.png', '<h1><strong>T&aacute;o hữu cơ Juliet</strong></h1>\r\n\r\n<h3>1.Giống v&agrave; chủng loại</h3>\r\n\r\n<p><img src=\"https://hoaquafuji.com/storage/app/media/Tao-Apple/tao-juliet-01-min1.jpg\" /></p>\r\n\r\n<ul>\r\n	<li>Nhập khẩu từ Ph&aacute;p</li>\r\n	<li>T&aacute;o Juliet l&agrave; thương hiệu t&aacute;o nổi tiếng bậc nhất thế giới, c&oacute; thể coi đ&acirc;y l&agrave; loại t&aacute;o hữu cơ đầu ti&ecirc;n đạt chất lượng xuất khẩu. Ra đời ở những trang trại t&aacute;o rộng lớn Nam nước Ph&aacute;p, diện t&iacute;ch dự kiến 350 hecta. T&aacute;o Juliet Organic l&agrave; th&agrave;nh quả của qu&aacute; tr&igrave;nh nghi&ecirc;n cứu l&acirc;u năm (những c&acirc;y đầu ti&ecirc;n được chồng từ những năm 90 của thế kỉ trước), qua nhiều thử nghiệm nghi&ecirc;m ngặt, t&aacute;o juliet kh&ocirc;ng sử bất k&igrave; loại ph&acirc;n b&oacute;n, h&oacute;a chất, thuốc trừ s&acirc;u diệt cỏ n&agrave;o, nghi&ecirc;m cấm c&aacute;c loại thuốc k&iacute;ch th&iacute;ch tăng trưởng. Đảm bảo t&aacute;o hữu cơ 100%.</li>\r\n	<li>M&ugrave;a vụ: T&aacute;o Juliet thường c&oacute; quanh năm, đứt lứa 1-2 th&aacute;ng do thời tiết</li>\r\n</ul>\r\n\r\n<h3>2. Đặc điểm sản phẩm</h3>\r\n\r\n<ul>\r\n	<li>T&aacute;o hữu cơ Juliet c&oacute; k&iacute;ch thước quả vừa phải. Vỏ t&aacute;o m&agrave;u đỏ sậm tr&ecirc;n nền v&agrave;ng nhạt. Thịt t&aacute;o m&agrave;u trắng, độ gi&ograve;n, ngọt v&agrave; điều tạo n&ecirc;n sự kh&aacute;c biệt với c&aacute;c giống t&aacute;o kh&aacute;c l&agrave; hương thơm đậm của n&oacute;.</li>\r\n	<li>C&acirc;y t&aacute;o Juliet c&oacute; đặc t&iacute;nh rất khỏe mạnh, c&oacute; khả năng tự nhi&ecirc;n chống lại một số c&aacute;c căn bệnh thường gặp ở t&aacute;o.</li>\r\n	<li>T&aacute;o Juliet l&agrave; giống t&aacute;o đầu ti&ecirc;n tr&ecirc;n thế giới được sản xuất độc quyền sử dụng c&aacute;c biện ph&aacute;p trồng trọt hữu cơ.</li>\r\n	<li>Tại Ph&aacute;p, t&aacute;o hữu cơ Juliet thực sự rất phổ biến v&agrave; được ưa chuộng. N&oacute; thậm ch&iacute; c&oacute; cả nh&acirc;n vật hoạt h&igrave;nh l&agrave;m biểu tượng ri&ecirc;ng m&agrave; chưa giống t&aacute;o n&agrave;o c&oacute;.</li>\r\n</ul>\r\n\r\n<h3>3. Gi&aacute; trị dinh dưỡng</h3>\r\n\r\n<ul>\r\n	<li>Giảm cholesterol v&agrave; chất chống oxy h&oacute;a, ngăn ngừa bệnh tim.</li>\r\n	<li>Điều chỉnh &aacute;p suất m&aacute;u v&agrave; giữ cho nhịp đập tim ở mức ổn định, chất chống &ocirc;xy ho&aacute; tự nhi&ecirc;n gi&uacute;p bảo vệ th&agrave;nh mạch m&aacute;u.</li>\r\n	<li>Vỏ t&aacute;o gi&agrave;u chất xơ v&agrave; c&oacute; lợi cho hệ ti&ecirc;u h&oacute;a</li>\r\n	<li>Lượng đường tự nhi&ecirc;n trong t&aacute;o kh&ocirc;ng ảnh hưởng tới bệnh nh&acirc;n tiểu đường.</li>\r\n	<li>Tăng khả năng ghi nhớ của n&atilde;o người, n&acirc;ng cao khả năng đề kh&aacute;ng của cơ thể.</li>\r\n</ul>\r\n\r\n<h3>4. Lưu &yacute; bảo quản</h3>\r\n\r\n<ul>\r\n	<li>Để t&aacute;o kh&ocirc;ng bị th&acirc;m, ta cắt t&aacute;o nh&uacute;ng qua nước lạnh c&oacute; pha vai giọt nước cốt chanh hoặc nước muối lo&atilde;ng.</li>\r\n	<li>Nhiệt độ bảo quản: Bảo quản tại nh&agrave;: Tủ lạnh từ 4 đến 8 độ C. T&aacute;o giữ đươc độ tươi, độ gi&ograve;n trong v&ograve;ng 4 tuần. Sau thời gian n&agrave;y, t&aacute;o sẽ ngọt hơn, độ PSI thấp hơn (t&aacute;o xốp hơn). Cần tr&aacute;nh để với c&aacute;c thực phẩm c&oacute; m&ugrave;i kh&aacute;c như h&agrave;nh, tỏi, t&aacute;o sẽ dễ nhiễm m&ugrave;i.</li>\r\n</ul>\r\n', 20, 'Đã đăng', 1, '2023-11-27 06:13:01', 'vuhonglinh123', 0),
(23, 'VHL#20', 'Bơ Booth', 55000, '<p>Bơ booth xuất hiện ở Việt Nam từ hơn 10 năm trước v&agrave; được trồng ở nhiều nơi tại nước ta, từ T&acirc;y Nguy&ecirc;n, Đồng Bằng s&ocirc;ng Cửu Long cho đến miền Đ&ocirc;ng Nam Bộ.</p>\r\n\r\n<p>Trong đ&oacute;, bơ booth được trồng ở T&acirc;y Nguy&ecirc;n đạt năng suất cao v&agrave; c&oacute; chất lượng tốt nhất nhờ điều kiện thổ dưỡng thuận lợi như h&agrave;m lượng chất dinh dưỡng cao của đất đỏ bazan v&agrave; khả năng tho&aacute;t nước tốt.</p>\r\n', '6-1.png', '<p><strong>BƠ BOOTH</strong></p>\r\n\r\n<h2>1. Bơ booth l&agrave; g&igrave;?</h2>\r\n\r\n<p>Để nhận biết được bơ booth, bạn h&atilde;y dựa v&agrave;o những đặc điểm sau đ&acirc;y:&nbsp;Đặc điểm của bơ booth&nbsp;Bơ booth l&agrave; loại bơ được lai gh&eacute;p giữa giống bơ T&acirc;y Ấn Độ v&agrave; giống bơ Guatemala, do chủ trang trại t&ecirc;n l&agrave; Willam J.Krome v&agrave; Will Booth ở v&ugrave;ng Florida ph&iacute;a nam của Dade Country lai tạo ra.&nbsp;Bơ booth xuất hiện ở Việt Nam từ hơn 10 năm trước v&agrave; được trồng ở nhiều nơi tại nước ta, từ T&acirc;y Nguy&ecirc;n, Đồng Bằng s&ocirc;ng Cửu Long cho đến miền Đ&ocirc;ng Nam Bộ.&nbsp;Trong đ&oacute;, bơ booth được trồng ở T&acirc;y Nguy&ecirc;n đạt năng suất cao v&agrave; c&oacute; chất lượng tốt nhất nhờ điều kiện thổ dưỡng thuận lợi như h&agrave;m lượng chất dinh dưỡng cao của đất đỏ bazan v&agrave; khả năng tho&aacute;t nước tốt.</p>\r\n\r\n<h2><strong>2. Đặc điểm của bơ booth</strong></h2>\r\n\r\n<p>Bơ booth c&oacute; dạng h&igrave;nh tr&ograve;n đều, cầm chắc tay, k&egrave;m với lớp vỏ d&agrave;y, m&agrave;u xanh hơi ng&agrave; v&agrave;ng khi ch&iacute;n, nhiều v&acirc;n x&ugrave; x&igrave;, da căng nhưng kh&ocirc;ng b&oacute;ng.&nbsp;Thịt bơ m&agrave;u v&agrave;ng đậm, dẻo v&agrave; m&ugrave;i thơm nhẹ nhưng c&oacute; vị b&eacute;o kh&ocirc;ng bằng so với bơ 034. K&iacute;ch thước bơ c&agrave;ng to th&igrave; thường c&oacute; độ dẻo thơm c&agrave;ng nhiều.&nbsp;Trung b&igrave;nh mỗi quả bơ booth nặng khoảng 400 &ndash; 700gr với phần cuống d&agrave;i, kh&ocirc;ng mọc th&agrave;nh từng ch&ugrave;m như một số giống bơ kh&aacute;c. Mức độ ch&iacute;n của bơ booth l&acirc;u hơn so với bơ b&igrave;nh thường, n&ecirc;n thuận tiện cho việc bảo quản từ 5 &ndash; 7 ng&agrave;y.&nbsp;Bơ booth cũng được chia th&agrave;nh nhiều giống bơ kh&aacute;c nhau &ndash; đ&aacute;nh dấu từ số 1 cho đến số 8 tương ứng với 8 giống được lai tạo. Một v&agrave;i loại bơ booth c&oacute; điểm đặc trưng được liệt k&ecirc; dưới đ&acirc;y:&nbsp;Bơ booth 1: Đ&acirc;y l&agrave; giống đầu ti&ecirc;n n&ecirc;n k&iacute;ch thước kh&aacute; lớn, to nhất trong c&aacute;c giống bơ booth. Phần thịt c&oacute; m&agrave;u trắng v&agrave;ng nhạt, hương vị kh&ocirc;ng đậm đ&agrave; m&agrave; lại chứa hạt to b&ecirc;n trong n&ecirc;n phần thịt dường như rất &iacute;t. V&igrave; thế, giống n&agrave;y rất &iacute;t được sử dụng.&nbsp;Bơ booth 3: C&oacute; h&igrave;nh dạng tương tự như bơ Hass nhưng k&iacute;ch thước bầu v&agrave; tr&ograve;n hơn, dao động từ 13 &ndash; 15cm. Vỏ xanh, nhẵn, trơn b&oacute;ng nhưng hạt to n&ecirc;n phần thịt b&ecirc;n trong kh&aacute; &iacute;t, giống như bơ booth 1 n&ecirc;n kh&ocirc;ng mang lại gi&aacute; trị kinh tế cao.&nbsp;Bơ booth 5: Lớp vỏ ngo&agrave;i đặc biệt hơn so với giống bơ booth kh&aacute;c, d&ugrave; sần nhưng vẫn tạo ra độ b&oacute;ng nhất định. K&iacute;ch thước quả từ 7 &ndash; 10cm nhưng phần thịt b&ecirc;n trong rất &iacute;t so với k&iacute;ch thước hạt, c&oacute; m&agrave;u nhạt v&agrave; vị b&eacute;o vừa đủ.&nbsp;Bơ booth 7: Giống bơ được trồng phổ biến tại c&aacute;c tỉnh T&acirc;y Nguy&ecirc;n v&igrave; cho năng suất cao, vượt trội hơn so với c&aacute;c giống bơ kh&aacute;c.&nbsp;Đặc biệt, chất lượng bơ booth 7 rất được ưa chuộng bởi k&iacute;ch thước tr&ograve;n đều, dao động từ 10 &ndash; 12cm, nh&igrave;n bắt mắt. Lớp vỏ c&oacute; m&agrave;u xanh tươi, phần ruột bơ b&ecirc;n trong d&agrave;y, độ s&aacute;p cao v&agrave; c&oacute; m&agrave;u v&agrave;ng đậm.&nbsp;Trung b&igrave;nh mỗi quả bơ booth 7 nặng khoảng 350gr v&agrave; thường ch&iacute;n khoảng từ 4 &ndash; 6 ng&agrave;y sau khi thu hoạch n&ecirc;n c&oacute; thời gian bảo quản tốt hơn so với những giống bơ kh&aacute;c. Thời điểm thu hoạch của bơ booth 7 diễn ra từ th&aacute;ng 9 đến th&aacute;ng 11.&nbsp;Bơ booth 8: C&oacute; h&igrave;nh d&aacute;ng d&agrave;i v&agrave; bầu hơn so với bơ booth 7 (tr&ograve;n đều). K&iacute;ch thước to, dao động từ 10 &ndash; 13cm c&ugrave;ng với lớp vỏ trơn. Phần thịt nhiều tương tự như booth 7.</p>\r\n\r\n<h2>3. Gi&aacute; trị dinh dưỡng&nbsp;</h2>\r\n\r\n<p>Trong 450g quả Bơ Booth bao gồm: 50 calo, 4,5g chất b&eacute;o, 1g protein, 1g chất xơ, c&aacute;c vitamin v&agrave; kho&aacute;ng chất cần thiết cho cơ thể. Việc ăn bơ đ&uacute;ng c&aacute;ch sẽ mang lại nhiều t&aacute;c dụng tốt cho sức khỏe như:</p>\r\n\r\n<ul>\r\n	<li>Ph&ograve;ng chống ung thư</li>\r\n	<li>Hỗ trợ hệ ti&ecirc;u h&oacute;a hoạt động tốt hơn</li>\r\n	<li>Ph&ograve;ng chống bệnh lo&atilde;ng xương</li>\r\n	<li>Giảm stress</li>\r\n	<li>Tốt cho sức khỏe mẹ bầu v&agrave; sự ph&aacute;t triển của thai nhi</li>\r\n	<li>Ph&ograve;ng ngừa bệnh tim mạch</li>\r\n	<li>Cải thiện sinh l&yacute; nam giới</li>\r\n	<li>Ph&ograve;ng chống bệnh tiểu đường</li>\r\n</ul>\r\n\r\n<h2>4. Bảo quản&nbsp;</h2>\r\n\r\n<p>Đặt nơi kh&ocirc; r&aacute;o v&agrave; c&oacute; nhiệt độ ổn định từ 23 &ndash; 25 độ C, đồng thời tr&aacute;nh &aacute;nh nắng mặt trời chiếu trực tiếp. C&oacute; thể đặt bơ tiếp x&uacute;c với mặt đất nhưng tr&aacute;nh cho bơ chưa ch&iacute;n v&agrave;o t&uacute;i nilong v&igrave; dễ bị thối&nbsp;&nbsp;</p>\r\n', 20, 'Đã đăng', 1, '2023-11-22 13:48:11', 'vuhonglinh123', 0),
(24, 'VHL#21', 'Cam vàng Úc', 169000, '<p>Cam Dolci Navel được ph&aacute;t hiện v&agrave;o năm 2010 bởi một người h&aacute;i tr&aacute;i c&acirc;y Washington Navels tr&ecirc;n trang trại của c&aacute;c ch&agrave;ng trai Nardi (Frank, Mick v&agrave; Joe) ở Leeton, New South Wales.&nbsp;&Ocirc;ng nhận thấy một nh&aacute;nh của c&acirc;y cam c&oacute; ba đến bốn quả cam c&oacute; m&agrave;u sắc kh&aacute;c thường với lớp vỏ m&agrave;u xanh n&acirc;u. Sau khi nếm thử họ thấy những quả cam n&agrave;y c&oacute; hương vị v&ocirc; c&ugrave;ng ngọt ng&agrave;o, ngọt ng&agrave;o hơn nhiều so với những d&ograve;ng cam Navel th&ocirc;ng thường kh&aacute;c.</p>\r\n', 'e40c2b703e50146557e288379b2c4c3ee5fad555_s2_n2_y2-768x768.png', '<h2><strong>CAM XANH &Uacute;C&nbsp;</strong></h2>\r\n\r\n<h3>1. Giống v&agrave; chủng loại</h3>\r\n\r\n<ul>\r\n	<li>Cam Xanh &Uacute;c được nhập khẩu trực tiếp từ &Uacute;c. Đ&acirc;y l&agrave; một quốc gia c&oacute; tất cả c&aacute;c m&ugrave;a, điều kiện tự nhi&ecirc;n thuận lợi n&ecirc;n &nbsp;tạo điều kiện cho cam xanh ph&aacute;t triển thuận lợi v&agrave; đạt chất lượng hảo &nbsp;hạng nhất</li>\r\n	<li>&nbsp;M&ugrave;a vụ: từ th&aacute;ng 7 đến th&aacute;ng 12 h&agrave;ng năm&nbsp;</li>\r\n</ul>\r\n\r\n<h3>2. Đặc điểm sản phẩm</h3>\r\n\r\n<ul>\r\n	<li>Cam Dolci Navel được ph&aacute;t hiện v&agrave;o năm 2010 bởi một người h&aacute;i tr&aacute;i c&acirc;y Washington Navels tr&ecirc;n trang trại của c&aacute;c ch&agrave;ng trai Nardi (Frank, Mick v&agrave; Joe) ở Leeton, New South Wales.&nbsp;&Ocirc;ng nhận thấy một nh&aacute;nh của c&acirc;y cam c&oacute; ba đến bốn quả cam c&oacute; m&agrave;u sắc kh&aacute;c thường với lớp vỏ m&agrave;u xanh n&acirc;u. Sau khi nếm thử họ thấy những quả cam n&agrave;y c&oacute; hương vị v&ocirc; c&ugrave;ng ngọt ng&agrave;o, ngọt ng&agrave;o hơn nhiều so với những d&ograve;ng cam Navel th&ocirc;ng thường kh&aacute;c.</li>\r\n	<li>Thời gian thu hoạch: Từ th&aacute;ng 7 tới đầu th&aacute;ng 8</li>\r\n	<li>Cam Dolci c&oacute; 1 m&agrave;u sắc rất kh&aacute;c biệt: m&agrave;u xanh n&acirc;u. Lớp vỏ sần s&ugrave;i b&ecirc;n ngo&agrave;i tuy nhi&ecirc;n khi bổ ra bạn sẽ thấy sắc cam v&agrave;ng &oacute;ng ả, t&eacute;p cam mọng nước thơm ngọt v&agrave; đặc biệt kh&ocirc;ng c&oacute; hạt.</li>\r\n	<li>M&uacute;i cam to, t&eacute;p mọng nước m&agrave;u v&agrave;ng tươi, &iacute;t xơ, vị ngọt thanh, thơm &nbsp;m&aacute;t v&agrave; đặc trưng m&agrave; ai ai cũng th&iacute;ch l&agrave; hầu hết kh&ocirc;ng c&oacute; hạt. Vị tươi &nbsp;ngọt sảng kho&aacute;i tan v&agrave;o trong miệng khiến bạn cảm thấy cực k&igrave; sung sướng &nbsp;khi được thưởng thức cam v&agrave;ng &Uacute;c</li>\r\n</ul>\r\n\r\n<h3>3. Gi&aacute; trị dinh dưỡng</h3>\r\n\r\n<ul>\r\n	<li>Cam v&agrave;ng &Uacute;c c&oacute; chứa nhiều Vitamin C, tốt cho da, chống l&atilde;o h&oacute;a, &nbsp;giảm Cholesterol, c&oacute; t&aacute;c dụng hồi phục sức khỏe nhanh, tăng cường chức &nbsp;năng tạo hồng huyết cầu, giảm căng thẳng thần kinh.</li>\r\n	<li>&nbsp;Việc ti&ecirc;u thụ vitamin C ở liều cao (khoảng 500mg mỗi ng&agrave;y) rất tốt cho người ốm.&nbsp;Quả cam mọng nước chứa một h&agrave;m lượng Vitamin C cao đến mức chỉ &nbsp;cần ăn một quả cũng đ&aacute;p ứng được 130% nhu cầu vitaminC h&agrave;ng ng&agrave;y của &nbsp;ch&uacute;ng ta.</li>\r\n	<li>Ngo&agrave;i ra, cam c&ograve;n được biết tới như một loại đồ ăn ki&ecirc;ng gi&agrave;u &nbsp;chất xơ, Vitamin A, B, Canxi, Magnesium, Sắt, Đồng, Iốt&hellip;</li>\r\n	<li>Ăn cam thường xuy&ecirc;n sẽ gi&uacute;p ph&ograve;ng tr&aacute;nh c&aacute;c bệnh truyền nhiễm do &nbsp;virus; giảm đ&aacute;ng kể nguy cơ mắc bệnh sỏi thận. Để tr&aacute;nh lượng calo dư &nbsp;thừa, h&atilde;y bổ sung Cam v&agrave;o chế độ dinh dưỡng h&agrave;ng ng&agrave;y của m&igrave;nh.</li>\r\n</ul>\r\n\r\n<h3>4. Lưu &yacute; bảo quản</h3>\r\n\r\n<ul>\r\n	<li>Cam c&oacute; thể được bảo quản ở nhiệt độ thường hoặc trong tủ lạnh. Nếu &nbsp;bảo quản trong tủ lạnh, nhiệt độ th&iacute;ch hợp nhất từ 7oC &ndash; 10oC. Đối với &nbsp;cả hai phương ph&aacute;p bảo quản, cam thường sẽ tươi được trong khoảng 2 tuần &nbsp;v&agrave; sẽ giữ được độ tươi c&ugrave;ng h&agrave;m lượng vitamin.</li>\r\n	<li>C&aacute;ch tốt nhất để bảo quản cam l&agrave; để tự nhi&ecirc;n thay v&igrave; cho v&agrave;o t&uacute;i &nbsp;nhựa v&igrave; nếu tiếp x&uacute;c với độ ẩm, cam c&oacute; thể dễ d&agrave;ng xuất hiện nấm mốc.</li>\r\n	<li>Cam xanh &Uacute;c vị ngọt thanh, &iacute;t xơ thường d&ugrave;ng để ăn tươi chứ kh&ocirc;ng n&ecirc;n &eacute;p nước.</li>\r\n</ul>\r\n', 20, 'Đã đăng', 1, '2023-11-22 13:49:37', 'vuhonglinh123', 0),
(25, 'VHL#22', 'Hồng táo Jujubin', 219000, '<ul>\r\n	<li>Hồng t&aacute;o Jujubin si&ecirc;u ngon, ăn cực cuốn lưỡi, t&aacute;o c&oacute; m&ugrave;i thơm nhẹ &ndash; ăn gi&ograve;n tan &ndash; ngọt đậm &ndash; hậu vị r&otilde; r&agrave;ng.</li>\r\n	<li>Được trồng trong nh&agrave; k&iacute;nh, đảm bảo từ kh&acirc;u gieo trồng đến khi thu hoạch v&agrave; xuất khẩu.</li>\r\n</ul>\r\n', 'Thiet-ke-chua-co-ten-7.png', '<h1><strong>Hồng t&aacute;o Jujubin</strong></h1>\r\n\r\n<h3>1. Giống v&agrave; chủng loại</h3>\r\n\r\n<ul>\r\n	<li>&nbsp;Xuất xứ: Đ&agrave;i Loan</li>\r\n</ul>\r\n\r\n<h3>2. Đặc điểm sản phẩm</h3>\r\n\r\n<ul>\r\n	<li>Tuy vẻ bề ngo&agrave;i kh&ocirc;ng bắt mắt nhưng Hồng t&aacute;o lại rất ngon, gi&ograve;n v&agrave; ngọt.</li>\r\n	<li>Kh&ocirc;ng phải ai cũng biết tới loại quả n&agrave;y v&agrave; những c&ocirc;ng dụng tuyệt vời của Hồng t&aacute;o nhưng ai đ&atilde; biết đến v&agrave; đ&atilde; thử một lần th&igrave; chắc chắn sẽ rất th&iacute;ch loại tr&aacute;i c&acirc;y n&agrave;y.</li>\r\n	<li>M&ugrave;a Hồng t&aacute;o chỉ diễn ra trong v&ograve;ng 1-2 th&aacute;ng trong năm v&igrave; vậy thời gian c&oacute; thể thưởng thức tr&aacute;i hồng t&aacute;o tươi l&agrave; giới hạn.</li>\r\n</ul>\r\n\r\n<h3>3. Gi&aacute; trị dinh dưỡng</h3>\r\n\r\n<ul>\r\n	<li><strong>Hồng t&aacute;o tươi</strong>&nbsp;rất gi&agrave;u dinh dưỡng, n&oacute; c&oacute; thể bổ sung chất sắt cho thai phụ, rất c&oacute; lợi cho dạ d&agrave;y, bổ sung kh&iacute; huyết, nhuận phổi.&nbsp;Thai nhi sẽ ph&aacute;t triển rất thuận lợi nếu c&aacute;c b&agrave; bầu kh&iacute; huyết đầy đủ.</li>\r\n	<li>Hồng t&aacute;o c&oacute; chứa lượng vitamin C v&agrave; vitamin E phong ph&uacute;, những loại vitamin n&agrave;y gi&uacute;p cho l&agrave;n da của phụ nữ mang bầu tươi s&aacute;ng v&agrave; đẹp hơn.</li>\r\n	<li>Ngo&agrave;i ra, n&oacute; c&ograve;n chống lại sự h&igrave;nh th&agrave;nh c&aacute;c vết n&aacute;m, vết th&acirc;m tr&ecirc;n khu&ocirc;n mặt</li>\r\n</ul>\r\n\r\n<h3>4. Bảo quản&nbsp;</h3>\r\n\r\n<ul>\r\n	<li>Chọn quả c&oacute; m&agrave;u xanh lục, pha m&agrave;u c&aacute;nh gi&aacute;n, vỏ trơn b&oacute;ng, kh&ocirc;ng bị mục vỏ. Quả như vậy l&agrave; tươi, vừa ch&iacute;n tới, độ gi&ograve;n cao nhất.</li>\r\n	<li>Bảo quản nơi tho&aacute;ng m&aacute;t: Cho t&aacute;o v&agrave;o hộp hoặc t&uacute;i k&iacute;n sau đ&oacute; bảo quản ở nơi tho&aacute;ng m&aacute;t v&agrave; tho&aacute;ng gi&oacute;, nhưng tốt nhất vẫn l&agrave; bảo quản ở ngăn m&aacute;t của tủ lạnh. V&igrave; kh&ocirc;ng c&oacute; chất bảo quản n&ecirc;n tốt hơn hết chỉ ăn t&aacute;o trong v&ograve;ng 2-3 ng&agrave;y sau khi mua về.</li>\r\n	<li><strong>Lưu &yacute;</strong>: hồng t&aacute;o tươi kh&ocirc;ng n&ecirc;n đem ng&acirc;m hoặc nấu, bởi v&igrave; h&agrave;m lượng vitamin C trong t&aacute;o rất cao, d&ugrave;ng nước n&oacute;ng ng&acirc;m sẽ l&agrave;m ph&aacute; vỡ hết vitamin C. Bởi vậy h&atilde;y ăn tươi, ăn trực tiếp l&agrave; tốt nhất. Nếu muốn nấu tr&agrave; hay l&agrave;m c&aacute;c m&oacute;n ăn từ hồng t&aacute;o n&ecirc;n chọn t&aacute;o kh&ocirc; l&agrave; tốt nhất.</li>\r\n</ul>\r\n', 20, 'Đã đăng', 1, '2023-11-22 13:51:27', 'vuhonglinh123', 0),
(26, 'VHL#23', 'Cam vàng Đài Loan', 99000, '<p>L&agrave; giống cam kh&ocirc;ng hạt, thịt quả v&agrave;ng tươi, t&eacute;p to, mọng nước, vị ngọt thanh m&aacute;t, rất thơm.&nbsp;Cam v&agrave;ng Đ&agrave;i Loan c&oacute; chứa nhiều Vitamin C, tốt cho da, chống l&atilde;o h&oacute;a, &nbsp;giảm Cholesterol, c&oacute; t&aacute;c dụng hồi phục sức khỏe nhanh, tăng cường chức &nbsp;năng tạo hồng huyết cầu, giảm căng thẳng thần kinh.</p>\r\n', '28201094_orange_premium_psd_72.png', '<h2><strong>CAM V&Agrave;NG&nbsp;</strong>Đ&Agrave;I LOAN</h2>\r\n\r\n<h3>1. Giống v&agrave; chủng loại</h3>\r\n\r\n<ul>\r\n	<li>Xuất xứ: Đ&agrave;i Loan</li>\r\n	<li>M&ugrave;a vụ: Cam v&agrave;ng Đ&agrave;i Loan thu hoạch từ th&aacute;ng 1 đến th&aacute;ng 5.</li>\r\n</ul>\r\n\r\n<h3>2. Đặc điểm sản phẩm</h3>\r\n\r\n<ul>\r\n	<li>L&agrave; giống cam kh&ocirc;ng hạt, thịt quả v&agrave;ng tươi, t&eacute;p to, mọng nước, vị ngọt thanh m&aacute;t, rất thơ</li>\r\n</ul>\r\n\r\n<h3>3. Gi&aacute; trị dinh dưỡng</h3>\r\n\r\n<ul>\r\n	<li>Cam v&agrave;ng Đ&agrave;i Loan c&oacute; chứa nhiều Vitamin C, tốt cho da, chống l&atilde;o h&oacute;a, &nbsp;giảm Cholesterol, c&oacute; t&aacute;c dụng hồi phục sức khỏe nhanh, tăng cường chức &nbsp;năng tạo hồng huyết cầu, giảm căng thẳng thần kinh.</li>\r\n	<li>&nbsp;Việc ti&ecirc;u thụ vitamin C ở liều cao (khoảng 500mg mỗi ng&agrave;y) rất tốt cho người ốm.</li>\r\n	<li>Quả cam mọng nước chứa một h&agrave;m lượng Vitamin C cao đến mức chỉ cần ăn &nbsp;một quả cũng đ&aacute;p ứng được 130% nhu cầu vitaminC h&agrave;ng ng&agrave;y của ch&uacute;ng ta. &nbsp;Ngo&agrave;i ra, cam c&ograve;n được biết tới như một loại đồ ăn ki&ecirc;ng gi&agrave;u chất xơ, &nbsp;Vitamin A, B, Canxi, Magnesium, Sắt, Đồng, Iốt&hellip;</li>\r\n	<li>&nbsp;Ăn cam thường xuy&ecirc;n &nbsp;sẽ gi&uacute;p ph&ograve;ng tr&aacute;nh c&aacute;c bệnh truyền nhiễm do virus; giảm đ&aacute;ng kể nguy &nbsp;cơ mắc bệnh sỏi thận. Để tr&aacute;nh lượng calo dư thừa, h&atilde;y bổ sung Cam v&agrave;o &nbsp;chế độ dinh dưỡng h&agrave;ng ng&agrave;y của m&igrave;nh</li>\r\n</ul>\r\n\r\n<h3>4. Lưu &yacute; bảo quản</h3>\r\n\r\n<ul>\r\n	<li>Cam c&oacute; thể được bảo quản ở nhiệt độ thường hoặc trong tủ lạnh. Nếu bảo quản trong tủ lạnh, nhiệt độ th&iacute;ch hợp nhất từ 7oC &ndash; 10oC. Đối với cả hai phương ph&aacute;p bảo quản, cam thường sẽ tươi được trong khoảng 2 tuần v&agrave; sẽ giữ được độ tươi c&ugrave;ng h&agrave;m lượng vitamin.</li>\r\n	<li>C&aacute;ch tốt nhất để bảo quản cam l&agrave; để tự nhi&ecirc;n thay v&igrave; cho v&agrave;o t&uacute;i nhựa v&igrave; nếu tiếp x&uacute;c với độ ẩm, cam c&oacute; thể dễ d&agrave;ng xuất hiện nấm mốc.</li>\r\n	<li>Cam v&agrave;ng Đ&agrave;i Loan vị ngọt thanh, &iacute;t xơ thường d&ugrave;ng để ăn tươi chứ kh&ocirc;ng n&ecirc;n &eacute;p nước.</li>\r\n</ul>\r\n', 20, 'Đã đăng', 1, '2023-11-22 14:19:41', 'vuhonglinh123', 0),
(27, 'VHL#26', 'Bưởi Vipome', 99000, '<p>Thưởng thức tr&aacute;i bưởi hồng da xanh Vipome, người ti&ecirc;u d&ugrave;ng sẽ cảm nhận được vị ngọt được lưu m&atilde;i độ đậm nơi cuống họng. Mỗi tr&aacute;i bưởi Vipome được trồng theo ti&ecirc;u chuẩn VietGap, chọn lựa từ những vườn bưởi l&atilde;o, gieo trồng từ 11 đến hơn 20 năm tuổi.</p>\r\n', 'FUJI-FRUIT-5.png', '<h1><strong>BƯỞI VIPOME</strong></h1>\r\n\r\n<h2><strong>1. Giống v&agrave; chủng loại</strong></h2>\r\n\r\n<ul>\r\n	<li dir=\"ltr\">Vipome đ&atilde; tuyển lựa v&agrave; ph&acirc;n loại theo ti&ecirc;u chuẩn khắt khe của VietGap (ti&ecirc;u chuẩn&nbsp;đ&aacute;nh gi&aacute; mức độ an to&agrave;n của thực phẩm)&nbsp;để cho ra đời những tr&aacute;i bưởi v&ocirc; c&ugrave;ng đặc biệt. Mỗi tr&aacute;i bưởi phải trải qua quy tr&igrave;nh trồng trọt bao gồm 12 đầu mục v&agrave; 65 ti&ecirc;u ch&iacute; đ&aacute;nh gi&aacute; kh&aacute;c nhau.</li>\r\n	<li dir=\"ltr\">Nhờ những c&ocirc;ng nghệ ti&ecirc;n tiến v&agrave; hiện đại bậc nhất cả nước, những tr&aacute;i bưởi đều chằn chặn ra đời, điều hiếm thấy ở tr&aacute;i c&acirc;y Việt n&oacute;i chung v&agrave; những tr&aacute;i bưởi Việt n&oacute;i ri&ecirc;ng.</li>\r\n</ul>\r\n\r\n<h2><strong>2. Đặc điểm sản phẩm</strong></h2>\r\n\r\n<ul>\r\n	<li dir=\"ltr\">Mỗi tr&aacute;i bưởi ruột hồng da xanh Vipome nặng trung b&igrave;nh từ 1,5 &ndash; 1,7kg. Hơn nữa, đ&acirc;y l&agrave; d&ograve;ng bưởi kh&ocirc;ng hạt, r&aacute;o m&uacute;i, gi&ograve;n t&eacute;p nhưng vẫn giữ được độ căng mọng v&agrave; nhiều nước. Khi ch&iacute;n, vỏ tr&aacute;i giữ được m&agrave;u xanh, dễ lột v&agrave; kh&aacute; mỏng.</li>\r\n	<li dir=\"ltr\">So với c&aacute;c d&ograve;ng kh&aacute;c, bưởi Vipome vẫn giữ được hương vị đặc trưng ri&ecirc;ng của ch&uacute;ng. Nhờ hương vị ngọt thanh c&ugrave;ng m&uacute;i t&eacute;p căng mọng, bưởi Vipome ruột hồng, da xanh ch&iacute;nh l&agrave; xứng đ&aacute;ng l&agrave; lựa chọn h&agrave;ng đầu của người ti&ecirc;u d&ugrave;ng.</li>\r\n</ul>\r\n\r\n<p><img alt=\"\" height=\"230\" src=\"https://www.fujifruit.com.vn/wp-content/uploads/2022/12/C9824DB5-3170-4E56-B43D-A1599C178BAE_1_201_a-300x230.jpeg\" width=\"300\" /></p>\r\n\r\n<h2><strong>3. Gi&aacute; trị dinh dưỡng</strong></h2>\r\n\r\n<ul>\r\n	<li dir=\"ltr\">Quả bưởi da xanh Vipome kh&ocirc;ng chỉ ngon, hấp dẫn m&agrave; c&ograve;n mang lại rất nhiều gi&aacute; trị dinh dưỡng, l&agrave; phương thuốc qu&yacute; để ph&ograve;ng v&agrave; trị bệnh. Trong đ&oacute; c&oacute; chứa rất nhiều loại kho&aacute;ng chất, vitamin v&agrave; một số loại hoạt chất c&oacute; lợi kh&aacute;c.</li>\r\n	<li dir=\"ltr\">Chẳng hạn, trong 100g bưởi da xanh Vipome c&oacute; 59 đơn vị calo, tới 30mg Canxi, 21mg Phốt pho, 0,7mg sắt c&ugrave;ng c&aacute;c loại vitamin. Nhờ đ&oacute;, ch&uacute;ng cực kỳ tốt cho sức khỏe.</li>\r\n</ul>\r\n\r\n<h2><strong>4. Lưu &yacute; bảo quản</strong></h2>\r\n\r\n<ul>\r\n	<li>\r\n	<p dir=\"ltr\">Bởi chất lượng v&agrave; hương vị tuyệt vời của bưởi da xanh Vipome, mỗi gia đ&igrave;nh thường mua cho m&igrave;nh khoảng 5 &ndash; 10 quả. Tuy nhi&ecirc;n khi để l&acirc;u, bưởi dễ bị kh&ocirc; đầu v&agrave; thối hỏng.</p>\r\n	</li>\r\n	<li>\r\n	<p dir=\"ltr\">Dưới đ&acirc;y l&agrave; những c&aacute;ch bảo quản bưởi trong khoảng thời gian 15 &ndash; 20 ng&agrave;y cực k&igrave; đơn giản:</p>\r\n\r\n	<ul>\r\n		<li dir=\"ltr\">\r\n		<p dir=\"ltr\">Đặt ở nơi kh&ocirc; r&aacute;o, tr&aacute;nh &aacute;nh nắng mặt trời</p>\r\n		</li>\r\n		<li dir=\"ltr\">\r\n		<p dir=\"ltr\">Xếp l&ecirc;n kệ hoặc đặt v&agrave;o gi&aacute; rổ</p>\r\n		</li>\r\n		<li dir=\"ltr\">\r\n		<p dir=\"ltr\">L&oacute;t b&igrave;a carton v&agrave; xếp bưởi l&ecirc;n tr&ecirc;n</p>\r\n		</li>\r\n	</ul>\r\n	</li>\r\n</ul>\r\n', 20, 'Đã đăng', 1, '2023-11-13 05:22:06', 'vuhonglinh123', 0),
(31, 'VHL#25', 'Cam canh loại 1', 99000, '<p>&ndash;&nbsp;Tr&aacute;i cam nhỏ, gi&agrave; căng b&oacute;ng</p>\r\n\r\n<p>&ndash;&nbsp;Vỏ mỏng, thơm nhẹ</p>\r\n\r\n<p>&ndash;&nbsp;Hương vị ngọt thanh m&aacute;t, đậm đ&agrave;</p>\r\n\r\n<p>&ndash;&nbsp;Đặc biệt, tiết trời c&agrave;ng lạnh, cam c&agrave;ng ngọt!</p>\r\n', 'Blue-Gradient-Modern-Bussiness-Facebook-Cover-1066-×-1600-px-14.png', '<h1><strong>CAM CANH LOẠI 1</strong></h1>\r\n\r\n<h2>1. Giống v&agrave; chủng loại</h2>\r\n\r\n<p>Cam canh loại 1 l&agrave; giống được canh t&aacute;c bằng phương ph&aacute;p hữu cơ đạt chuẩn đầu ti&ecirc;n ở Việt Nam, đầy đủ chứng nhận VIETGAP.</p>\r\n\r\n<h2>2. Đặc điểm sản phẩm</h2>\r\n\r\n<p>&ndash;&nbsp;Tr&aacute;i cam nhỏ, gi&agrave; căng b&oacute;ng&nbsp;&ndash;&nbsp;Vỏ mỏng, thơm nhẹ&nbsp;&ndash;&nbsp;Hương vị ngọt thanh m&aacute;t, đậm đ&agrave;&nbsp;&ndash;&nbsp;Đặc biệt, tiết trời c&agrave;ng lạnh, cam c&agrave;ng ngọt!&nbsp;<img src=\"https://hoaquafuji.com/storage/app/media/cam-canh-huu-co-2T.jpg\" /></p>\r\n\r\n<ul>\r\n	<li>PHƯƠNG PH&Aacute;P CANH T&Aacute;C!<br data-text=\"true\" />\r\n	&ndash; Cam canh loại 1 được canh t&aacute;c theo phương thức hệ sinh th&aacute;i&nbsp;.&nbsp;Nghĩa l&agrave;: Để cỏ tự nhi&ecirc;n giữ ẩm,l&agrave;m m&ocirc;i trường sống cho vi sinh vật c&oacute; lợi ph&aacute;t triển v&agrave; c&acirc;n bằng hệ sinh th&aacute;i.&nbsp;<br data-text=\"true\" />\r\n	&ndash; Ph&acirc;n b&oacute;n hữu cơ: Dịch tr&ugrave;n quế, rơm mục. Ng&acirc;m ủ đậu tương, ng&ocirc;, chuối bổ sung dinh dưỡng cho c&acirc;y trồng. KH&Ocirc;NG sử dụng thuốc k&iacute;ch th&iacute;ch tăng trưởng!<br data-text=\"true\" />\r\n	&ndash;&nbsp;Sử dụng thi&ecirc;n địch v&agrave; vi sinh để &nbsp;ph&ograve;ng ngừa v&agrave; loại trừ s&acirc;u bệnh. Do đ&oacute;,Cam canh loại 1 kh&ocirc;ng cần đến thuộc bảo vệ thực vật h&oacute;a học.<br data-text=\"true\" />\r\n	&ndash;&nbsp;Với những nỗ lực kh&ocirc;ng ngừng trong việc cải tiến phương thức canh t&aacute;c, Cam canh loại 1 tự h&agrave;o đạt ti&ecirc;u chuẩn VIETGAP v&agrave; chuẩn hữu cơ đầu ti&ecirc;n tại Việt Nam!</li>\r\n</ul>\r\n\r\n<ul>\r\n	<li>Qu&yacute; kh&aacute;ch mua h&agrave;ng c&oacute; thể truy xuất nguồn gốc h&agrave;ng h&oacute;a qua tem d&aacute;n!</li>\r\n</ul>\r\n\r\n<h2>3. Gi&aacute; trị dinh dưỡng</h2>\r\n\r\n<p>Mỗi 100 gr quả cam c&oacute; chứa 87,6 g nước, 1.104 microgram Carotene &ndash; một &nbsp;loại vitamin chống oxy h&oacute;a, 30 mg vitamin C, 10,9 g chất tinh bột, 93 mg &nbsp;kali, 26 mg canxi, 9 mg Magnesium , 0,3 g chất xơ, 4,5 mg natri, 7 mg &nbsp;Chromium, 20 mg phốt pho, 0,32 mg sắt v&agrave; gi&aacute; trị năng lượng l&agrave; 48 Kcal.&nbsp;<strong>Trị vi&ecirc;m khớp:</strong>&nbsp;Chất Carotene &ndash; tiền sinh tố của vitamin A c&oacute; t&aacute;c dụng l&agrave;m giảm cơn đau của vi&ecirc;m khớp v&agrave; bệnh Gout.&nbsp;<strong>Trị l&atilde;o ho&aacute; da:</strong>&nbsp;Nước &eacute;p tr&aacute;i cam c&oacute; thể sử dụng như một loại nước tẩy trang, l&agrave;m sạch &nbsp;da. Vỏ quả cam được d&ugrave;ng tẩy da chết, tinh dầu trong vỏ cam sẽ l&agrave;m da &nbsp;th&ecirc;m mềm mại, lấy lại độ căng b&oacute;ng do da hấp thụ được lượng tinh dầu &nbsp;trong vỏ cam, từ đ&oacute; n&acirc;ng cao sức đề kh&aacute;ng cho l&agrave;n da.&nbsp;<strong>Trị cảm lạnh:</strong>&nbsp;Khi bạn đang bị cảm lạnh, h&atilde;y uống một cốc nước cam n&oacute;ng sẽ gi&uacute;p cơ thể giảm mệt mỏi, ngăn ngừa t&igrave;nh trạng sổ mũi.&nbsp;<strong>Trị sốt, c&uacute;m:</strong>&nbsp;Nước &eacute;p của quả cam tốt cho bất kỳ bệnh sốt n&agrave;o như c&uacute;m, thương h&agrave;n v&agrave;ng da&hellip; vv&nbsp;<strong>Trị vi&ecirc;m phế quản v&agrave; hen suyễn:</strong>&nbsp;Mật ong, muối v&agrave; nước n&oacute;ng pha với nước cam rất c&oacute; lợi đối với bệnh nh&acirc;n vi&ecirc;m phế quản v&agrave; hen suyễn.&nbsp;<strong>Trị chứng t&aacute;o b&oacute;n:</strong>&nbsp;Ăn hoặc uống nước cam v&agrave;o bữa s&aacute;ng li&ecirc;n tục trong v&agrave;i ng&agrave;y sẽ gi&uacute;p &nbsp;bạn khắc phục chứng t&aacute;o b&oacute;n rất hiệu quả. Một cốc nước cam sau mỗi bữa &nbsp;s&aacute;ng cũng sẽ mang lại cho bạn tinh thần phấn chấn để bắt đầu một ng&agrave;y &nbsp;mới. Buổi s&aacute;ng cũng l&agrave; thời điểm cơ thể hấp thu tốt nhất c&aacute;c canxi v&agrave; &nbsp;c&aacute;c vitamin trong quả cam. Kh&ocirc;ng n&ecirc;n uống nước cam v&agrave;o buổi tối v&igrave; sẽ &nbsp;g&acirc;y ra chứng kh&oacute; ngủ.&nbsp;<strong>Tốt cho người mắc tim mạch v&agrave; huyết &aacute;p cao:</strong>&nbsp;Nếu bạn kh&ocirc;ng phải l&agrave; bệnh nh&acirc;n tiểu đường, bạn c&oacute; thể uống nước cam &nbsp;với mật ong, số lượng kali cao trong nước cam c&oacute; t&aacute;c dụng l&agrave;m giảm huyết &nbsp;&aacute;p cao.</p>\r\n\r\n<h2>4. Lưu &yacute; bảo quản:</h2>\r\n\r\n<p>Cam c&oacute; thể được bảo quản ở nhiệt độ thường hoặc trong tủ lạnh. Nếu &nbsp;bảo &nbsp;quản trong tủ lạnh, nhiệt độ th&iacute;ch hợp nhất từ 7 &ndash; 10 độ C. Đối với &nbsp;cả &nbsp;hai phương ph&aacute;p bảo quản, cam thường sẽ tươi được trong khoảng 2 &nbsp;tuần v&agrave; &nbsp;sẽ giữ được độ tươi c&ugrave;ng h&agrave;m lượng vitamin.&nbsp;</p>\r\n', 20, 'Đã đăng', 1, '2023-11-13 05:22:06', 'vuhonglinh123', 0);
INSERT INTO `tb_products` (`product_id`, `product_code`, `product_name`, `price`, `product_desc`, `product_thumb`, `product_content`, `quantity`, `status`, `cat_id`, `created_date`, `creator`, `sales`) VALUES
(33, 'VHL#27', 'Bưởi da xanh', 129000, '<ul>\r\n	<li>Tr&aacute;i bưởi da xanh c&oacute; dạng h&igrave;nh cầu, nặng trung b&igrave;nh từ 1,2 &ndash; &nbsp;2,5 &nbsp;kg/tr&aacute;i; vỏ c&oacute; m&agrave;u xanh đến xanh hơi v&agrave;ng khi ch&iacute;n, dễ lột v&agrave; kh&aacute; &nbsp;mỏng &nbsp;(14&ndash;18 mm); t&eacute;p bưởi m&agrave;u hồng đỏ, b&oacute; chặt v&agrave; dễ t&aacute;ch khỏi v&aacute;ch m&uacute;i; &nbsp;vị &nbsp;ngọt kh&ocirc;ng chua.</li>\r\n	<li>Set 3 quả bưởi th&iacute;ch hợp đi biếu lễ, Tết.</li>\r\n</ul>\r\n', 'FUJI-FRUIT-1.png', '<h1><strong>BƯỞI DA XANH PH&Uacute; QU&Yacute;</strong></h1>\r\n\r\n<h2><strong>1. Giống v&agrave; chủng loại</strong></h2>\r\n\r\n<p>Bưởi da xanh l&agrave; một giống bưởi c&oacute; nguồn gốc đầu ti&ecirc;n ở x&atilde; Thanh T&acirc;n, huyện Mỏ C&agrave;y, tỉnh Bến Tre</p>\r\n\r\n<h2><strong>2. Đặc điểm sản phẩm</strong></h2>\r\n\r\n<p>Tr&aacute;i bưởi da xanh c&oacute; dạng h&igrave;nh cầu, nặng trung b&igrave;nh từ 1,2 &ndash; &nbsp;2,5 &nbsp;kg/tr&aacute;i; vỏ c&oacute; m&agrave;u xanh đến xanh hơi v&agrave;ng khi ch&iacute;n, dễ lột v&agrave; kh&aacute; &nbsp;mỏng &nbsp;(14&ndash;18 mm); t&eacute;p bưởi m&agrave;u hồng đỏ, b&oacute; chặt v&agrave; dễ t&aacute;ch khỏi v&aacute;ch m&uacute;i; &nbsp;vị &nbsp;ngọt kh&ocirc;ng chua.&nbsp;<img alt=\"\" height=\"300\" src=\"https://www.fujifruit.com.vn/wp-content/uploads/2022/12/FUJI-FRUIT-2-300x300.png\" width=\"300\" /></p>\r\n\r\n<h2><strong>3. Gi&aacute; trị dinh dưỡng</strong></h2>\r\n\r\n<ul>\r\n	<li>Tăng cường hệ miễn dịch: bưởi cung cấp khoảng 600% nhu cầu vitamin C &nbsp;h&agrave;ng ng&agrave;y của cơ thể. Lượng vitamin C n&agrave;y c&oacute; thể gi&uacute;p bạn tr&aacute;nh được cảm &nbsp;lạnh, sốt, nhiễm tr&ugrave;ng v&agrave; c&aacute;c chứng bệnh kh&aacute;c.</li>\r\n	<li>Điều h&ograve;a huyết &aacute;p: Bưởi c&oacute; chứa một lượng kali cao gi&uacute;p thư gi&atilde;n c&aacute;c mạch m&aacute;u v&agrave; th&uacute;c đẩy sự lưu th&ocirc;ng m&aacute;u dễ d&agrave;ng.</li>\r\n	<li>Củng cố xương cốt: Bởi trong bưởi rất gi&agrave;u Kali gi&uacute;p xương chắc khỏe.</li>\r\n	<li>Giảm c&acirc;n, giữ g&igrave;n v&oacute;c d&aacute;ng: enzyme &ndash; carnitine palmitoyltransferase c&oacute; &nbsp;trong bưởi gi&uacute;p qu&aacute; tr&igrave;nh giảm c&acirc;n nhanh v&agrave; cực kỳ an to&agrave;n.</li>\r\n</ul>\r\n\r\n<h2><strong>4. Lưu &yacute; bảo quản</strong></h2>\r\n\r\n<ul>\r\n	<li>D&ugrave;ng dao hay k&eacute;o sắc cắt cuống quả d&agrave;i 0,5cm, lấy v&ocirc;i t&ocirc;i chấm v&agrave;o vết cắt c&oacute; t&aacute;c dụng khử tr&ugrave;ng, chống thối.</li>\r\n	<li>C&aacute;ch bảo quản bưởi &nbsp;da xanh l&agrave;m đồ thờ, tế lễ: V&igrave; để c&uacute;ng n&ecirc;n quả bưởi phải giữ được đẹp &nbsp;mắt, bảo quản lượng quả &iacute;t d&ugrave;ng th&ugrave;ng carton hay th&ugrave;ng phuy lớn, cho một &nbsp;lớp c&aacute;t kh&ocirc;, nhỏ d&agrave;y 10-15cm, xếp 1 lớp quả bưởi l&ecirc;n tr&ecirc;n, cứ một lớp &nbsp;c&aacute;t d&agrave;y 5-7cm lại xếp một lớp bưởi da xanh cho đến khi đầy th&ugrave;ng, lớp &nbsp;c&aacute;t tr&ecirc;n c&ugrave;ng d&agrave;y 20cm. C&aacute;ch bảo quản bưởi da xanh số lượng lớn l&agrave; n&ecirc;n &nbsp;k&egrave; gạch ở một g&oacute;c nh&agrave; hay gian nh&agrave; nơi kh&ocirc; r&aacute;o, sau đ&oacute; cho 1 lớp c&aacute;t rồi &nbsp;mới lại xếp 1 lớp bưởi l&ecirc;n, l&agrave;m như c&aacute;c bước tr&ecirc;n. Với c&aacute;ch bảo quản &nbsp;bưởi da xanh n&agrave;y, quả bưởi c&oacute; thể giữ tươi l&acirc;u được l&ecirc;n tới 2 th&aacute;ng</li>\r\n</ul>\r\n', 20, 'Đã đăng', 1, '2023-11-13 05:22:06', 'vuhonglinh123', 0),
(35, 'VHL#24', 'Bơ Cuba', 69000, '<p>BƠ CUBA THƠM, B&Eacute;O NG&Acirc;Y: &ndash; Tr&aacute;i to, cơm v&agrave;ng &oacute;ng, dẻo ngậy, ngọt vừa, ăn ho&agrave;i kh&ocirc;ng ng&aacute;n &ndash; Được trồng v&agrave; canh t&aacute;c theo phương ph&aacute;p hữu cơ, an to&agrave;n tuyệt đối với mẹ bầu v&agrave; trẻ nhỏ</p>\r\n', '6-1.png', '<h3>BƠ CUBA THƠM, B&Eacute;O NG&Acirc;Y: &ndash; Tr&aacute;i to, cơm v&agrave;ng &oacute;ng, dẻo ngậy, ngọt vừa, ăn ho&agrave;i kh&ocirc;ng ng&aacute;n &ndash; Được trồng v&agrave; canh t&aacute;c theo phương ph&aacute;p hữu cơ, an to&agrave;n tuyệt đối với mẹ bầu v&agrave; trẻ nhỏ</h3>\r\n', 20, 'Đã đăng', 1, '2023-11-22 13:52:38', 'vuhonglinh123', 0),
(36, 'VHL#100', 'Dưa Leo VietGAP', 18000, '<p><em><strong>Dưa leo</strong></em>&nbsp;hay c&ograve;n gọi l&agrave; dưa chuột, l&agrave; loại quả được trồng phổ biến ở nhiều nước. Dưa leo thuộc họ bầu b&iacute;,&nbsp;c&oacute; nguồn gốc ở<em>&nbsp;Ấn Độ, giữa vịnh Bengal v&agrave; d&atilde;y Hymalayas</em>&nbsp;c&aacute;ch nay hơn 3000 năm v&agrave; giống c&acirc;y n&agrave;y được mang đi dọc theo ph&iacute;a&nbsp;<em>T&acirc;y ch&acirc;u &Aacute;, ch&acirc;u Phi v&agrave; miền Nam ch&acirc;u &Acirc;u.&nbsp;</em></p>\r\n\r\n<p>Hiện nay dưa leo trở th&agrave;nh loại quả thương mại n&ecirc;n c&oacute; diện t&iacute;ch trồng lớn ở 1 số nước&nbsp;<em>Trung Quốc, Nhật Bản, Mỹ, H&agrave; Lan,...&nbsp;</em>Ở Việt Nam, dưa leo được trồng theo hướng VietGap tại c&aacute;c n&ocirc;ng trại rau sạch hoặc hữu cơ tại một&nbsp;số hộ gia đ&igrave;nh.</p>\r\n', 'c30db426-58ef-4de1-9685-3572b15de94d.jpeg', '<p><strong>I. Nguồn gốc, xuất xứ:</strong></p>\r\n\r\n<p><em><strong>Dưa leo</strong></em>&nbsp;hay c&ograve;n gọi l&agrave; dưa chuột, l&agrave; loại quả được trồng phổ biến ở nhiều nước. Dưa leo thuộc họ bầu b&iacute;,&nbsp;c&oacute; nguồn gốc ở<em>&nbsp;Ấn Độ, giữa vịnh Bengal v&agrave; d&atilde;y Hymalayas</em>&nbsp;c&aacute;ch nay hơn 3000 năm v&agrave; giống c&acirc;y n&agrave;y được mang đi dọc theo ph&iacute;a&nbsp;<em>T&acirc;y ch&acirc;u &Aacute;, ch&acirc;u Phi v&agrave; miền Nam ch&acirc;u &Acirc;u.&nbsp;</em></p>\r\n\r\n<p>Hiện nay dưa leo trở th&agrave;nh loại quả thương mại n&ecirc;n c&oacute; diện t&iacute;ch trồng lớn ở 1 số nước&nbsp;<em>Trung Quốc, Nhật Bản, Mỹ, H&agrave; Lan,...&nbsp;</em>Ở Việt Nam, dưa leo được trồng theo hướng VietGap tại c&aacute;c n&ocirc;ng trại rau sạch hoặc hữu cơ tại một&nbsp;số hộ gia đ&igrave;nh.</p>\r\n\r\n<p><strong>II. Đặc điểm, h&igrave;nh dạng, hương vị:</strong></p>\r\n\r\n<p>Dưa leo c&oacute; k&iacute;ch thước t&ugrave;y theo điều kiện trồng,&nbsp;<em>c&aacute;c quả thon d&agrave;i với k&iacute;ch thước khoảng 10-12cm, m&agrave;u xanh nhạt c&oacute; pha lẫn v&agrave;ng nhạt khi ch&iacute;n</em>. Tr&ecirc;n vỏ dưa c&oacute; c&aacute;c sọc hay chấm.</p>\r\n\r\n<p>Thịt quả m&agrave;u xanh nhạt, mỏng, mọng nước, ăn c&oacute; vị ngọt v&agrave; gi&ograve;n, m&aacute;t rất dễ chịu. Hạt b&ecirc;n trong nhỏ m&agrave;u trắng s&aacute;ng.</p>\r\n\r\n<p><strong>III. C&ocirc;ng dụng:</strong></p>\r\n\r\n<p><em><strong>Dưa leo</strong></em>&nbsp;cũng mang đến lợi &iacute;ch kh&ocirc;ng k&eacute;m c&aacute;c loại rau quả&nbsp;kh&aacute;c: gi&uacute;p cơ thể b&ugrave; nước, cung cấp độ ẩm cho l&agrave;n da, hỗ trợ tim mạch, ngăn ngừa ung thư, bổ sung kali c&ugrave;ng 1 số vitamin, l&agrave;m sạch miệng, tốt cho ti&ecirc;u h&oacute;a, giảm cholesterol,...</p>\r\n\r\n<p><strong>IV. C&aacute;c m&oacute;n ăn:</strong></p>\r\n\r\n<p>Dưa leo được biết đến như nguy&ecirc;n liệu ăn k&egrave;m trong c&aacute;c bữa cơm hằng ng&agrave;y, d&ugrave;ng để trộn salad hay thực hiện c&aacute;c m&oacute;n x&agrave;o: b&ograve; x&agrave;o dưa leo, mực x&agrave;o dưa leo...</p>\r\n\r\n<p>Ngo&agrave;i ra, dưa leo d&ugrave;ng l&agrave;m gỏi, nộm, muối chua l&agrave;m kim chi hay dưa muối, l&agrave;m rau ăn k&egrave;m trong c&aacute;c m&oacute;n cuốn</p>\r\n\r\n<p><strong>V. Sơ chế v&agrave; bảo quản:</strong></p>\r\n\r\n<p>Ng&acirc;m&nbsp;<strong><em>Dưa leo&nbsp;</em></strong>với nước muối rồi gọt bỏ vỏ</p>\r\n\r\n<p>T&ugrave;y theo c&aacute;ch sử dụng v&agrave; chế biến m&agrave; cắt cho ph&ugrave; hợp</p>\r\n\r\n<p>Nếu dưa ng&acirc;m muối th&igrave; kh&ocirc;ng n&ecirc;n gọt vỏ</p>\r\n\r\n<p>Bảo quản dưa leo ở ngăn m&aacute;t tủ lạnh trong 5-7 ng&agrave;y n&ecirc;n bọc dưa lại v&igrave; nhiệt độ lạnh&nbsp;l&agrave;m dưa bị nhăn v&agrave; h&eacute;o ở 2 đầu.&nbsp;</p>\r\n', 20, 'Đã đăng', 2, '2023-11-22 14:23:05', 'vuhonglinh123', 0),
(37, 'VHL#101', 'Đậu Que Đà Lạt', 18000, '<p>Với sản phẩm tươi sống, trọng lượng thực tế c&oacute; thể ch&ecirc;nh lệch khoảng 10%.</p>\r\n\r\n<p>Đậu que Đ&agrave; Lạt khay</p>\r\n\r\n<p>Gi&aacute; sản phẩm tr&ecirc;n Tiki đ&atilde; bao gồm thuế theo luật hiện h&agrave;nh. B&ecirc;n cạnh đ&oacute;, tuỳ v&agrave;o loại sản phẩm, h&igrave;nh thức v&agrave; địa chỉ giao h&agrave;ng m&agrave; c&oacute; thể ph&aacute;t sinh th&ecirc;m chi ph&iacute; kh&aacute;c như ph&iacute; vận chuyển, phụ ph&iacute; h&agrave;ng cồng kềnh, thuế nhập khẩu (đối với đơn h&agrave;ng giao từ nước ngo&agrave;i c&oacute; gi&aacute; trị tr&ecirc;n 1 triệu đồng).....</p>\r\n', '63d0ad8a-53f2-45af-a055-d17c00ff7cb4.jpeg', '<p>Với sản phẩm tươi sống, trọng lượng thực tế c&oacute; thể ch&ecirc;nh lệch khoảng 10%.</p>\r\n\r\n<p>Đậu que Đ&agrave; Lạt khay</p>\r\n\r\n<p>Gi&aacute; sản phẩm tr&ecirc;n Tiki đ&atilde; bao gồm thuế theo luật hiện h&agrave;nh. B&ecirc;n cạnh đ&oacute;, tuỳ v&agrave;o loại sản phẩm, h&igrave;nh thức v&agrave; địa chỉ giao h&agrave;ng m&agrave; c&oacute; thể ph&aacute;t sinh th&ecirc;m chi ph&iacute; kh&aacute;c như ph&iacute; vận chuyển, phụ ph&iacute; h&agrave;ng cồng kềnh, thuế nhập khẩu (đối với đơn h&agrave;ng giao từ nước ngo&agrave;i c&oacute; gi&aacute; trị tr&ecirc;n 1 triệu đồng).....</p>\r\n', 20, 'Đã đăng', 2, '2023-11-22 14:30:04', 'vuhonglinh123', 0),
(38, 'VHL#102', 'Khoai Tây Đà Lạt', 22000, '<p>Khoai t&acirc;y đ&ocirc;ng lạnh l&agrave; sản phẩm rau củ đ&ocirc;ng tiện lợi, c&oacute; thể nhanh ch&oacute;ng chế biến th&agrave;nh m&oacute;n khoai t&acirc;y chi&ecirc;n... giữ nguy&ecirc;n hương vị tự nhi&ecirc;n từ khoai t&acirc;y tươi,</p>\r\n', '2ac50934-3c64-4cd1-9d85-4b597e3888db.jpeg', '<h2><strong>I. Nguồn gốc, xuất xứ của khoai t&acirc;y</strong></h2>\r\n\r\n<p><em><strong>Khoai t&acirc;y</strong></em>&nbsp;c&oacute; nguồn gốc ở&nbsp;<em>Nam Mỹ&nbsp;</em>v&agrave; được đưa đến ch&acirc;u &Acirc;u trong thế kỷ 16 sau đ&oacute; đ&atilde; được trồng rộng r&atilde;i tr&ecirc;n thế giới. Ở Việt Nam, khoai t&acirc;y được trồng tại Đ&agrave; Lạt theo phương ph&aacute;p hữu cơ, kh&ocirc;ng h&agrave;m lượng kim loại nặng, kh&ocirc;ng nhiễm vi sinh, c&aacute;c chất h&oacute;a học, c&aacute;c loại thuốc trừ s&acirc;u, tăng trưởng,...</p>\r\n\r\n<h2><strong>II. Đặc điểm, h&igrave;nh d&aacute;ng, hương vị</strong></h2>\r\n\r\n<p>Khoai t&acirc;y v&agrave;ng c&oacute; h&igrave;nh dạng củ &ocirc; van, lớp vỏ v&agrave; ruột đều c&oacute; m&agrave;u v&agrave;ng. Khoai t&acirc;y v&agrave;ng&nbsp; chứa nhiều chất dinh dưỡng v&agrave; c&oacute; vị ngon đặc trưng khi chế biến. Khi ăn khoai t&acirc;y c&oacute; vị ngọt, b&eacute;o, nhiều bột, mềm</p>\r\n\r\n<h2><strong>III. C&ocirc;ng dụng của khoai t&acirc;y</strong></h2>\r\n\r\n<p>Khoai t&acirc;y y gi&agrave;u vitamin v&agrave; kho&aacute;ng chất, đặc biệt l&agrave;&nbsp;<em>vitamin C v&agrave; kali</em>. Lượng vitamin C dồi d&agrave;o gi&uacute;p tăng cường hệ miễn dịch, th&uacute;c đẩy qu&aacute; tr&igrave;nh t&aacute;i tạo tế b&agrave;o v&agrave; tăng khả năng hấp thụ chất sắt. Trong khi đ&oacute;, Kali tốt cho huyết &aacute;p v&agrave; giảm nguy cơ đột quỵ. Ngo&agrave;i ra, khoai t&acirc;y c&ograve;n hỗ trợ đẩy nhanh qu&aacute; tr&igrave;nh ti&ecirc;u h&oacute;a, l&agrave;m đẹp da, tốt cho tim mạch,...</p>\r\n\r\n<h2><strong>IV. C&aacute;c m&oacute;n ăn từ khoai t&acirc;y</strong></h2>\r\n\r\n<p>Khoai t&acirc;y vừa l&agrave; nguy&ecirc;n liệu tạo ra m&oacute;n ăn hằng ng&agrave;y vừa tạo n&ecirc;n c&aacute;c m&oacute;n chip, snack tuyệt vời m&agrave; ai cũng th&iacute;ch:</p>\r\n\r\n<p>Khoai t&acirc;y chi&ecirc;n ph&ocirc; mai, khoai t&acirc;y chi&ecirc;n</p>\r\n\r\n<p>B&aacute;nh khoai t&acirc;y chi&ecirc;n x&ugrave;, b&aacute;nh khoai t&acirc;y c&agrave; rốt</p>\r\n\r\n<p>Khoai t&acirc;y x&agrave;o thịt b&ograve;, chả c&aacute; x&agrave;o khoai t&acirc;y</p>\r\n\r\n<p>Sườn heo &ocirc;m khoai t&acirc;y, khoai t&acirc;y hầm xương vơi c&agrave; rốt củ dền</p>\r\n\r\n<h2><strong>V. Sơ chế v&agrave; bảo quản</strong></h2>\r\n\r\n<p><em><strong>Khoai t&acirc;y</strong></em>&nbsp;gọt sạch vỏ, kho&eacute;t bỏ mắt rồi đem rửa sạch.&nbsp;</p>\r\n\r\n<p>Cắt&nbsp;khoai t&acirc;y&nbsp;th&agrave;nh những miếng d&agrave;i hay khối vu&ocirc;ng<strong>&nbsp;</strong>hoặc cắt khoanh t&ugrave;y &yacute;.&nbsp;</p>\r\n\r\n<p>Ng&acirc;m&nbsp;khoai&nbsp;v&agrave;o nước muối pha lo&atilde;ng trong khoảng 15 ph&uacute;t cho&nbsp;khoai&nbsp;ra hết nhựa v&agrave; kh&ocirc;ng c&ograve;n bị th&acirc;m đen. Vớt&nbsp;khoai&nbsp;ra rổ để r&aacute;o nước v&agrave; chế biến</p>\r\n\r\n<p>Giữ&nbsp;khoai t&acirc;y&nbsp;trong nhiệt độ m&aacute;t. Nhiệt độ tốt nhất để&nbsp;bảo quản khoai t&acirc;y&nbsp;l&agrave; dưới 10 độ C (từ 6-10 độ C). Ở nhiệt độ n&agrave;y,&nbsp;khoai t&acirc;y&nbsp;c&oacute; thể tươi trong v&agrave;i th&aacute;ng.&nbsp; Kỹ hơn cho khoai v&agrave;o t&uacute;i lưới v&agrave; để nơi tho&aacute;ng m&aacute;t.</p>\r\n\r\n<p>&nbsp;Lưu &yacute; rằng tủ lạnh thường qu&aacute; lạnh để lưu trữ<em>&nbsp;<strong>khoai t&acirc;y</strong></em>&nbsp;v&agrave; c&oacute; thể l&agrave;m hỏng hương vị v&agrave; m&agrave;u sắc của ch&uacute;ng</p>\r\n', 20, 'Đã đăng', 2, '2023-11-13 05:22:06', 'vuhonglinh123', 0),
(39, 'VHL#103', 'Su Hào Trắng', 40000, '<p>- Thực phẩm sạch tốt cho sức khỏe</p>\r\n\r\n<p>- C&oacute; thể chế biến th&agrave;nh nhiều m&oacute;n ăn</p>\r\n\r\n<p>- Sản phẩm đạt chất lượng VietGap</p>\r\n\r\n<p>- Li&ecirc;n hệ trực tiếp để được gi&aacute; rau sỉ tại vựa Đ&agrave; Lạt tốt nhất.</p>\r\n', 'su_hao_trang_raucusachdl_1631958311.jpeg', '<p>Để tiện cho Qu&yacute; kh&aacute;ch h&agrave;ng tham khảo&nbsp;<strong><a href=\"https://raucusachdl.com/rau-cu-sach-da-lat\" title=\"các loại rau củ Đà Lạt\">c&aacute;c loại rau củ Đ&agrave; Lạt</a></strong>&nbsp;kh&aacute;c, ch&uacute;ng t&ocirc;i xin gửi đến Qu&yacute; kh&aacute;ch h&agrave;ng những sản phẩm rau sạch Đ&agrave; Lạt m&agrave; DL Fresh hiện đang c&oacute; v&agrave; cung cấp cho thị trường</p>\r\n\r\n<p><strong>Đơn vị t&iacute;nh :&nbsp;</strong>Kg |&nbsp;<em>Bảng gi&aacute; của vựa rau sạch Đ&agrave; Lạt -&nbsp;<strong><a href=\"https://raucusachdl.com/\" title=\"DL Fresh\">DL Fresh</a></strong>&nbsp;mang t&iacute;nh chất tham khảo sản phẩm (Gi&aacute; thay đổi theo thời vụ n&ecirc;n hệ thống đang cập nhật), c&ograve;n đối với Qu&yacute; kh&aacute;ch h&agrave;ng cần cung cấp số lớn xin vui l&ograve;ng li&ecirc;n hệ</em></p>\r\n', 20, 'Đã đăng', 2, '2023-11-13 05:22:06', 'vuhonglinh123', 0),
(40, 'VHL#104', 'Rau Rừng ( Rau Lủi)', 40000, '<p>- Thực phẩm sạch tốt cho sức khỏe</p>\r\n\r\n<p>- C&oacute; thể chế biến th&agrave;nh nhiều m&oacute;n ăn</p>\r\n\r\n<p>- Sản phẩm đạt chất lượng VietGap</p>\r\n\r\n<p>- Li&ecirc;n hệ trực tiếp để được gi&aacute; rau sỉ tại vựa Đ&agrave; Lạt tốt nhất.</p>\r\n', 'rau_lui_rau_rung_raucusachdl_1631957043.jpg', '<h2>1. Rau wasibi l&agrave; rau g&igrave;?&nbsp;</h2>\r\n\r\n<ul>\r\n	<li>Rau wasabina, c&ograve;n được gọi l&agrave; cải wasabina, c&oacute; 2 m&agrave;u xanh v&agrave; t&iacute;m, c&oacute; nguồn gốc từ Nhật Bản.</li>\r\n	<li>Khi ăn sống c&oacute; vị cay nồng, hương thơm đặc trưng tương tự như wasabi.</li>\r\n	<li>Tuy nhi&ecirc;n, khi nấu ch&iacute;n, n&oacute; sẽ giảm vị cay nồng v&agrave; c&oacute; vị ngọt tạo ra một trải nghiệm ẩm thực độc đ&aacute;o.</li>\r\n</ul>\r\n\r\n<p><img alt=\"Rau wasabi - Rau Củ Quả Tươi - Rau Củ Quả Sạch - Đặc Sản Đà Lạt - https://dalatfarm.net/\" height=\"1000\" sizes=\"(max-width: 1000px) 100vw, 1000px\" src=\"https://dalatfarm.net/wp-content/uploads/2023/09/rau-wasibi-5.jpg\" srcset=\"https://dalatfarm.net/wp-content/uploads/2023/09/rau-wasibi-5.jpg 1000w, https://dalatfarm.net/wp-content/uploads/2023/09/rau-wasibi-5-300x300.jpg 300w, https://dalatfarm.net/wp-content/uploads/2023/09/rau-wasibi-5-150x150.jpg 150w, https://dalatfarm.net/wp-content/uploads/2023/09/rau-wasibi-5-768x768.jpg 768w, https://dalatfarm.net/wp-content/uploads/2023/09/rau-wasibi-5-600x600.jpg 600w, https://dalatfarm.net/wp-content/uploads/2023/09/rau-wasibi-5-100x100.jpg 100w, https://dalatfarm.net/wp-content/uploads/2023/09/rau-wasibi-5-64x64.jpg 64w\" width=\"1000\" /></p>\r\n\r\n<h2>2. Rau wasibi l&agrave;m m&oacute;n g&igrave; ngon?&nbsp;</h2>\r\n\r\n<ul>\r\n	<li>Loại rau n&agrave;y c&oacute; vị cay nồng độc đ&aacute;o, nhưng vị cay n&agrave;y vừa phải v&agrave; dễ chịu.</li>\r\n	<li>Thường được sử dụng để l&agrave;m salad, x&agrave;o, nấu canh&hellip;</li>\r\n	<li>Đặc biệt, loại rau n&agrave;y rất ph&ugrave; hợp khi cuốn với&nbsp;<em><strong><a href=\"https://dalatfarm.net/san-pham/la-vung-han-quoc/\">l&aacute; m&egrave; H&agrave;n Quốc</a></strong></em>&nbsp;v&agrave; thịt nướng&hellip;</li>\r\n</ul>\r\n\r\n<p><img alt=\"Rau wasabi - Rau Củ Quả Tươi - Rau Củ Quả Sạch - Đặc Sản Đà Lạt - https://dalatfarm.net/\" height=\"1000\" loading=\"lazy\" sizes=\"(max-width: 1000px) 100vw, 1000px\" src=\"https://dalatfarm.net/wp-content/uploads/2023/09/rau-wasibi-3.jpg\" srcset=\"https://dalatfarm.net/wp-content/uploads/2023/09/rau-wasibi-3.jpg 1000w, https://dalatfarm.net/wp-content/uploads/2023/09/rau-wasibi-3-300x300.jpg 300w, https://dalatfarm.net/wp-content/uploads/2023/09/rau-wasibi-3-150x150.jpg 150w, https://dalatfarm.net/wp-content/uploads/2023/09/rau-wasibi-3-768x768.jpg 768w, https://dalatfarm.net/wp-content/uploads/2023/09/rau-wasibi-3-600x600.jpg 600w, https://dalatfarm.net/wp-content/uploads/2023/09/rau-wasibi-3-100x100.jpg 100w, https://dalatfarm.net/wp-content/uploads/2023/09/rau-wasibi-3-64x64.jpg 64w\" width=\"1000\" /></p>\r\n\r\n<h3>C&aacute;c m&oacute;n ngon từ rau wasibi&nbsp;</h3>\r\n\r\n<ul>\r\n	<li>Canh cải wasibi thịt bằm</li>\r\n	<li>Canh cải wasibi c&aacute; th&aacute;c l&aacute;c</li>\r\n	<li>Cải wasibi x&agrave;o tỏi&hellip;</li>\r\n</ul>\r\n\r\n<h2>3. C&ocirc;ng dụng của rau wasibi</h2>\r\n\r\n<p>Rau wasabi l&agrave; một loại thực phẩm gi&agrave;u chất dinh dưỡng với nhiều lợi &iacute;ch sức khỏe quan trọng:</p>\r\n\r\n<ul>\r\n	<li>Chống oxi h&oacute;a: Rau cải m&ugrave; tạt chứa nhiều chất chống oxi h&oacute;a, gi&uacute;p bảo vệ cơ thể khỏi bệnh tim, ung thư v&agrave; Alzheimer.</li>\r\n	<li>Vitamin K: Loại rau n&agrave;y cung cấp vitamin K quan trọng cho đ&ocirc;ng m&aacute;u, tim mạch v&agrave; xương.</li>\r\n	<li>Hệ thống miễn dịch mạnh mẽ: Rau cải m&ugrave; tạt gi&agrave;u vitamin C v&agrave; A, gi&uacute;p tăng sức đề kh&aacute;ng v&agrave; bảo vệ cơ thể khỏi bệnh tật.</li>\r\n	<li>Sức khỏe tim mạch: Chất chống oxi h&oacute;a trong rau gi&uacute;p giảm nguy cơ bệnh tim v&agrave; cholesterol.</li>\r\n	<li>Sức khỏe mắt: Lutein v&agrave; zeaxanthin trong rau bảo vệ mắt khỏi tho&aacute;i h&oacute;a điểm v&agrave;ng.</li>\r\n	<li>Chống ung thư: Rau cải m&ugrave; tạt cũng chứa glucosinolates, hợp chất c&oacute; khả năng chống lại ung thư đại trực tr&agrave;ng v&agrave; phổi.</li>\r\n	<li>Th&ecirc;m rau cải m&ugrave; tạt v&agrave;o chế độ ăn h&agrave;ng ng&agrave;y l&agrave; một c&aacute;ch tốt để cải thiện sức khỏe tổng thể của bạn.</li>\r\n</ul>\r\n\r\n<p><img alt=\"Rau wasabi - Rau Củ Quả Tươi - Rau Củ Quả Sạch - Đặc Sản Đà Lạt - https://dalatfarm.net/\" height=\"1000\" loading=\"lazy\" sizes=\"(max-width: 1000px) 100vw, 1000px\" src=\"https://dalatfarm.net/wp-content/uploads/2023/09/rau-wasibi-2.jpg\" srcset=\"https://dalatfarm.net/wp-content/uploads/2023/09/rau-wasibi-2.jpg 1000w, https://dalatfarm.net/wp-content/uploads/2023/09/rau-wasibi-2-300x300.jpg 300w, https://dalatfarm.net/wp-content/uploads/2023/09/rau-wasibi-2-150x150.jpg 150w, https://dalatfarm.net/wp-content/uploads/2023/09/rau-wasibi-2-768x768.jpg 768w, https://dalatfarm.net/wp-content/uploads/2023/09/rau-wasibi-2-600x600.jpg 600w, https://dalatfarm.net/wp-content/uploads/2023/09/rau-wasibi-2-100x100.jpg 100w, https://dalatfarm.net/wp-content/uploads/2023/09/rau-wasibi-2-64x64.jpg 64w\" width=\"1000\" /></p>\r\n\r\n<h2>4. G<strong>i&aacute;&nbsp;</strong>rau wasabi&nbsp;<strong>bao nhi&ecirc;u 1 kg?</strong></h2>\r\n\r\n<ul>\r\n	<li>Gi&aacute; rau thường dao động từ 70 &ndash; 80k/kg, t&ugrave;y từng khu vực gi&aacute; b&aacute;n sẽ kh&aacute;c nhau.</li>\r\n</ul>\r\n\r\n<h2>5. Mua rau wasabi ở đ&acirc;u tại tp.Hồ Ch&iacute; Minh?</h2>\r\n\r\n<ul>\r\n	<li>Ch&uacute;ng t&ocirc;i l&agrave; nguồn cung cấp đ&aacute;ng tin cậy cho những sản phẩm, rau wasabi,&nbsp;<em><strong><a href=\"https://dalatfarm.net/dac-san-dalat/\">Đặc sản Đ&agrave; Lạt</a></strong></em>&nbsp;như:\r\n\r\n	<ul>\r\n		<li><strong><em><a href=\"https://dalatfarm.net/rau-cu-qua-say/\">Rau củ quả sấy</a></em></strong></li>\r\n		<li><em><strong><a href=\"https://dalatfarm.net/rau-cu-qua-tuoi/\">Rau củ quả tươi</a></strong></em></li>\r\n		<li><strong><em><a href=\"https://dalatfarm.net/tra-thao-moc/\">Tr&agrave; thảo mộc</a></em></strong></li>\r\n		<li><strong><em><a href=\"https://dalatfarm.net/san-pham-tu-rau-cu/\">Bột rau củ rau củ</a></em>&nbsp;&hellip;</strong></li>\r\n	</ul>\r\n	</li>\r\n	<li>Với trụ sở tại Đ&agrave; Lạt v&agrave; chi nh&aacute;nh tại TP.HCM.</li>\r\n	<li>Ch&uacute;ng t&ocirc;i cam kết cung cấp những sản phẩm tươi ngon, an to&agrave;n, chất lượng&hellip;</li>\r\n</ul>\r\n', 20, 'Đã đăng', 2, '2023-11-22 14:21:25', 'vuhonglinh123', 0),
(41, 'VHL#105', 'RAU WASABI – CẢI WASIBI NHẬT – CẢI MÙ TẠT', 150000, '<ul>\r\n	<li>Rau wasabina, c&ograve;n được gọi l&agrave; cải wasabina, c&oacute; 2 m&agrave;u xanh v&agrave; t&iacute;m, c&oacute; nguồn gốc từ Nhật Bản.</li>\r\n	<li>Khi ăn sống c&oacute; vị cay nồng, hương thơm đặc trưng tương tự như wasabi.</li>\r\n	<li>Tuy nhi&ecirc;n, khi nấu ch&iacute;n, n&oacute; sẽ giảm vị cay nồng v&agrave; c&oacute; vị ngọt tạo ra một trải nghiệm ẩm thực độc đ&aacute;o.</li>\r\n</ul>\r\n', 'rau-wasibi.jpg', '<h2>1. Rau wasibi l&agrave; rau g&igrave;?&nbsp;</h2>\r\n\r\n<ul>\r\n	<li>Rau wasabina, c&ograve;n được gọi l&agrave; cải wasabina, c&oacute; 2 m&agrave;u xanh v&agrave; t&iacute;m, c&oacute; nguồn gốc từ Nhật Bản.</li>\r\n	<li>Khi ăn sống c&oacute; vị cay nồng, hương thơm đặc trưng tương tự như wasabi.</li>\r\n	<li>Tuy nhi&ecirc;n, khi nấu ch&iacute;n, n&oacute; sẽ giảm vị cay nồng v&agrave; c&oacute; vị ngọt tạo ra một trải nghiệm ẩm thực độc đ&aacute;o.</li>\r\n</ul>\r\n\r\n<p><img alt=\"Rau wasabi - Rau Củ Quả Tươi - Rau Củ Quả Sạch - Đặc Sản Đà Lạt - https://dalatfarm.net/\" height=\"1000\" sizes=\"(max-width: 1000px) 100vw, 1000px\" src=\"https://dalatfarm.net/wp-content/uploads/2023/09/rau-wasibi-5.jpg\" srcset=\"https://dalatfarm.net/wp-content/uploads/2023/09/rau-wasibi-5.jpg 1000w, https://dalatfarm.net/wp-content/uploads/2023/09/rau-wasibi-5-300x300.jpg 300w, https://dalatfarm.net/wp-content/uploads/2023/09/rau-wasibi-5-150x150.jpg 150w, https://dalatfarm.net/wp-content/uploads/2023/09/rau-wasibi-5-768x768.jpg 768w, https://dalatfarm.net/wp-content/uploads/2023/09/rau-wasibi-5-600x600.jpg 600w, https://dalatfarm.net/wp-content/uploads/2023/09/rau-wasibi-5-100x100.jpg 100w, https://dalatfarm.net/wp-content/uploads/2023/09/rau-wasibi-5-64x64.jpg 64w\" width=\"1000\" /></p>\r\n\r\n<h2>2. Rau wasibi l&agrave;m m&oacute;n g&igrave; ngon?&nbsp;</h2>\r\n\r\n<ul>\r\n	<li>Loại rau n&agrave;y c&oacute; vị cay nồng độc đ&aacute;o, nhưng vị cay n&agrave;y vừa phải v&agrave; dễ chịu.</li>\r\n	<li>Thường được sử dụng để l&agrave;m salad, x&agrave;o, nấu canh&hellip;</li>\r\n	<li>Đặc biệt, loại rau n&agrave;y rất ph&ugrave; hợp khi cuốn với&nbsp;<em><strong><a href=\"https://dalatfarm.net/san-pham/la-vung-han-quoc/\">l&aacute; m&egrave; H&agrave;n Quốc</a></strong></em>&nbsp;v&agrave; thịt nướng&hellip;</li>\r\n</ul>\r\n\r\n<p><img alt=\"Rau wasabi - Rau Củ Quả Tươi - Rau Củ Quả Sạch - Đặc Sản Đà Lạt - https://dalatfarm.net/\" height=\"1000\" loading=\"lazy\" sizes=\"(max-width: 1000px) 100vw, 1000px\" src=\"https://dalatfarm.net/wp-content/uploads/2023/09/rau-wasibi-3.jpg\" srcset=\"https://dalatfarm.net/wp-content/uploads/2023/09/rau-wasibi-3.jpg 1000w, https://dalatfarm.net/wp-content/uploads/2023/09/rau-wasibi-3-300x300.jpg 300w, https://dalatfarm.net/wp-content/uploads/2023/09/rau-wasibi-3-150x150.jpg 150w, https://dalatfarm.net/wp-content/uploads/2023/09/rau-wasibi-3-768x768.jpg 768w, https://dalatfarm.net/wp-content/uploads/2023/09/rau-wasibi-3-600x600.jpg 600w, https://dalatfarm.net/wp-content/uploads/2023/09/rau-wasibi-3-100x100.jpg 100w, https://dalatfarm.net/wp-content/uploads/2023/09/rau-wasibi-3-64x64.jpg 64w\" width=\"1000\" /></p>\r\n\r\n<h3>C&aacute;c m&oacute;n ngon từ rau wasibi&nbsp;</h3>\r\n\r\n<ul>\r\n	<li>Canh cải wasibi thịt bằm</li>\r\n	<li>Canh cải wasibi c&aacute; th&aacute;c l&aacute;c</li>\r\n	<li>Cải wasibi x&agrave;o tỏi&hellip;</li>\r\n</ul>\r\n\r\n<h2>3. C&ocirc;ng dụng của rau wasibi</h2>\r\n\r\n<p>Rau wasabi l&agrave; một loại thực phẩm gi&agrave;u chất dinh dưỡng với nhiều lợi &iacute;ch sức khỏe quan trọng:</p>\r\n\r\n<ul>\r\n	<li>Chống oxi h&oacute;a: Rau cải m&ugrave; tạt chứa nhiều chất chống oxi h&oacute;a, gi&uacute;p bảo vệ cơ thể khỏi bệnh tim, ung thư v&agrave; Alzheimer.</li>\r\n	<li>Vitamin K: Loại rau n&agrave;y cung cấp vitamin K quan trọng cho đ&ocirc;ng m&aacute;u, tim mạch v&agrave; xương.</li>\r\n	<li>Hệ thống miễn dịch mạnh mẽ: Rau cải m&ugrave; tạt gi&agrave;u vitamin C v&agrave; A, gi&uacute;p tăng sức đề kh&aacute;ng v&agrave; bảo vệ cơ thể khỏi bệnh tật.</li>\r\n	<li>Sức khỏe tim mạch: Chất chống oxi h&oacute;a trong rau gi&uacute;p giảm nguy cơ bệnh tim v&agrave; cholesterol.</li>\r\n	<li>Sức khỏe mắt: Lutein v&agrave; zeaxanthin trong rau bảo vệ mắt khỏi tho&aacute;i h&oacute;a điểm v&agrave;ng.</li>\r\n	<li>Chống ung thư: Rau cải m&ugrave; tạt cũng chứa glucosinolates, hợp chất c&oacute; khả năng chống lại ung thư đại trực tr&agrave;ng v&agrave; phổi.</li>\r\n	<li>Th&ecirc;m rau cải m&ugrave; tạt v&agrave;o chế độ ăn h&agrave;ng ng&agrave;y l&agrave; một c&aacute;ch tốt để cải thiện sức khỏe tổng thể của bạn.</li>\r\n</ul>\r\n\r\n<p><img alt=\"Rau wasabi - Rau Củ Quả Tươi - Rau Củ Quả Sạch - Đặc Sản Đà Lạt - https://dalatfarm.net/\" height=\"1000\" loading=\"lazy\" sizes=\"(max-width: 1000px) 100vw, 1000px\" src=\"https://dalatfarm.net/wp-content/uploads/2023/09/rau-wasibi-2.jpg\" srcset=\"https://dalatfarm.net/wp-content/uploads/2023/09/rau-wasibi-2.jpg 1000w, https://dalatfarm.net/wp-content/uploads/2023/09/rau-wasibi-2-300x300.jpg 300w, https://dalatfarm.net/wp-content/uploads/2023/09/rau-wasibi-2-150x150.jpg 150w, https://dalatfarm.net/wp-content/uploads/2023/09/rau-wasibi-2-768x768.jpg 768w, https://dalatfarm.net/wp-content/uploads/2023/09/rau-wasibi-2-600x600.jpg 600w, https://dalatfarm.net/wp-content/uploads/2023/09/rau-wasibi-2-100x100.jpg 100w, https://dalatfarm.net/wp-content/uploads/2023/09/rau-wasibi-2-64x64.jpg 64w\" width=\"1000\" /></p>\r\n\r\n<h2>4. G<strong>i&aacute;&nbsp;</strong>rau wasabi&nbsp;<strong>bao nhi&ecirc;u 1 kg?</strong></h2>\r\n\r\n<ul>\r\n	<li>Gi&aacute; rau thường dao động từ 70 &ndash; 80k/kg, t&ugrave;y từng khu vực gi&aacute; b&aacute;n sẽ kh&aacute;c nhau.</li>\r\n</ul>\r\n\r\n<h2>5. Mua rau wasabi ở đ&acirc;u tại tp.Hồ Ch&iacute; Minh?</h2>\r\n\r\n<ul>\r\n	<li>Ch&uacute;ng t&ocirc;i l&agrave; nguồn cung cấp đ&aacute;ng tin cậy cho những sản phẩm, rau wasabi,&nbsp;<em><strong><a href=\"https://dalatfarm.net/dac-san-dalat/\">Đặc sản Đ&agrave; Lạt</a></strong></em>&nbsp;như:\r\n\r\n	<ul>\r\n		<li><strong><em><a href=\"https://dalatfarm.net/rau-cu-qua-say/\">Rau củ quả sấy</a></em></strong></li>\r\n		<li><em><strong><a href=\"https://dalatfarm.net/rau-cu-qua-tuoi/\">Rau củ quả tươi</a></strong></em></li>\r\n		<li><strong><em><a href=\"https://dalatfarm.net/tra-thao-moc/\">Tr&agrave; thảo mộc</a></em></strong></li>\r\n		<li><strong><em><a href=\"https://dalatfarm.net/san-pham-tu-rau-cu/\">Bột rau củ rau củ</a></em>&nbsp;&hellip;</strong></li>\r\n	</ul>\r\n	</li>\r\n	<li>Với trụ sở tại Đ&agrave; Lạt v&agrave; chi nh&aacute;nh tại TP.HCM.</li>\r\n	<li>Ch&uacute;ng t&ocirc;i cam kết cung cấp những sản phẩm tươi ngon, an to&agrave;n, chất lượng&hellip;</li>\r\n</ul>\r\n', 20, 'Đã đăng', 2, '2023-11-22 14:26:34', 'vuhonglinh123', 0),
(42, 'VHL#106', 'Khoai Lang Nhật', 52000, '<p>Khoai lang Nhật l&agrave; một loại củ c&oacute; h&igrave;nh d&aacute;ng thon, d&agrave;i. Với lớp vỏ b&ecirc;n ngo&agrave;i m&agrave;u đỏ t&iacute;m, trong ruột th&igrave; v&agrave;ng, hương vị ngọt dịu nhẹ, b&ugrave;i n&ecirc;n chiếm được rất nhiều cảm t&igrave;nh của mọi người.</p>\r\n', 'f6666149-18fc-42c0-911e-d0d82ec690cb.jpeg', '<h3 id=\"docs-internal-guid-f29158b9-7fff-3fb8-6d2b-1c81f7bdf223\"><a href=\"https://www.thegioididong.com/may-tinh-bang/ipad-pro-m1-129-inch-wifi-512gb-2021\" target=\"_blank\" title=\"Tham khảo máy tính bảng iPad Pro M1 12.9 inch WiFi 512GB (2021) chính hãng giá tốt tại TGDĐ\">iPad Pro M1 12.9 inch WiFi 512GB (2021)</a>&nbsp;chiếc&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang\" target=\"_blank\" title=\"Tham khảo các dòng máy tính bảng tablet chính hãng giá tốt tại TGDĐ\">m&aacute;y t&iacute;nh bảng</a>&nbsp;với thiết kế sang trọng đẳng cấp, m&agrave;n h&igrave;nh mini-LED tuyệt hảo 12.9 inch, sức mạnh tương đương m&aacute;y t&iacute;nh để b&agrave;n nhờ bộ vi xử l&yacute; Apple M1 v&agrave; hệ thống camera chất lượng rất đ&aacute;ng để đầu tư v&agrave; n&acirc;ng cấp.</h3>\r\n\r\n<h3>Thiết kế sang trọng, đẳng cấp h&agrave;ng đầu</h3>\r\n\r\n<p>Giống với những d&ograve;ng iPad tiền nhiệm, iPad Pro 12.9 phi&ecirc;n bản 2021 tiếp tục được&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang-apple-ipad\" target=\"_blank\" title=\"Tham khảo giá điện thoại smartphone iPhone chính hãng\">Apple</a>&nbsp;thiết kế với vẻ ngoại h&igrave;nh đơn giản nhưng v&ocirc; c&ugrave;ng nổi bật với những đường n&eacute;t thiết kế v&aacute;t cạnh vu&ocirc;ng vức tạo cảm gi&aacute;c mạnh mẽ, nam t&iacute;nh v&agrave; cứng c&aacute;p.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074101.jpg\" onclick=\"return false;\"><img alt=\"Máy tính bảng iPad Pro M1 12.9 inch WiFi 512GB (2021) | Thiết kế sang trọng, đẳng cấp hàng đầu\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074101.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074101.jpg\" title=\"Máy tính bảng iPad Pro M1 12.9 inch WiFi 512GB (2021) | Thiết kế sang trọng, đẳng cấp hàng đầu\" /></a></p>\r\n\r\n<p>To&agrave;n bộ th&acirc;n m&aacute;y được ho&agrave;n thiện bằng chất liệu nh&ocirc;m nguy&ecirc;n khối kết hợp c&ugrave;ng khung viền kim loại cao cấp với k&iacute;ch thước 280.6 x 214.9 x 6.4 mm, d&agrave;y hơn một ch&uacute;t so với thế hệ trước nhưng vẫn đảm bảo sự liền mạch thoải m&aacute;i khi cầm nắm sử dụng.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074057.jpg\" onclick=\"return false;\"><img alt=\"Máy tính bảng iPad Pro M1 12.9 inch WiFi 512GB (2021) | Trải nghiệm cầm nắm thao tác\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074057.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074057.jpg\" title=\"Máy tính bảng iPad Pro M1 12.9 inch WiFi 512GB (2021) | Trải nghiệm cầm nắm thao tác\" /></a></p>\r\n\r\n<p>Tr&ecirc;n iPad Pro 12.9 M1 2021 được trang bị c&oacute; tới 5 micro gi&uacute;p ghi lại &acirc;m thanh r&otilde; n&eacute;t v&agrave; hệ thống 4 loa ngo&agrave;i hỗ trợ Dolby Atmos cao cấp cho chất lượng &acirc;m thanh sống động ch&acirc;n thực như ở rạp chiếu phim chuy&ecirc;n nghiệp.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074027.jpg\" onclick=\"return false;\"><img alt=\"Máy tính bảng iPad Pro M1 12.9 inch WiFi 512GB (2021) | Hệ thông âm thanh chất lượng\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074027.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074027.jpg\" title=\"Máy tính bảng iPad Pro M1 12.9 inch WiFi 512GB (2021) | Hệ thông âm thanh chất lượng\" /></a></p>\r\n\r\n<h3>M&agrave;n h&igrave;nh Mini-LED 12.9 inch si&ecirc;u n&eacute;t, si&ecirc;u mượt</h3>\r\n\r\n<p>Với iPad Pro 11 M1 2021, Apple vẫn mang đến cho bạn trải nghiệm h&igrave;nh ảnh cao cấp với c&ocirc;ng nghệ Liquid Retina XDR mini-LED LCD c&oacute; k&iacute;ch thước 12.9 inch, sử dụng đến tận 10.000 đ&egrave;n LED si&ecirc;u nhỏ kh&ocirc;ng chỉ hiển thị tuyệt đẹp, sắc n&eacute;t đ&aacute;ng kinh ngạc m&agrave; c&ograve;n tiết kiệm điện năng một c&aacute;ch ti&ecirc;n tiến.&nbsp;</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074044.jpg\" onclick=\"return false;\"><img alt=\"Máy tính bảng iPad Pro M1 12.9 inch WiFi 512GB (2021) | Màn hình Mini-LED siêu nét, siêu mượt\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074044.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074044.jpg\" title=\"Máy tính bảng iPad Pro M1 12.9 inch WiFi 512GB (2021) | Màn hình Mini-LED siêu nét, siêu mượt\" /></a></p>\r\n\r\n<p>Độ tương phản 1.000.000:1 v&agrave; độ s&aacute;ng to&agrave;n m&agrave;n h&igrave;nh cũng được n&acirc;ng l&ecirc;n để bạn xem, chỉnh sửa ảnh chất lượng cao hay thưởng thức những thước phim giải tr&iacute; chất lượng cao ngay tr&ecirc;n chiếc m&aacute;y t&iacute;nh bảng n&agrave;y.&nbsp;</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074046.jpg\" onclick=\"return false;\"><img alt=\"Máy tính bảng iPad Pro M1 12.9 inch WiFi 512GB (2021) | Công nghệ Mini-LED\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074046.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074046.jpg\" title=\"Máy tính bảng iPad Pro M1 12.9 inch WiFi 512GB (2021) | Công nghệ Mini-LED\" /></a></p>\r\n\r\n<p>Hơn nữa, m&agrave;n h&igrave;nh n&agrave;y c&ograve;n hỗ trợ tần số qu&eacute;t 120 Hz c&ugrave;ng c&ocirc;ng nghệ ProMotion kết hợp với hệ điều h&agrave;nh iPadOS 15 (12/2021) cho mọi thao t&aacute;c, chuyển cảnh tr&ecirc;n chiếc m&aacute;y t&iacute;nh bảng cao cấp n&agrave;y đều diễn ra mượt m&agrave; v&agrave; hấp dẫn.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074028.jpg\" onclick=\"return false;\"><img alt=\"Máy tính bảng iPad Pro M1 12.9 inch WiFi 512GB (2021) | Màn hình với độ sáng vượt trội\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074028.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074028.jpg\" title=\"Máy tính bảng iPad Pro M1 12.9 inch WiFi 512GB (2021) | Màn hình với độ sáng vượt trội\" /></a></p>\r\n\r\n<h3>Vi xử l&yacute; Apple M1, bứt ph&aacute; hiệu năng vượt trội</h3>\r\n\r\n<p>Mang tr&ecirc;n m&igrave;nh sức mạnh của bộ vi xử l&yacute; Apple M1, c&oacute; thể n&oacute;i iPad Pro 12.9 2021 ch&iacute;nh l&agrave; chiếc m&aacute;y t&iacute;nh bảng nhanh nhất từ trước đến nay với sức mạnh nhanh hơn tới 50% v&agrave; khả năng xử l&yacute; đồ họa tốt hơn 40% so với thế hệ trước.&nbsp;</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-081717.jpg\" onclick=\"return false;\"><img alt=\"Máy tính bảng iPad Pro M1 12.9 inch WiFi 512GB (2021) | Hiệu năng bứt phá vượt trội\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-081717.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-081717.jpg\" title=\"Máy tính bảng iPad Pro M1 12.9 inch WiFi 512GB (2021) | Hiệu năng bứt phá vượt trội\" /></a></p>\r\n\r\n<p>Với cấu h&igrave;nh v&agrave; sức mạnh gần như tương tự với một chiếc laptop, giờ đ&acirc;y bạn ho&agrave;n to&agrave;n c&oacute; thể thoải m&aacute;i l&agrave;m việc, chơi game đồ họa cao hay dựng c&aacute;c m&ocirc; h&igrave;nh AR phức tạp ngay tr&ecirc;n m&aacute;y t&iacute;nh bảng iPad Pro 12.9 M1 2021.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074104.jpg\" onclick=\"return false;\"><img alt=\"Máy tính bảng iPad Pro M1 12.9 inch WiFi 512GB (2021) | Thoải mái dựng phim \" data-src=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074104.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074104.jpg\" title=\"Máy tính bảng iPad Pro M1 12.9 inch WiFi 512GB (2021) | Thoải mái dựng phim \" /></a></p>\r\n\r\n<p>Dung lượng&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang-ram-8gb\" target=\"_blank\" title=\"Tham khảo máy tính bảng có dung lượng RAM 8 GB đang bán tại TGDĐ\">RAM 8 GB</a>&nbsp;gi&uacute;p mọi thao t&aacute;c đa nhiệm hay sử dụng nhiều ứng dụng c&ugrave;ng l&uacute;c lu&ocirc;n được mượt m&agrave; v&agrave; dung lượng bộ nhớ trong 512 GB, iPad Pro 12.9 M1 2021 cho ph&eacute;p bạn thoải m&aacute;i c&agrave;i đặt ứng dụng, lưu trữ những nội dung bộ nhớ lớn như ảnh RAW để tha hồ chỉnh sửa ảnh, video, &acirc;m nhạc, bản thu,... m&agrave; kh&ocirc;ng lo đầy bộ nhớ.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074025.jpg\" onclick=\"return false;\"><img alt=\"Máy tính bảng iPad Pro M1 12.9 inch WiFi 512GB (2021) | Thoải mái đa nhiệm lưu trữ\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074025.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074025.jpg\" title=\"Máy tính bảng iPad Pro M1 12.9 inch WiFi 512GB (2021) | Thoải mái đa nhiệm lưu trữ\" /></a></p>\r\n\r\n<p>Kết nối kh&ocirc;ng d&acirc;y tr&ecirc;n iPad Pro 12.9 M1 2021 cũng được n&acirc;ng cấp với chuẩn Wi-Fi 6, tăng cường sự mượt m&agrave;, ổn định khi lướt web chơi game kể cả ở những nơi c&ocirc;ng cộng với nguồn ph&aacute;t Wi-Fi yếu v&agrave; đ&ocirc;ng người truy cập, để bạn thoải m&aacute;i trải nghiệm internet tốc độ cao kh&ocirc;ng giới hạn.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074113.jpg\" onclick=\"return false;\"><img alt=\"Máy tính bảng iPad Pro M1 12.9 inch WiFi 512GB (2021) | Trải nghiệm chơi game chất lượng cao\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074113.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074113.jpg\" title=\"Máy tính bảng iPad Pro M1 12.9 inch WiFi 512GB (2021) | Trải nghiệm chơi game chất lượng cao\" /></a></p>\r\n\r\n<h3>Cụm camera chụp ảnh, quay video sắc n&eacute;t v&agrave; hơn thế nữa</h3>\r\n\r\n<p>Ph&iacute;a sau iPad Pro 12.9 2021 l&agrave; hệ thống camera chuy&ecirc;n nghiệp với camera ch&iacute;nh 12 MP, camera g&oacute;c si&ecirc;u rộng 10 MP v&agrave; m&aacute;y qu&eacute;t LiDAR tăng cường c&ocirc;ng nghệ thực tế ảo AR, t&iacute;ch hợp bộ xử l&yacute; h&igrave;nh ảnh ISP v&agrave; hỗ trợ t&iacute;nh năng Smart HDR 3 cho ph&eacute;p người d&ugrave;ng lưu lại ho&agrave;n hảo những khoảnh khắc đ&aacute;ng nhớ kể cả trong điều kiện &aacute;nh s&aacute;ng phức tạp.&nbsp;</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074103.jpg\" onclick=\"return false;\"><img alt=\"Máy tính bảng iPad Pro M1 12.9 inch WiFi 512GB (2021) | Cụm camera sau\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074103.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074103.jpg\" title=\"Máy tính bảng iPad Pro M1 12.9 inch WiFi 512GB (2021) | Cụm camera sau\" /></a></p>\r\n\r\n<p>Ph&iacute;a trước, iPad Pro 12.9 M1 2021 được trang bị camera trước độ ph&acirc;n giải cao 12 MP hỗ trợ t&iacute;nh năng TrueDepth, g&oacute;c nh&igrave;n si&ecirc;u rộng l&ecirc;n tới 122 độ cho chất lượng h&igrave;nh ảnh sắc n&eacute;t ho&agrave;n hảo m&agrave; kh&ocirc;ng bỏ lỡ bất kỳ chi tiết n&agrave;o. M&aacute;y c&ograve;n đ&aacute;p ứng nhu cầu&nbsp;mở kh&oacute;a bằng&nbsp;nhận diện khu&ocirc;n mặt&nbsp;nhanh ch&oacute;ng v&agrave; an to&agrave;n.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074050.jpg\" onclick=\"return false;\"><img alt=\"Máy tính bảng iPad Pro M1 12.9 inch WiFi 512GB (2021) | Camera trước\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074050.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074050.jpg\" title=\"Máy tính bảng iPad Pro M1 12.9 inch WiFi 512GB (2021) | Camera trước\" /></a></p>\r\n\r\n<p>Apple cũng kh&ocirc;ng qu&ecirc;n trang bị t&iacute;nh năng Center Stage cho iPad Pro 12.9 M1, t&iacute;nh năng n&agrave;y gi&uacute;p bạn lu&ocirc;n nằm ở trung t&acirc;m của khung h&igrave;nh, kể cả khi c&oacute; nhiều hơn m&aacute;y cũng sẽ tự động c&acirc;n chỉnh kh&eacute;o l&eacute;o để người xem c&oacute; được g&oacute;c nh&igrave;n tốt nhất.&nbsp;</p>\r\n\r\n<h3>Kết nối Thunderbolt tương th&iacute;ch những phụ kiện h&agrave;ng đầu</h3>\r\n\r\n<p>iPad Pro 12.9 2021 hỗ trợ cổng kết nối Thunderbolt dưới giao thức USB-C mạnh mẽ cho tốc độ truyền dữ liệu l&ecirc;n tới 40 Gbps, dễ d&agrave;ng kết nối với nhiều phụ kiện kh&aacute;c nhau, kể cả xuất ra m&agrave;n h&igrave;nh lớn độ ph&acirc;n giải l&ecirc;n tới 6K, để bạn l&agrave;m việc trực quan v&agrave; chuy&ecirc;n nghiệp hơn.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074032.jpg\" onclick=\"return false;\"><img alt=\"Máy tính bảng iPad Pro M1 12.9 inch WiFi 512GB (2021) | Kết nối Thunderbolt cao cấp \" data-src=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074032.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074032.jpg\" title=\"Máy tính bảng iPad Pro M1 12.9 inch WiFi 512GB (2021) | Kết nối Thunderbolt cao cấp \" /></a></p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, iPad Pro 11 M1 cũng ho&agrave;n to&agrave;n tương th&iacute;ch với b&uacute;t cảm ứng Apple Pencil biến iPad Pro th&agrave;nh một bảng vẽ v&agrave; l&agrave; thiết bị ghi ch&uacute; v&agrave; b&agrave;n ph&iacute;m Magic Keyboard sẽ gi&uacute;p trải nghiệm iPad Pro kh&ocirc;ng k&eacute;m g&igrave; những chiếc MacBook hiện tại với tốc độ đ&aacute;nh m&aacute;y cực nhanh v&agrave; t&iacute;ch hợp b&agrave;n di chuột TouchPad.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074042.jpg\" onclick=\"return false;\"><img alt=\"Máy tính bảng iPad Pro M1 12.9 inch WiFi 512GB (2021) | Bàn phím rời\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074042.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074042.jpg\" title=\"Máy tính bảng iPad Pro M1 12.9 inch WiFi 512GB (2021) | Bàn phím rời\" /></a></p>\r\n\r\n<p>Lưu &yacute;: B&agrave;n ph&iacute;m Magic Keyboard v&agrave; b&uacute;t Apple Pencil kh&ocirc;ng đi k&egrave;m trong hộp m&aacute;y.</p>\r\n\r\n<h3>Thời lượng pin ấn tượng</h3>\r\n\r\n<p>iPad Pro 12.9 inch 2021 được trang bị vi&ecirc;n pin dung lượng lớn 40.88 Wh tương đương khoảng 10.835 mAh cho thời lượng sử dụng l&acirc;u d&agrave;i để thực hiện nhiều t&aacute;c vụ kh&aacute;c nhau.&nbsp;</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074038.jpg\" onclick=\"return false;\"><img alt=\"Máy tính bảng iPad Pro M1 12.9 inch WiFi 512GB (2021) | Thời lượng pin ấn tượng\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074038.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074038.jpg\" title=\"Máy tính bảng iPad Pro M1 12.9 inch WiFi 512GB (2021) | Thời lượng pin ấn tượng\" /></a></p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute; m&aacute;y t&iacute;nh bảng n&agrave;y cũng hỗ trợ&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang-sac-pin-nhanh\" target=\"_blank\" title=\"Tham khảo máy tính bảng có khả năng sạc nhanh\">sạc pin nhanh</a>&nbsp;c&ocirc;ng suất cao 20 W r&uacute;t ngắn đ&aacute;ng kể thời gian để bạn nhanh ch&oacute;ng tiếp tục lại c&ocirc;ng việc hay giải tr&iacute; c&ograve;n đang dang dở.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074049.jpg\" onclick=\"return false;\"><img alt=\"Máy tính bảng iPad Pro M1 12.9 inch WiFi 512GB (2021) | Thời lượng pin ấn tượng\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074049.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074049.jpg\" title=\"Máy tính bảng iPad Pro M1 12.9 inch WiFi 512GB (2021) | Thời lượng pin ấn tượng\" /></a></p>\r\n\r\n<p>T&oacute;m lại, iPad Pro 12.9 M1 Wifi 512GB 2021 l&agrave; một chiếc m&aacute;y t&iacute;nh bảng rất đ&aacute;ng để đầu tư v&agrave; sở hữu với thiết kế sang trọng, nhỏ gọn, t&iacute;nh di động cao, thời lượng pin ổn định v&agrave; đặc biệt mạnh mẽ c&oacute; thể xử l&yacute; tốt c&aacute;c t&aacute;c vụ. Sản phẩm cũng hướng tới nh&oacute;m người d&ugrave;ng đang t&igrave;m kiếm một sản phẩm c&oacute; thể thay thế cho laptop để l&agrave;m việc v&agrave; giải tr&iacute;.</p>\r\n', 20, 'Đã đăng', 2, '2023-11-22 14:30:49', 'vuhonglinh123', 0);
INSERT INTO `tb_products` (`product_id`, `product_code`, `product_name`, `price`, `product_desc`, `product_thumb`, `product_content`, `quantity`, `status`, `cat_id`, `created_date`, `creator`, `sales`) VALUES
(59, 'VHL#400', 'Dưa hấu không hạt', 35000, '<p>&ndash; Gi&agrave;u vitamin v&agrave; kho&aacute;ng chất: Cũng &nbsp;giống như mọi loại quả kh&aacute;c, dưa &nbsp;hấu rất gi&agrave;u kho&aacute;ng chất v&agrave; vitamin. &nbsp;N&oacute; chứa nhiều vitamin A, B, C, kali, sắt, canxi, mangan, kẽm, chất chống oxy h&oacute;a v&agrave; c&aacute;c chất dinh &nbsp;dưỡng kh&aacute;c.</p>\r\n\r\n<p>&ndash; Tốt cho tim mạch v&agrave; xương: Ăn nhiều dưa hấu gi&uacute;p cải thiện chức năng tim mạch v&igrave; n&oacute; cải thiện lưu &nbsp;lượng m&aacute;u. Dưa hấu cũng rất gi&agrave;u kali, gi&uacute;p giữ canxi trong cơ thể, gi&uacute;p &nbsp;xương v&agrave; khớp mạnh hơn.</p>\r\n\r\n<p>&ndash; Ph&ograve;ng chống ung thư: Lycopene c&oacute; trong dưa hấu c&oacute; t&aacute;c &nbsp;dụng ngăn ngừa ung thư, đặc biệt l&agrave; ung &nbsp;thư v&uacute;, ung thư ruột kết, ung thư phổi, ung thư tuyến tiền liệt v&agrave; ung &nbsp;thư nội mạc tử cung.</p>\r\n\r\n<p>&ndash; &nbsp;L&agrave;m giảm mỡ trong cơ thể: dưa hấu c&ograve;n rất &iacute;t calo v&agrave; chứ', 'Untitled-design-28.png', '<h1><strong>DƯA HẤU KH&Ocirc;NG HẠT</strong></h1>\r\n\r\n<h2>1. Giống v&agrave; chủng loại</h2>\r\n\r\n<p>Dưa hấu (t&ecirc;n khoa học: &nbsp;Citrullus lanatus) l&agrave; một lo&agrave;i thực vật trong họ Bầu b&iacute; (Cucurbitaceae), &nbsp;một loại tr&aacute;i c&acirc;y c&oacute; vỏ cứng, chứa nhiều nước, c&oacute; nguồn gốc từ miền nam &nbsp;ch&acirc;u Phi v&agrave; l&agrave; loại quả phổ biến nhất trong họ Bầu b&iacute;. Dưa hấu l&agrave; một &nbsp;loại c&acirc;y c&oacute; bộ rễ lan xa n&ecirc;n d&ugrave; ở v&ugrave;ng kh&ocirc; cằn cũng c&oacute; thể cho ra loại &nbsp;tr&aacute;i c&acirc;y chứa đến 90% nước, l&agrave;m cho dưa hấu trở th&agrave;nh nguồn hyđrat ho&aacute; &nbsp;quan trọng ở những nơi khan hiếm nước.&nbsp;Dưa hấu Mặt trời đỏ l&agrave; loại giống dưa đ&atilde; được lai tạo nhằm tạo ra giống &nbsp;mới c&oacute; những đặc t&iacute;nh vượt trội hơn. Một số đặc t&iacute;nh của dưa hấu Mặt &nbsp;trời đỏ được kể đến l&agrave;:&nbsp;&nbsp;&ndash; Sức sinh trưởng, ph&aacute;t triển khỏe, dễ trồng, dễ đậu quả.&nbsp;&nbsp;&ndash; Thời gian từ trồng đến thu hoạch 60 &ndash; 65 ng&agrave;y, trồng được nhiều vụ trong năm, ở miền Nam c&oacute; thể trồng được quanh năm.<img alt=\"\" height=\"365\" src=\"https://www.fujifruit.com.vn/wp-content/uploads/2022/12/Untitled-design-28.png\" width=\"365\" /></p>\r\n\r\n<h2>2. Đặc điểm sản phẩm</h2>\r\n\r\n<p>Dưa hấu kh&ocirc;ng hạt Mặt Trời Đỏ l&agrave; giống quả mới lạ v&agrave; độc đ&aacute;o.&nbsp;&nbsp;Quả dưa &nbsp;tr&ograve;n, da xanh nhạt c&oacute; g&acirc;n xanh đậm, kh&ocirc;ng c&oacute; hạt v&agrave; vị ngọt đậm đ&agrave;. Sản &nbsp;phẩm được chọn lọc từ những quả dưa tươi ngon, kh&ocirc;ng bị dập n&aacute;t.</p>\r\n\r\n<h2>3. Gi&aacute; trị dinh dưỡng</h2>\r\n\r\n<p>&ndash;&nbsp;Gi&agrave;u vitamin v&agrave; kho&aacute;ng chất: Cũng &nbsp;giống như mọi loại quả kh&aacute;c, dưa hấu rất gi&agrave;u kho&aacute;ng chất v&agrave; vitamin. &nbsp;N&oacute; chứa nhiều vitamin A, B, C, kali, sắt, canxi, mangan, kẽm, chất chống &nbsp;oxy h&oacute;a v&agrave; c&aacute;c chất dinh dưỡng kh&aacute;c.&nbsp;&nbsp;&ndash; Tốt cho tim mạch v&agrave; xương: Ăn nhiều dưa hấu gi&uacute;p cải thiện chức năng tim mạch v&igrave; n&oacute; cải thiện lưu &nbsp;lượng m&aacute;u. Dưa hấu cũng rất gi&agrave;u kali, gi&uacute;p giữ canxi trong cơ thể, gi&uacute;p &nbsp;xương v&agrave; khớp mạnh hơn.&nbsp;&ndash; Ph&ograve;ng chống ung thư: Lycopene c&oacute; trong dưa hấu c&oacute; t&aacute;c dụng ngăn ngừa ung thư, đặc biệt l&agrave; ung &nbsp;thư v&uacute;, ung thư ruột kết, ung thư phổi, ung thư tuyến tiền liệt v&agrave; ung &nbsp;thư nội mạc tử cung.&nbsp;&ndash; L&agrave;m giảm mỡ trong cơ thể: dưa hấu c&ograve;n rất &iacute;t calo v&agrave; chứa nhiều nước, v&igrave; thế, dưa hấu l&agrave; m&oacute;n ho&agrave;n hảo cho những người muốn giảm c&acirc;n.&nbsp;&ndash; Tăng cường hệ miễn dịch, chữa l&agrave;nh vết thương: Dưa hấu chứa h&agrave;m lượng vitamin C cao, gi&uacute;p tăng cường hệ miễn dịch v&agrave; bảo vệ c&aacute;c tế b&agrave;o v&agrave; ADN khỏi bị oxy hoạt t&iacute;nh.</p>\r\n\r\n<h2>4. Lưu &yacute; bảo quản:&nbsp;</h2>\r\n\r\n<p>Pha nước muối nồng độ 15% sau đ&oacute; cho dưa hấu v&agrave;o ng&acirc;m 30 ph&uacute;t sau lấy ra &nbsp;lau kh&ocirc; vỏ dưa. Nước muối sẽ l&agrave;m cho dung dịch nước muối thẩm thấu v&agrave;o &nbsp;vỏ dưa, h&igrave;nh th&agrave;nh một lớp ph&ograve;ng chống vữa n&aacute;t, bảo vệ cho dưa tươi. Tiếp theo cho dưa hấu v&agrave;o t&uacute;i m&agrave;ng polyethylene bảo quản ở nơi m&aacute;t. Sau một th&aacute;ng lấy ra bề mặt da vẫn tươi, m&ugrave;i vị vẫn thơm ngon.</p>\r\n', 20, 'Đã đăng', 1, '2023-11-13 05:22:06', 'vuhonglinh123', 0),
(63, 'VHL#2222', 'IPhone 15 Pro MaxDưa chấm giống Nhật', 99000, '<p>&nbsp;Những c&ocirc;ng dụng tuy&ecirc;t vời của dưa chấm giống Nhật m&agrave; c&oacute; thể bạn chưa biết:</p>\r\n\r\n<p>&ndash;&nbsp;Gi&uacute;p ngừa bệnh Alzheimer (bệnh suy giảm tr&iacute; nhớ)</p>\r\n\r\n<p>&ndash;&nbsp; Gi&uacute;p hạ huyết &aacute;p</p>\r\n\r\n<p>&ndash;&nbsp; Gi&uacute;p da s&aacute;ng đẹp</p>\r\n\r\n<p>&ndash;&nbsp; Tốt cho tim mạch</p>\r\n\r\n<p>&ndash;&nbsp; Hỗ trợ giảm c&acirc;n</p>\r\n\r\n<p>&ndash;&nbsp; Cải thiện mật độ xương</p>\r\n', 'GIAI-NHIET-NGAY-HE-36.png', '<p>&nbsp;Những c&ocirc;ng dụng tuy&ecirc;t vời của dưa chấm giống Nhật m&agrave; c&oacute; thể bạn chưa biết:</p>\r\n\r\n<p>&ndash;&nbsp;Gi&uacute;p ngừa bệnh Alzheimer (bệnh suy giảm tr&iacute; nhớ)</p>\r\n\r\n<p>&ndash;&nbsp; Gi&uacute;p hạ huyết &aacute;p</p>\r\n\r\n<p>&ndash;&nbsp; Gi&uacute;p da s&aacute;ng đẹp</p>\r\n\r\n<p>&ndash;&nbsp; Tốt cho tim mạch</p>\r\n\r\n<p>&ndash;&nbsp; Hỗ trợ giảm c&acirc;n</p>\r\n\r\n<p>&ndash;&nbsp; Cải thiện mật độ xương</p>\r\n', 20, 'Đã đăng', 1, '2023-11-26 02:50:30', 'vuhonglinh123', 0),
(64, 'VHL#2223', 'Giỏ quà biếu tặng', 350000, '<p>Giỏ quả biếu tặng cao cấp, sang trọng, lịch sự.</p>\r\n', 'Gio-qua-khong-hoa-900-x-900-px-1.png', '<h3>Giỏ qu&agrave; biếu tặng cao cấp bao gồm:</h3>\r\n\r\n<ul>\r\n	<li>Giỏ m&acirc;y v&agrave; phụ kiện</li>\r\n	<li>Nho sữa Đ&agrave;i Loan</li>\r\n	<li>L&ecirc; H&agrave;n Quốc</li>\r\n	<li>Kiwi</li>\r\n	<li>Qu&yacute;t &Uacute;c</li>\r\n	<li>T&aacute;o Envy</li>\r\n</ul>\r\n', 20, 'Đã đăng', 1, '2023-11-13 05:22:06', 'vuhonglinh123', 0);

