--
-- Cấu trúc bảng cho bảng `tb_promotions`
--

CREATE TABLE `tb_promotions` (
  `id` int(11) NOT NULL,
  `title` varchar(255) NOT NULL,
  `description` text DEFAULT NULL,
  `start_date` date DEFAULT NULL,
